\begin{eqnarray}
  S_0^{(2)} y^2 &=& r^2 R e^{2 i \varphi \pm i \theta}.  
\label{s25y}\

\begin{eqnarray}
  \Omega_{\pm} &=& \pm i |\lambda| ( \frac{\sqrt{4|\lambda|^2}-1}{2 
|\lambda|}
  \mp \frac{1}{2 |\lambda|} ) = |\lambda| e^{\pm i (\frac{\pi}{2} - 
\theta)} \  ,
  \label{omega5} \\
  S_0 &=& -\frac{|\lambda|^2}{2 |\lambda|^2 e^{\pm i (\pi - 2 \theta)}}
  -\ln (|\lambda| e^{\pm i (\frac{\pi}{2} - \theta)}) \nonumber \\
   &=& \frac{1}{2} \cos 2 \theta -\ln |\lambda| 
  \mp i (\frac{\pi}{2} - \theta - \frac{1}{2} \sin 2 \theta) \ , 
\label{s02} 
\\
  S_0^{(3)} &=& \frac{2 i |\lambda|^3}{|\lambda|^3 
    e^{\pm i (\frac{3}{2} \pi - 3 \theta)}}
  = \mp 2 e^{\pm 3 i \theta} \  , \label{s32} \\
  S_0^{(4)} &=& \frac{6 |\lambda|^4}{|\lambda|^4 
    e^{\pm i (2 \pi - 4 \theta)}}
  = 6 e^{\pm 4 i \theta} \ , \label{s42} \\
  S_0^{(5)} &=& - \frac{24 i |\lambda|^5}{|\lambda|^5 
    e^{\pm i (\frac{5}{2} \pi - 5 \theta)}}
  = \mp 24 e^{\pm 5 i \theta} \ , \label{s52} \\
  S_0^{(6)} &=& - \frac{120 |\lambda|^6}{|\lambda|^6 
    e^{\pm i (3 \pi - 6 \theta)}}
  = 120 e^{\pm 6 i \theta}  \ . \label{s62} 
\

\begin{eqnarray}
  Z_{\mbox{3-loop}} &\equiv & 
  \frac{2 |\lambda|^N}{\sqrt{R}} e^{-\frac{N}{2} \cos 2 \theta} 
  \bigg[ \cos \bigg{\{}N \bigg( \frac{\pi}{2} - \theta - \frac{1}{2} 
  \sin 2 \theta \bigg)-\frac{\theta}{2} \bigg{\}} \nonumber \\
  &+& \frac{1}{N} \bigg{\{} -\frac{3}{4 R^2} 
  \cos \bigg{\{}N \bigg( \frac{\pi}{2} - \theta - \frac{1}{2} 
  \sin 2 \theta \bigg) + \frac{3}{2} \theta \bigg{\}} \nonumber \\
  &&+ \frac{5}{6 R^3} 
  \cos \bigg{\{}N \bigg( \frac{\pi}{2} - \theta - \frac{1}{2} 
  \sin 2 \theta \bigg)+ \frac{5}{2} \theta \bigg{\}}
  \bigg{\}} \nonumber \\
  &+& \frac{1}{N^2} \bigg{\{} -\frac{5}{2 R^3} 
  \cos \bigg{\{}N \bigg( \frac{\pi}{2} - \theta - \frac{1}{2} 
  \sin 2 \theta \bigg)+\frac{5}{2} \theta \bigg{\}} \nonumber \\
  &&+ \frac{329}{32 R^4} 
  \cos \bigg{\{}N \bigg( \frac{\pi}{2} - \theta - \frac{1}{2} 
  \sin 2 \theta \bigg)+\frac{7}{2} \theta \bigg{\}} \nonumber \\
  &&- \frac{105}{8 R^5} 
  \cos \bigg{\{}N \bigg( \frac{\pi}{2} - \theta - \frac{1}{2} 
  \sin 2 \theta \bigg)+\frac{9}{2} \theta \bigg{\}} \nonumber \\
  &&+ \frac{385}{72 R^6} 
  \cos \bigg{\{}N \bigg( \frac{\pi}{2} - \theta - \frac{1}{2} 
  \sin 2 \theta \bigg)+\frac{11}{2} \theta \bigg{\}}
  \bigg{\}} \bigg]
 \label{conc33}  \ , 
\

\begin{eqnarray}
  \int_{-\infty}^{\infty} \frac{d y}{\sqrt{2\pi}} y^{2n}
  \exp \Bigl[ -\frac{S^{(2)}_0}{2}y^2 \Bigl]
&=& e^{\mp i \frac{2n+1}{2} \theta} 
\int_{-\infty}^{\infty} \frac{d r}{\sqrt{2\pi}} r^{2n}
  \exp \Bigl[ -\frac{1}{2} R r^2 \Bigl] \nonumber \\
&=& \frac{(2n-1)!!}{R^{n+\frac{1}{2}}}
e^{\mp i \frac{2n+1}{2} \theta} \quad  (n \geq 0) \  . \label{gauss}
\

