\section*{$|\zeta|<1$}
Define
\begin{equation}
x=z a^{\frac{1}{n-s}},\,\,l=-b a^{\frac{-n}{n-s}}
\end{equation}
The $n-s$ roots are given by
\begin{equation}
x^{\gamma}_{i}=a^{\frac{\gamma}{n-s}}
\epsilon^{i\gamma}\left(F_{0}(\zeta)+\frac{\gamma}{n-s}
\sum_{\kappa=1}^{n-s-1} \epsilon^{-in\kappa}\mu_{\kappa} l^{\kappa}
F_{\kappa}(\zeta)\right) \label{ns}
\end{equation}
\begin{displaymath}
(i=1,2,...,n-s)
\end{displaymath}
where $\gamma$ is an arbitrary constant and
\begin{equation}
\epsilon=e^{{\frac{2\pi}{n-s}}\sqrt{-1}},\,\,
\mu_{1}=1,\,\,\,\mu_{\kappa}=\frac{1}{\kappa}\left(\begin{array}{c}
\frac{\gamma -s \kappa}{n-s}-1\\ \kappa -1
\end{array}\right)
\end{equation}
and
\begin{equation}
F_{\kappa}(\zeta)=F \left(
\begin{array}{cccc}
a_{1,\kappa}, & \dots, & a_{n-1,\kappa}, & a_{n,\kappa}\\
b_{1,\kappa}, & \dots, & b_{n-1,\kappa}, & \zeta
\end{array}
\right)
\end{equation}
are the higher order hypergeometric functions of order $n$ in
general\footnote{When $\zeta=1$, $F_{\kappa}(\zeta)$'s reduce to
hypergeometric functions of order $n-1$.}:
\begin{equation}
F_{\kappa}(\zeta)=\sum^{\infty}_\mu \frac{(a_{1,\kappa})_{\mu}\dots
(a_{n,\kappa})_{\mu} }{(b_{1,\kappa})_{\mu}\dots (b_{n,\kappa})_{\mu}}
\frac{\zeta^{\mu}}{\mu!}
\end{equation}
in which the Pochhammer symbol has been used: $(a)_{\mu}$ stands for
$a(a+1)(a+2)\dots(a+\mu-1)$. Also here
\begin{equation}
\begin{array}{rcl}
a_{i,\kappa}&=&\frac{\kappa}{n-s}+\frac{n-i}{n}-\frac{\gamma}{n(n-s)}
\,\,\, (i=1,2,\dots,n),\\
b_{i,\kappa}&=&\frac{\kappa}{n-s}+\frac{s-i+1}{s}-\frac{\gamma}{s(n-s)}
\,\,\, (i=1,2,\dots,s),\\
b_{i,\kappa}&=&\frac{\kappa}{n-s}+\frac{i-s}{n-s}+\frac{\delta_{i}}{n-s}
\,\,\, (i=s+1,\dots,n-1).
\end{array}
\end{equation}
and 
\begin{equation}
\delta_{i}=0, {\mathrm{when}}\,\,\, i<n-\kappa,\,\,\delta_{i}=1,
{\mathrm{when}}\,\,\, i \ge n-\kappa.
\end{equation}
Now define 
\begin{equation}
x=z
(-l)^{\frac{\gamma}{s}}a^{\frac{1}{n-s}},\,\,\,\lambda=(-l)^{\frac{n-s}{s}}=b^{\frac{n-s}{s}}\,g^{-\frac{n}{s}}.
\end{equation}
Under the condition $|\zeta| <1$, the remaining $s$ roots are
\begin{equation}
x^{\gamma}_{n-s+i}=(-l)^{\frac{\gamma}{s}}g^{\frac{\gamma}{n-s}}
\delta^{i\gamma}\left[\phi_{0}(\zeta)+\frac{\gamma}{s}\sum_{\kappa=1}^{s-1}\delta^{i\,n\,\kappa}\,\Delta_{\kappa}\lambda^{\kappa}\phi_{\kappa}(\zeta)\right]
\label{s}
\end{equation}
\begin{displaymath}
(i=1,2,...,s),
\end{displaymath}
where
\begin{equation}
\delta =e^{{\frac{2\pi}{s}}\sqrt{-1}},\,\,\,\,\,\,
\Delta_{1}=1,\,\,\,\,\,\,\Delta_{\kappa}=\frac{1}{\kappa}\left(\begin{array}{c}
\frac{\gamma +n \kappa}{s}-1\\
\kappa -1
\end{array}\right)
\end{equation}
and
\begin{equation}
\phi_{\kappa}(\zeta)=F\left(
\begin{array}{cccc}
d_{1,\kappa}, & \dots, & d_{n-1,\kappa}, & d_{n,\kappa}\\
e_{1,\kappa}, & \dots, & e_{n-1,\kappa}, & \zeta
\end{array}
\right)
\end{equation}
in which
\begin{equation}
\begin{array}{rcl}
d_{i,\kappa}&=&\frac{\kappa}{s}+\frac{i-1}{s}+\frac{\gamma}{s\,n} \,\,\,
(i=1,2,\dots,n),\\
e_{i,\kappa}&=&\frac{\kappa}{s}+\frac{i}{n-s}+\frac{\gamma}{s\,(n-s)} \,\,\,
(i=1,2,\dots,n-s),\\
e_{i,\kappa}&=&\frac{\kappa}{s}+1+\frac{i-n}{s}+\frac{\delta_{i}}{s} \,\,\,
(i=n-s+1,\dots,n-1),
\end{array}
\end{equation}
and $\delta_{i}$ is the same as before.

The expansions (\ref{ns}) and (\ref{s}) converge for $|\zeta
|<1$. They will diverge for $|\zeta|>1$ and hence the letter is
treated separately.
\section*{$|\zeta|>1$}
The roots
for $|\zeta|>1$ are given by higher order hypergeometric function of
the variable $\frac{1}{\zeta}$. Defining
\begin{equation}
x=z (-b)^{\frac{1}{n}},\,\,\,l_{1}=\rho=g (-b)^{-\frac{n-s}{n}},
\end{equation}
all of the $n$ roots are given by
\begin{equation}
x^{\gamma}_{i}=\beta^{\frac{\gamma}{n}} \nu^{i\,\gamma}\left[
\psi_{0}\left( \frac{1}{\zeta}\right)+ \frac{\gamma}{n}
\sum_{\kappa=1}^{n-1}
\nu^{i\,s\,\kappa}\,\theta_{\kappa}\,\rho^{\kappa}\psi_{\kappa}\left(
\frac{1}{\zeta}\right)\right]\,\,\,\,\,\,\,\,\,(i=1,2,\dots,n),
\end{equation}
in which
\begin{equation}
\nu=e^{\frac{2\,\pi}{n}\sqrt{-1}},\,\,\,\,\,\,\,\,\,\theta_{1}=1,\,\,\,\,\,\,\,\,\,\theta_{\kappa}=\left(\begin{array}{c}
\frac{\gamma +s \kappa}{n}-1\\ \kappa -1
\end{array}\right),
\end{equation}
and $\gamma$ is an arbitrary constant as before.

The $\psi$'s here are hypergeometric functions of order $n$ with the
explicit form:
\begin{equation}
\psi_{\kappa}\left( \frac{1}{\zeta}\right)=F\left(
\begin{array}{cccc}
g_{1,\kappa}, & \dots, & g_{n-1,\kappa}, & d_{n,\kappa}\\
h_{1,\kappa}, & \dots, & h_{n-1,\kappa}, & \zeta
\end{array}
\right)
\end{equation}
in which
\begin{equation}
\begin{array}{rcl}
g_{i,\kappa}&=&\frac{\kappa}{n}+\frac{i-1}{s}+\frac{\gamma}{s\,n}
\,\,\, (i=1,2,\dots,s),\\
g_{i,\kappa}&=&\frac{\kappa}{n}+\frac{n-i}{n-s}-\frac{\gamma}{n\,(n-s)}
\,\,\, (i=1,2,\dots,n),\\
h_{i,\kappa}&=&\frac{\kappa+i}{s}+\frac{\delta_{i}}{n} \,\,\, (i=1,
2,\dots,n-1),
\end{array}
\end{equation}
where $\delta_{i}$ is the same as before.
