%%%%%%%%Original coefficients in terms of D-functions%%%%%%%%%%%%%%%%%% 


To write down our main result it is convenient to introduce a special combination
\begin{eqnarray}
Q=x_{13}^2x_{24}^2+x_{14}^2x_{23}^2-x_{12}^2x_{34}^2 \, .
\end{eqnarray} 
Then we found the following coefficient functions $a_1$ and $b_1$ describing the 
four-point amplitude (\ref{Ampl}) in the supergravity approximation
\begin{eqnarray}
\label{a1}
&&a_1=-\frac{16}{\pi^2N^2}x_{12}^8x_{34}^8\Biggl[\frac{D_{2211}}{x_{12}^4x_{34}^6}
+\frac{D_{2222}}{x_{12}^4x_{34}^4}
-2Q\frac{D_{3322}}{x_{12}^4x_{34}^6}-\frac{36}{5}Q
\frac{D_{5544}}{x_{34}^2}
\\
\nonumber
&&-6\left(Q+\frac{3}{4}x_{12}^2x_{34}^2\right)
\frac{D_{4433}}{x_{12}^2x_{34}^4}
-\frac{8}{3}\left(Q+\frac{1}{2}x_{12}^2x_{34}^2\right)
\frac{D_{3333}}{x_{12}^4x_{34}^4}
-\frac{27}{5}\left(Q+\frac{7}{2}x_{12}^2x_{34}^2\right)
\frac{D_{4444}}{x_{12}^2x_{34}^2}
\Biggr] \, ; 
\end{eqnarray}

\begin{eqnarray}
\nonumber
&&b_1=\frac{48}{5\pi^2N^2}x_{12}^6x_{34}^6x_{13}^2x_{24}^2\Biggl[
32Q\frac{D_{5533}}{x_{34}^4}+48Q\frac{D_{5544}}{x_{34}^2} -
48x_{23}^2D_{4554}+108D_{4444}
\\
\nonumber
&&-\frac{40}{x_{12}^6}\left(D_{1245}x_{24}^2-D_{1254}x_{23}^2 \right)
-\frac{36}{x_{13}^2}(D_{4534}x_{12}^2-D_{4435}x_{14}^2)-\frac{12}{x_{12}^2}
(D_{4345}x_{14}^2-D_{4354}x_{13}^2)\\
\label{b2}
&&-45\left(\frac{D_{4433}}{x_{34}^2}-\frac{D_{4343}}{x_{24}^2}\right)
+30\frac{D_{4411}}{x_{34}^6}-50\frac{D_{4422}}{x_{34}^4}\Biggr]\, .
\end{eqnarray}
For the coefficients $c_1$ and $d_1$ we found
\begin{eqnarray}
\nonumber
c_1&=&-\frac{24}{5\pi^2N^2}x_{12}^4x_{34}^4x_{13}^4x_{24}^4\Biggl[
\frac{160}{x_{13}^4}(D_{3524}x_{12}^2-D_{3425}x_{14}^2)+
\frac{160}{x_{12}^4}(D_{3254}x_{13}^2-D_{3245}x_{14}^2)\\
&+&\frac{168}{x_{13}^2}(D_{4534}x_{12}^2-D_{4435}x_{14}^2)
+\frac{168}{x_{12}^2}(D_{4354}x_{13}^2-D_{4345}x_{14}^2)\\
\nonumber
&-&24\frac{D_{5454}}{x_{24}^2}(x_{12}^2x_{34}^2+x_{14}^2x_{23}^2+15x_{13}^2x_{24}^2 )
-24\frac{D_{5544}}{x_{34}^2}(x_{13}^2x_{24}^2+x_{14}^2x_{23}^2+15x_{12}^2x_{34}^2 )\\
\nonumber
&-&160\left(\frac{D_{4422}}{x_{34}^4}+\frac{D_{4242}}{x_{24}^4}\right)
-85\left(\frac{D_{4433}}{x_{34}^2}+\frac{D_{4343}}{x_{24}^2}\right)+702D_{4444}
\Biggr] \, 
\end{eqnarray}
and
\begin{eqnarray}
\nonumber
d_1&=&-\frac{48}{5\pi^2N^2}x_{12}^4x_{34}^4x_{13}^2x_{14}^2x_{23}^2x_{24}^2
\Biggl[ \frac{108}{x_{13}^2}(D_{4534}x_{12}^2-D_{4435}x_{14}^2)+
\frac{108}{x_{14}^2}(D_{4543}x_{12}^2-D_{4453}x_{13}^2)
\\
\nonumber
&+&144(D_{5454}x_{13}^2+D_{4554}x_{23}^2)
-405\left(\frac{D_{4334}}{x_{23}^2}+\frac{D_{4343}}{x_{24}^2}\right)-48Q\frac{D_{5544}}{x_{34}^2}
-320\frac{D_{4422}}{x_{34}^4}
\\
\nonumber
&-&150\frac{D_{4433}}{x_{34}^2}+504D_{4444}
\Biggr] \, .
\end{eqnarray}

