\begin{enumerate}
\item [$(i)$]Watson's equations \cite{Wa}:
\begin{equation}
\mathcal{O}_{\dots ij\dots}(\dots,\theta_{i},\theta_{j},\dots)=\mathcal{O}%
_{\dots ji\dots}(\dots,\theta_{j},\theta_{i},\dots)\,S_{ij}(\theta_{i}%
-\theta_{j})\,.\label{pf1}%
\end{equation}

\item[$(ii)$] Crossing relation (for the connected part of the matrix
element):
\begin{align}
_{\bar{\alpha}_{1}}\langle\,p_{1}\,|\,\mathcal{O}(0)\,|\,p_{2},\dots
,p_{n}\,\rangle_{\alpha_{2}\dots\alpha_{n}}^{in,conn.} &  =\mathcal{O}%
_{\alpha_{1}\alpha_{2}\dots\alpha_{n}}(\theta_{1}+i\pi,\theta_{2},\dots
,\theta_{n})\label{pf2}\\
&  =\mathcal{O}_{\alpha_{2}\dots\alpha_{n}\alpha_{1}}(\theta_{2},\dots
,\theta_{n},\theta_{1}-i\pi)\,.\nonumber
\end{align}

\item[$(iii)$] Recursion relation:
\begin{equation}
\operatorname*{Res}_{\theta_{12}=i\pi}\mathcal{O}_{1\dots n}(\theta_{1}%
,\dots)=2i\,\mathbf{C}_{12}\,\,\mathcal{O}_{3\dots n}(\theta_{3},\dots)\left(
\mathbf{1}-S_{2n}\dots S_{23}\right)  \,,\label{pf3}%
\end{equation}
where $\mathbf{C}_{12}$ is the charge conjugation matrix.

\item[$(iv)$] Bound state form factors equations:
\begin{equation}
\operatorname*{Res}_{\theta_{12}=ia}\mathcal{O}_{123\dots n}(\underline
{\theta})=\mathcal{O}_{(12)3\dots n}(\theta_{(12)},\underline{\theta}^{\prime
})\,\sqrt{2}\,\Gamma_{12}^{(12)}\,,\label{pf4}%
\end{equation}
where $a$ is the fusion angle and $\Gamma_{12}^{(12)}$ is the fusion
intertwiner \cite{K1,BK1}.

\item[$(v)$] Lorentz invariance:
\begin{equation}
\mathcal{O}_{1\dots n}(\theta_{1}+u,\dots,\theta_{n}+u)=e^{su}\,\mathcal{O}%
_{1\dots n}(\theta_{1},\dots,\theta_{n})\,,\label{pf5}%
\end{equation}
where $s$ is the ``spin'' of $\mathcal{O}$.
