enumerate:
\begin{enumerate}

\item The \emph{K\"{a}hler potential} 
$ K_{matter} = Z_{IJ} \overline{C}^{I} C^{J} $ determines the kinetic
terms of the chiral matter fields.  To first order in the expansion
parameter $ \epsilon $, the K\"{a}hler metric $ Z_{IJ} $ takes the form
\begin{equation}
\label{Kmetric}
Z_{IJ} = e^{-K_{T}/3} \left[ G_{IJ} - \frac{\epsilon}{2 \mathcal{V}}
\tilde{\Gamma}^{i}_{IJ} \sum_{n=0}^{N+1} (1 - z_{n})^{2} \beta_{i}^{(n)}
\right],
\end{equation}
where 
\begin{align}
G^{ ( \mathcal{R} ) }_{IJ} &= \frac{1}{ \mathcal{V} } \int_{Z} 
  \sqrt{ g^{(CY)} } g^{ (CY) a \overline{b} } 
  u_{Iax}(\mathcal{R}) u^{x}_{J \overline{b}}(\mathcal{R})   \\
\tilde{\Gamma}^{i}_{IJ} 
 &= \Gamma^{i}_{IJ} - (T^{i} + \overline{T}^{i}) G_{IJ}
   -\frac{2}{3}( T^{i} + \overline{T}^{i} ) 
               ( T^{k} + \overline{T}^{k} ) K_{Tkj} \Gamma^{j}_{IJ}  \\
K_{T} &= -\textrm{ln} \left[ \frac{1}{6} d_{ijk} 
         ( T^{i} + \overline{T}^{i} )
         ( T^{j} + \overline{T}^{j} )
         ( T^{k} + \overline{T}^{k} ) \right]   
\end{align}
and
\begin{equation}
K_{Tij} = \frac{ \partial^{2} K_{T} }
                  { \partial T^{i} \partial \overline{T}^{j} }; \quad
\Gamma^{i}_{IJ} =  K^{ij}_{T} \frac{ \partial G_{IJ} }{ \partial T^{j} };
\quad
d_{ijk} = \int_{Z} \omega_{i} \wedge \omega_{j} \wedge \omega_{k}.
\end{equation}

\item The holomorphic \emph{superpotential} $ W $ determines the Yukawa
couplings
\begin{equation}
\label{Yukawa}
Y^{ ( \mathcal{R}_{1} \mathcal{R}_{2} \mathcal{R}_{3} ) }_{IJK}
 = 2 \sqrt{ 2 \pi \alpha_{G} }
   \int_{Z} \Omega \wedge u^{x}_{I}(\mathcal{R}_{1}) \wedge 
                          u^{y}_{J}(\mathcal{R}_{2}) \wedge
                          u^{z}_{K}(\mathcal{R}_{3}) 
            f^{ (\mathcal{R}_{1} \mathcal{R}_{2} \mathcal{R}_{3} ) }_{xyz}
\end{equation}
as well as the $ F $-term part of the scalar potential.  $ \Omega $ is the
covariantly constant $ (3,0) $ form and 
$ f^{ (\mathcal{R}_{1} \mathcal{R}_{2} \mathcal{R}_{3} ) }_{xyz} $
projects out the singlet in 
$ \mathcal{R}_{1} \times \mathcal{R}_{2} \times \mathcal{R}_{3} $ (if
any).  The Yukawa contribution to the superpotential is
\begin{equation}
W_{Y} = e^{K_{mod}/2} \frac{1}{3} Y_{IJK} C^{I}C^{J}C^{K},
\end{equation}
where $ K_{mod} = -\textrm{ln} (S + \overline{S}) + K_{T} $ is the moduli
contribution to the K\"{a}hler potential.

\item The holomorphic \emph{gauge kinetic function}
\begin{equation} 
f = S + \epsilon T^{i} \left[ \beta^{(0)}_{i} \sum_{n=1}^{N} 
                              (1 - Z_{n})^{2} \beta^{(n)}_{i} \right]
\end{equation}
determines the gauge kinetic terms and contributes to the gaugino masses
and the gauge part of the scalar potential.

