\begin{equation}
  \label{eq:effpot2}
  \begin{split}
    \veff &= - \sum_{m,n} \tilde{\alpha}_{mn} \cdot (\bar{\varphi} \varphi)^{-
    \frac{2}{3} (m+n+1)} \bar{F}^{m+1} F^{n+1} \cdot \medsp
  &\quad \phantom{ - \sum_{m,n} \tilde{\alpha}_{m,n}} \cdot \biggl( 1 + \frac{m+n+1}{3}
    \bigl( (\bar{F}\bar{\varphi})^{-1} (\bar{\psi} \bar{\psi}) +
    (F\varphi)^{-1} (\psi\psi)\bigr)\medsp
    &\quad \phantom{ - \sum_{m,n} \tilde{\alpha}_{m,n}\cdot \biggl(}  + \frac{(m+n+1)^2}{9} (\bar{F}
    F)^{-1} (\bar{\varphi} \varphi)^{-1} (\psi \psi) (\bar{\psi} \bar{\psi})
    \biggr) \medsp
    &\quad + \sum_k \tilde{\beta}_k \cdot (\bar{\varphi} \varphi)^{-
      \frac{2}{3} k} F \bar{F}^k \cdot \medsp
    &\quad \phantom{+ \sum_k \tilde{\beta}_k} \cdot \biggr( 1 + \frac{k}{3} \bigl( (\bar{F}\bar{\varphi})^{-1} (\bar{\psi} \bar{\psi}) +
    (F\varphi)^{-1} (\psi\psi)\bigr) + \frac{k^2}{9} (\bar{F}
    F)^{-1} (\bar{\varphi} \varphi)^{-1} (\psi \psi) (\bar{\psi} \bar{\psi})
    \biggr) \medsp
 &\quad + \sum_l \Tilde{\Bar{\beta}}_l \cdot (\bar{\varphi} \varphi)^{-
      \frac{2}{3} l} F^l \bar{F} \cdot \medsp
    &\quad \phantom{+ \sum_k \Tilde{\Bar{\beta}}_l} \cdot \biggr( 1 + \frac{l}{3} \bigl( (\bar{F}\bar{\varphi})^{-1} (\bar{\psi} \bar{\psi}) +
    (F\varphi)^{-1} (\psi\psi)\bigr) + \frac{l^2}{9} (\bar{F}
    F)^{-1} (\bar{\varphi} \varphi)^{-1} (\psi \psi) (\bar{\psi} \bar{\psi})
    \biggr)   
  \end{split}
\

\begin{equation}
\label{eq:Fpotconstr}
\begin{split}
  \Bigl. \metrtilde \bar{F} + \metrtilde[, F] F \bar{F}\Bigr|_{F = F_0} &= c
  \log \frac{z \varphi}{\Lambda^3}\ , \medsp
  \Bigl. \metrtilde + \metrtilde[, F \bar{F}] F \bar{F} + \bigl( \metrtilde[, F] F +
  \hc \bigr)\Bigr|_{F = F_0} &< 0\ .
\end{split}
\

\begin{equation}
  \label{eq:ord2bos}
\begin{split}
  \Ltext{sc}^{(2)} &= \Bigl( \metrtilde + \frac{4}{9} \tilde{g}_{F \bar{F}} (\bar{\varphi} \varphi)_0^{-1} (\bar{F}
  F)_0 + \bigl(\metrtilde[, F] F_0 - \frac{2}{3} \tilde{g}_{\varphi
  \bar{F}} (\bar{\varphi}^{-1} \bar{F})_0 + \hc \bigr) \Bigr) \partial_\mu \bar{\varphi} \partial^\mu \varphi \medsp
  &\quad + \tilde{g}_{F \bar{F}} \partial_\mu \bar{F} \partial^\mu F  + \bigl(
  \tilde{g}_{\varphi F} \partial_\mu F \partial^\mu \varphi + \hc \bigr)
  \medsp
  &\quad + \Bigl( \bigl( \tilde{g}_{\varphi \bar{F}} - \frac{2}{3}
  \tilde{g}_{F \bar{F}} (\varphi^{-1} F)_0 \bigr) \partial_\mu \bar{F}
  \partial^\mu \varphi + \hc \Bigr) \medsp
  &\quad + \Bigl( \bigl( \tilde{g}_{\varphi \varphi} - \frac{2}{3} \tilde{g}_{\varphi
  F} (\varphi^{-1} F)_0 \bigr) \partial_\mu \varphi \partial^\mu \varphi + \hc
  \Bigr)\ .
\end{split}
\

\begin{equation}
  \label{eq:vysuperpot}
  \intd{\diff{^2 \theta}} \bigl(c \Phi \log
  \frac{z \Phi}{\Lambda^3} + b \Phi \bigr) = c \bigl( F
  - \inv{2 \varphi} \psi \psi + F \log \frac{z \varphi}{\Lambda^3} \bigr) + b F\ .
\

