\begin{enumerate}
\item bulk one point function \cite{FZZ,T3}
\begin{equation}
\left\langle V_{\alpha}(z,\bar{z})\right\rangle
=\frac{U(\alpha|\mu_{B})}{\left| z-\bar{z}\right|
^{2\Delta_{\alpha}}} \label{onepoint} \nonumber
\end{equation}
\item boundary two point function \cite{FZZ}
\begin{equation}
\left\langle
B_{\beta_1}^{\sigma_{1}\sigma_{2}}(x)B_{\beta_2}^{\sigma_{2}\sigma_{1}}(0)\right\rangle
=\frac{\delta(\beta_2+\beta_1-Q)+S(\beta_1,\sigma_{2},\sigma_{1})\delta(\beta_2-\beta_1)}{\left|
x\right|^{2\Delta_{\beta_1}}} \nonumber
\end{equation}
We give here the explicit expression for the boundary reflection
amplitude:
\begin{eqnarray}
\lefteqn{S(\beta,\sigma_{2},\sigma_{1})
 =(\pi\mu\gamma(b^{2})b^{2-2b^{2}})^{\frac{1}{2b}(Q-2\beta)}}
\nonumber \\
& & \frac{\Gamma_{b}(2\beta-Q)}{\Gamma_{b}(Q-2\beta)}
\frac{S_b(\sigma_{2}+\sigma_{1}-\beta)S_b(2Q-\beta-\sigma_{1}-\sigma_{2})}
{S_b(\beta+\sigma_{2}-\sigma_{1})S_{b}(\beta+\sigma_{1}-\sigma_{2})}
\nonumber  \label{fonction 2 points boundary}
\end{eqnarray}
It satisfies the unitarity relation
$S(\beta,\sigma_{2},\sigma_{1})S(Q-\beta,\sigma_{2},\sigma_{1})=1$.

\item bulk-boundary two point function\cite{hosomichi}\footnote{The bulk one point function is a special case of the
bulk-boundary coefficient with $\beta=0$.}
\begin{equation}
\left\langle
V_{\alpha}(z,\bar{z})B_{\beta}^{\sigma\sigma}(x)\right\rangle
=\frac{R(\alpha,\beta |\mu_{B})}{\left|  z-\bar{z}\right|
^{2\Delta_{\alpha}-\Delta_{\beta}}\left| z-x\right|
^{2\Delta_{\beta}}} \label{bbound} \nonumber
\end{equation}
