\begin{itemize}
\item{$\Ga(x)$ function}\\
The Double Gamma function introduced by Barnes \cite{Barnes} is
defined by:
\begin{eqnarray}
\nonumber \\
&&
\text{log}\Gamma_{2}(s|\omega_1,\omega_2)=\left(\frac{\partial}{\partial
t}
\sum_{n_1,n_2=0}^{\infty}(s+n_1\omega_1+n_2\omega_2)^{-t}\right)_{t=0}
\nonumber
\end{eqnarray}
Definition: $\Gamma_b(x) \equiv \frac{\Gamma_2(x|b,b^{-1})}{\Gamma_2(Q/2|b,b^{-1})}$.\\
Functional relations:
\begin{eqnarray}
\nonumber \\
&&\Ga(x+b)= \frac{\sqrt{2\pi}b^{bx-\frac{1}{2}}}{\Gamma(bx)}\Ga(x), \nonumber \\
&&\Ga(x+1/b)=
\frac{\sqrt{2\pi}b^{-\frac{x}{b}+\frac{1}{2}}}{\Gamma(x/b)}\Ga(x).
\nonumber
\end{eqnarray}
$\Ga(x)$ is a meromorphic function of $x$, whose poles are located
at
$x=-nb-mb^{-1}, n,m \in \mathbb{N}$.\\
Integral representation convergent for $0<\mathrm{Re}x$
\begin{eqnarray}
&&\text{log}\Ga(x)=\int_{0}^{\infty}\frac{dt}{t}\left\lbrack\frac{e^{-xt}-e^{-Qt/2}}{(1-e^{-bt})(1-e^{-t/b})}
-\frac{(Q/2-x)^{2}}{2}e^{-t}-\frac{Q/2-x}{t}\right\rbrack\nonumber
\end{eqnarray}
\item{$S_b(x)$ function}\\
Definition: $S_b(x)\equiv \frac{\Ga(x)}{\Ga(Q-x)}$ \nonumber\\
Functional relations:
\begin{eqnarray}
&& S_b(x+b) = 2\text{sin}(\pi bx)S_b(x),  \nonumber \\
&& S_b(x+1/b) = 2\text{sin}(\pi x/b)S_b(x). \nonumber
\end{eqnarray}
$S_b(x)$ is a meromorphic function of $x$, whose poles are located
at $x=-nb-mb^{-1}, n,m \in \mathbb{N}$,
and whose zeros are located at $x=Q+nb+mb^{-1}, n,m \in \mathbb{N}$.\\
Integral representation convergent in the strip $0<\mathrm{Re}x<Q$
\begin{eqnarray}
&&\text{log}S_b(x)=\int_{0}^{\infty}\frac{dt}{t}\left\lbrack\frac{\text{sinh}(\frac{Q}{2}-x)t}
{2\text{sinh}(\frac{bt}{2})\text{sinh}(\frac{t}{2b})}-
\frac{(Q-2x)}{t}\right\rbrack\nonumber
\end{eqnarray}
\item{$\up(x)$ function}\\
Definition: $\up(x)^{-1} \equiv \Ga(x)\Ga(Q-x)$ \nonumber \\
Functional relations:
\begin{eqnarray}
&&\up(x+b)=\frac{\Gamma(bx)}{\Gamma(1-bx)}b^{1-2bx}\up(x),\nonumber \\
&&\up(x+1/b)=\frac{\Gamma(x/b)}{\Gamma(1-x/b)}b^{2x/b-1}\up(x).
\nonumber
\end{eqnarray}
$\up(x)$ is an entire function of $x$ whose zeros are located at $x=-nb-mb^{-1}$ and $x=Q+nb+mb^{-1}$,
 $n,m \in \mathbb{N}$.\\
Integral representation convergent in the strip $0<\mathrm{Re}x<Q$
\begin{eqnarray}
&&\text{log}\up(x)=\int_{0}^{\infty}\frac{dt}{t}\left\lbrack\left(\frac{Q}{2}-x\right)^{2}e^{-t}-
\frac{\text{sinh}^{2}(\frac{Q}{2}-x)\frac{t}{2}}{\text{sinh}\frac{bt}{2}\text{sinh}\frac{t}{2b}}
\right\rbrack \nonumber
\end{eqnarray}
