\section{Appendix}\label{appA}
%
In this Appendix we analyze the term
%
\begin{eqnarray}
{\cal H}_1 =  
\sum_{j=1}^{n-1} 
\rho_1^{(n-j)}\rho_1^{(j)} \int d^4x \, (-\partial^\mu \omega^a)
\frac{\delta}{\delta A_\mu^a} {\cal S}_0 (\int d^4y \, 
\hat A^{b*}_\nu A^{\nu b} ) 
\label{app1}
\end{eqnarray}
%
contributing to the $n$-th order ST identities and construct a functional
$\tilde \zeta^{(n)}_{{\cal H}_1}$ such that
%
\begin{eqnarray}
{\cal H}_1 = {\cal S}_0 ( \tilde \zeta^{(n)}_{{\cal H}_1} ) \, .
\label{app2}
\end{eqnarray}
%
In order to construct  $\tilde \zeta^{(n)}_{{\cal H}_1}$ 
we first notice the following relation:
%
\begin{eqnarray} 
&& \int d^4x \, \partial_\mu \omega^a \frac{\delta}{\delta A_\mu^a} 
{\cal S}_0 ( \int d^4y \, \hat A^{b*}_\nu A^{\nu b} )  = 
{\cal S}_0 ( \int d^4x \, A^{\mu}_a \frac{\delta}{\delta A_\mu^a} 
{\cal S}_0 ( \int d^4y \, \hat A^{b*}_\nu A^{\nu b} )) 
\nonumber \\
&& ~~~~~~~~~~~~~~ + \int d^4x d^4y \, A_\mu^a(x) 
\frac{\delta^2 \G^{(0)}}{\delta A_\mu^a(x) \delta A^d_\rho(y)}
\frac{\delta}{\delta \hat A(y)^{d*} _\rho}
{\cal S}_0 ( \int d^4z \, \hat A^{b*}_\nu A^{\nu b} ) \, .
\label{app3}
\end{eqnarray}
%
The R.H.S. in the first line of the above equation is 
${\cal S}_0$-exact. Therefore the analysis is reduced to the functional
%
\begin{eqnarray}
\Delta' & = & \int d^4x d^4y \, A_\mu^a(x) 
\frac{\delta^2 \G^{(0)}}{\delta A_\mu^a(x) \delta A^d_\rho(y)}
\frac{\delta}{\delta \hat A(y)^{d*} _\rho}
{\cal S}_0 ( \int d^4z \, \hat A^{b*}_\nu A^{\nu b} ) \nonumber \\
& = & - \int d^4x d^4y \, A_\mu^a(x) 
\frac{\delta^2 \G^{(0)}}{\delta A_\mu^a(x) \delta A^d_\rho(y)}
\partial^\rho \omega^d \, .
\label{app4}
\end{eqnarray}
%
We remark that $\Delta'$ is independent of the external sources and 
satisfies the Wess-Zumino consistency condition
%
\begin{eqnarray}
{\cal S}_0 (\Delta') = s \Delta' = 0 \, .
\label{app5}
\end{eqnarray}
%
$\Delta'$ depends only on $A_\mu^a$ and $\omega^a$. In order to construct
a functional $\tilde \zeta'$ such that
%
\begin{eqnarray}
s \tilde \zeta' = \Delta' 
\label{app6}
\end{eqnarray}
%
we use the techniques of homological perturbation theory \cite{Henneaux:ig}.
We consider the grading induced by the counting operator for the fields
$A_\mu^a$
%
\begin{eqnarray}
{\cal N} = \int d^4x \, A_\mu^a \frac{\delta}{\delta A_\mu^a} \, .
\label{app7}
\end{eqnarray}
%
The restriction of $s$ to the subspace spanned by
$A_\mu^a,\omega^a$ and their derivatives 
decomposes according to this grading into
%
\begin{eqnarray}
s = s_{-1} + s_0 \, , 
\label{app8}
\end{eqnarray}
%
where
%
\begin{eqnarray}
&& s_{-1} A_\mu^a = \partial_\mu \omega_a \, , ~~~~ s_{-1} \omega_a = 0 \, ,
\nonumber \\
&& s_0 A_\mu^a = \epsilon^{abc} A_\mu^b \omega^c \, , ~~~~
s_0 \omega^a = -\frac{1}{2} \epsilon^{abc} \omega^b \omega^c \, .
\label{app9}
\end{eqnarray}
%
The differentials $s_{-1},s_0$ satisfy
%
\begin{eqnarray}
s_0^2 = s_{-1}^2 = 0 \, , ~~~~ \{ s_{-1}, s_0 \} = 0 \, ,
\label{app10}
\end{eqnarray}
%
as a consequence of the nilpotency of $s$.
We notice that $s_{-1}$ is the Abelian approximation to $s$. 
$s_{-1}$ admits the homotopy operator \cite{Zumino}
%
\begin{eqnarray}
\kappa = \int_0^1 dt \left ( A_\mu^a \lambda_t 
\frac{\partial}{\partial (\partial_\mu \omega^a)} 
+ \partial_{(\mu} A_{\nu )}^a \lambda_t 
\frac{\partial}{\partial (\partial_\mu \partial_\nu \omega^a)}
+ \partial_{(\mu} \partial_\nu A^a_{\sigma)}\lambda_t
\frac{\partial}{\partial(\partial_\mu \partial_\nu \partial_\sigma \omega^a)}
+ \dots \right )
\label{app11}
\end{eqnarray}
%
where $(\mu,\dots,\sigma)$ denotes total symmetrization and the operator
$\lambda_t$ acts as 
%
\begin{eqnarray}
&& \lambda_t X(\omega^a, F^a_{\mu\nu},
\partial^\sigma  F^a_{\mu\nu}, \partial^\sigma \omega^a, A^\mu_a,
\partial_{\mu_1} \dots \partial_{\mu_{n-2}} 
\partial_{(\mu_{n-1}} A^a_{\mu_n )})  \nonumber \cr
&& ~~~~~~~~~ 
= X (\omega^a, F^a_{\mu\nu},
\partial^\sigma  F^a_{\mu\nu}, t \partial^\sigma \omega^a, t A^\mu_a,
t \partial_{\mu_1} \dots \partial_{\mu_{n-2}} 
\partial_{(\mu_{n-1}} A^a_{\mu_n )}) 
\label{app11_bis}
\end{eqnarray}
%
where $F_{\mu\nu}^a$ is 
%
\begin{eqnarray}
F_{\mu\nu}^a = \partial_\mu A_\nu^a - \partial_\nu A_\mu^a \, .
\label{app11_ter}
\end{eqnarray}
We also expand $\Delta'$ according to the degree induced by ${\cal N}$.
We get
%
\begin{eqnarray}
\Delta' = \Delta'_1 + \Delta'_2 + \Delta'_3 
\label{app12}
\end{eqnarray}
%
where
%
\begin{eqnarray}
\Delta'_1 & = & -\frac{1}{g^2} (\square A^d_\rho 
                               -\partial_\rho (\partial A)^d ) \partial^\rho \omega^d \, , \nonumber \\
\Delta'_2 & = & -\frac{1}{g^2} (4 \epsilon^{dlm} A_\sigma^l \partial_\sigma A_\rho^m
+2 \epsilon^{dlm} (\partial A)^l A_\rho^m -2 \epsilon^{dqk} A_\sigma^q 
\partial_\rho A_\sigma^k ) \partial^\rho \omega^d \, , \nonumber \\
\Delta'_3 & = & \frac{3}{g^2} \epsilon^{dqk}\epsilon^{krv} 
	                       A^q_\sigma A^r_\sigma A^v_\rho \partial^\rho \omega^d \, .
\label{app13}
\end{eqnarray}
%
We now prove that it is possible to solve the condition
%
\begin{eqnarray}
s \tilde \zeta' = \Delta'
\label{app14}
\end{eqnarray}
%
for $\tilde \zeta'$. Eq.(\ref{app14}) reads at order $j$ in the
grading induced by ${\cal N}$:
%
\begin{eqnarray}
s_{-1} \tilde \zeta'_{j+1} + s_0 \tilde \zeta'_j = \Delta'_j \,,  ~~~~
 j =0,1,2,3 \, .
\label{app15}
\end{eqnarray}
%
At order zero eq.(\ref{app15}) is verified by setting
%
\begin{eqnarray}
\tilde \zeta'_{1}=\tilde \zeta'_0 = 0 \, .
\label{app16}
\end{eqnarray}
%
We assume that eq.(\ref{app15}) is verified up to order $n-1$.
At order $n$ eq.(\ref{app15}) gives
%
\begin{eqnarray}
s_{-1} \tilde \zeta'_{n+1} = \Delta'_n - s_0 \tilde \zeta'_n \, .
\label{app17}
\end{eqnarray}
%
Since $s_{-1}$ is nilpotent, there is a consistency condition
on the R.H.S. of eq.(\ref{app17}):
%
\begin{eqnarray}
s_{-1} (\Delta'_n - s_0 \tilde \zeta'_n) = 0 \, .
\label{app18}
\end{eqnarray}
%
The above equation is verified since
%
\begin{eqnarray}
s_{-1}  (\Delta'_n - s_0 \tilde \zeta'_n) & = &
s_{-1} \Delta'_n + s_0 s_{-1} \tilde \zeta'_n \nonumber \\
& = & s_{-1} \Delta'_n + s_0 (\Delta'_{n-1} - s_0 \tilde \zeta'_{n-1} )
\nonumber \\
& = & s_{-1} \Delta'_n + s_0 \Delta'_{n-1} \nonumber \\
& = & 0 \, .
\label{app19}
\end{eqnarray}
%
In the second line of the above equation we have used the recursive
condition that eq.(\ref{app15}) is verified at order $n-1$.
In the last line we have used the fact that $\Delta'$ fulfills
eq.(\ref{app5}).

Then we can solve eq.(\ref{app17}) by using the homotopy operator
$\kappa$:
%
\begin{eqnarray}
\tilde \zeta'_{n+1} = \kappa (
 \Delta'_n - s_0 \tilde \zeta'_n  ) \, .
\label{app20}
\end{eqnarray}
%
By applying this procedure we get
%
\begin{eqnarray}
\tilde \zeta' & = &  - \int d^4x \, 
\frac{1}{g^2} ( \square A^d_\rho - \partial_\rho (\partial A)^d ) A^{\rho d}  \nonumber \\
& &  + \int d^4x \, 
\frac{2}{g^2} \epsilon^{dlm} A^l_\sigma (\partial_\rho A^m_\sigma -
					 \partial_\sigma A^m_\rho) A^d_\rho 
\nonumber \\
& & 
-  \int d^4x \, 
\frac{1}{4g^2} \epsilon^{vqk}\epsilon^{krd} A_\sigma^q A_\sigma^r 
A_\rho^v A_\rho^d \, .
\label{app21}
\end{eqnarray}
%
The final result for ${\cal H}_1$, combining eq.(\ref{app3})
and eq.(\ref{app21}), is given in eq.(\ref{ym35}).

