\begin{eqnarray} 
\varsigma_i &=& \left\{ \begin{array}{ll} 
   +1 & \mbox{(elem.),} \\
   -1 & \mbox{(solit.),} \end{array} \right. \\
  S'_i &=& \left\{ \begin{array}{ll} 
   \exp(\frac{1}{2}\sum_\alpha a_{i\alpha}\phi_\alpha-
\sum_{I:i\in I} d_I A_I)(e^{C_i})' & \mbox{(elem.),} \\
    & \\
   \exp(\frac{1}{2}\sum_\alpha a_{i\alpha}\phi_\alpha-\sum_{I:i\in \!\!\! / \, I} 
   d_I A_I-{\tilde{d}} B)
    \frac{\lambda_i}{r^{{\tilde{d}}+1}}& \mbox{(solit.).} \end{array} \right. 
\label{S_1} 
\

\begin{eqnarray} 
\exp({B_{\vartheta}}(\vartheta)) &=& 
\left( \frac12 \exp(-\sum_I d_I A_I(\vartheta) +\epsilon_\chi) \right)^{1/{\tilde{d}}} 
  \rho(\vartheta)^{-(1+1/{\tilde{d}})}, \label{btheta} \\
  \rho(\vartheta) &=& \left\{ \begin{array}{ll}
    \left| \frac{1}{4} ((1/R)^{{\tilde{d}}}+R^{{\tilde{d}}}) 
    \sin (\frac{4{\tilde{d}}\vartheta}{(1/R)^{{\tilde{d}}}+R^{{\tilde{d}}}}+2\arctan R^{\tilde{d}})\right|,
   & s_\chi=-1,\\
    \left| \frac{1}{4} ((1/R)^{{\tilde{d}}}-R^{{\tilde{d}}}) 
    \sinh(\frac{4{\tilde{d}}\vartheta}{\left|(1/R)^{{\tilde{d}}}-R^{{\tilde{d}}}\right|}+2{\rm Arth} R^{\tilde{d}}
    )\right|,
   & s_\chi=+1 
 \end{array} \right. 
\label{rho_1} 
\

\begin{eqnarray} 
\dot{S}_i &=& \left\{ \begin{array}{ll} 
\exp(\frac{1}{2}\sum_\alpha a_{i\alpha}\phi_\alpha-
\sum_{I:i\in I} d_I A_I)(e^{C_i})\dot{} & \mbox{(elem.),} \\
& \\
-2\lambda_i e^{-\epsilon_\chi}
\exp(\frac{1}{2}\sum_\alpha a_{i\alpha}\phi_\alpha+
\sum_{I:i\in I} d_I A_I)& \mbox{(solit.),} 
\end{array} \right. 
\label{S_2} \\
\Lambda_\chi &=& -\frac{16{\tilde{d}}({\tilde{d}}+1)c_\chi}{(c_\chi-1)^2} 
\label{kosmo_1}
