\section*{Appendix: Use of Coherent States in $SU(2)$}

Let 

\begin{equation}
a= \pmatrix{ a_1 \cr
a_2 \cr}~,~~~~a^\dag = \pmatrix{ a_1^\dag & a_2^\dag \cr}
\end{equation}
and

\begin{equation}
\xi= \pmatrix{ \xi_1 \cr \xi_2 \cr}~,~~~~\xi^\dag = \pmatrix{\xi_1^* & \xi_2^* \cr}
\end{equation}
with

\begin{equation}
[a_i,a_j^\dag]=\delta_{ij} 
\end{equation}
and defining the vacuum state $c=\mid 0>$, we have

\begin{equation}
a~\mid 0>=0~,~~~~<0\mid ~a^\dag=0
\end{equation}

We can construct a superposition state (coherent state) out of the vacuum state

\begin{equation}
\mid \xi>= e^{a^\dag \xi} ~\mid 0>~, ~~~~<\xi\mid =<0\mid ~e^{\xi^\dag a}
\end{equation}
that has the following properties:

(1.)

\begin{equation}
<0\mid \xi> =<\xi\mid 0> =<0\mid 0>=1
\end{equation}

(2.)
\begin{eqnarray}
\mid \xi>&=& e^{a_1^\dag \xi_1}~e^{a_2^\dag \xi_2}~ \mid 0> \\ 
& & = \sum_{j=0}^\infty~\sum_{m=-j}^j~ \frac{a_1^{\dag j+m}}{\sqrt{(j+m)!}}~ 
\frac{a_2^{\dag j-m}}{\sqrt{(j-m)!}}~\frac{\xi_1^{j+m}}{\sqrt{(j+m)!}}~\frac{\xi_2^{j-m}}{\sqrt{(j-m)!}}   
\end{eqnarray}
or

\begin{equation}
\mid \xi>= \sum_{j=0}^\infty~\sum_{m=-j}^j ~\frac{\xi_1^{j+m}}{\sqrt{(j+m)!}}~\frac{\xi_2^{j-m}}{\sqrt{(j-m)!}}~\mid j,m>   
\end{equation}
with

\begin{equation}
<\xi\mid = \sum_{j=0}^\infty~\sum_{m=-j}^j ~<j,m\mid  ~\frac{\xi_1^{*~j+m}}{\sqrt{(j+m)!}}~\frac{\xi_2^{*~j-m}}{\sqrt{(j-m)!}}
\end{equation}

(3.)

\begin{equation}
e^{-a^\dag \xi}~a_1~e^{a^\dag \xi} = e^{-a_1^\dag \xi_1} ~ a_1~e^{a_1^\dag \xi_1} = e^{\xi_1 \frac{\partial}{\partial a_1}}~a_1 = a_1+\xi_1
\end{equation}

\begin{equation}
e^{-a^\dag \xi}~a_2~e^{a^\dag \xi}=a_2+\xi_2 
\end{equation}
so that

\begin{equation}
e^{-a^\dag \xi}~a_i~e^{a^\dag \xi}=a_i +\xi_i 
\end{equation}

(4.)

\begin{equation}
a_i~\mid \xi>= \xi_i ~\mid \xi>
\end{equation}
so that $\mid \xi>$ is an eigenstate of $a_i$. We see that

\begin{eqnarray}
a_i~\mid \xi>&=& a_i~e^{a^\dag \xi} ~\mid 0> = e^{a^\dag \xi} ~e^{-a^\dag \xi}~a_i~ ~e^{a^\dag \xi}~\mid 0>=e^{a^\dag \xi} (a_i+\xi_i)~\mid 0>  \\ \nonumber & & =e^{a^\dag \xi}~\xi_i ~\mid 0>= \xi_i ~\mid \xi>
\end{eqnarray}
and similarly

\begin{equation}
<\xi\mid ~ a_i^\dag = <\xi\mid ~\xi_i^*
\end{equation}

(5.)

\begin{eqnarray}
<\xi\mid j,m>&=&\sum_{j'}~\sum_{m'}~
 ~\frac{\xi_1^{*~j'+m'}}{\sqrt{(j'+m')!}}~\frac{\xi_2^{*~j'-m'}}{\sqrt{(j'-m')!}}~<j',m'\mid j,m> \nonumber \\ & & = \sum_{j'} ~\sum_{m'}~  \frac{\xi_1^{*~j'+m'}}{\sqrt{(j'+m')!}}~\frac{\xi_2^{*~j'-m'}}{\sqrt{(j'-m')!}}~\delta_{j' j} ~\delta_{m' m}
\end{eqnarray}
or

\begin{equation}
<\xi \mid  j,m>=\frac{\xi_1^{*~j+m}}{\sqrt{(j+m)!}}~\frac{\xi_2^{*~j-m}}{\sqrt{(j-m)!}}
\end{equation}
is homogeneous in $\xi_1^*$, $\xi_2^*$ of degree $2j$ so that

\begin{equation}
<\lambda ~\xi\mid  j,m>= \lambda^{* 2j}~<\xi\mid j,m>
\end{equation}
and

\begin{equation}
<j,m\mid \lambda~ \xi>= \lambda^{2j}~<j,m\mid \xi>
\end{equation}

(6.)

\begin{eqnarray}
\frac{1}{\sqrt{(j+m)!}}~\frac{1}{\sqrt{(j-m)!}}~ (\frac{\partial}{\partial \xi_1^*})^{j+m}~(\frac{\partial}{\partial \xi_2^*})^{j-m}~<\xi\mid j,m>= \nonumber 
\end{eqnarray}
\begin{equation}
~~= \frac{1}{(j+m)!}~ \frac{1}{(j-m)!}~ (\frac{\partial}{\partial \xi_1^*})~(\frac{\partial}{\partial \xi_2^*})~\xi_1^{* j+m}~\xi_2^{* j-m}=1
\end{equation}

(7.)

\begin{equation}
a_i^\dag ~\mid \xi>= a_i^\dag~ e^{a_1^\dag \xi_1 + a_2^\dag \xi_2}~\mid 0>= \frac{\partial}{\partial \xi_i}~ e^{a^\dag \xi}~\mid 0>= \frac{\partial}{\partial \xi_i}~\mid \xi>
\end{equation}
Also

\begin{equation}
<\xi\mid ~a_i= \frac{\partial}{\partial \xi_*}~<\xi\mid 
\end{equation}
so that on $\mid \xi>$ we have $a_i \leftrightarrow \xi_i$, $a_i^\dag \leftrightarrow \frac{\partial}{\partial \xi_i} $.

(8.)

\begin{eqnarray}
& & <\xi\mid ~\frac{a_1^{j+m}}{\sqrt{(j+m)!}}~\frac{a_2^{j-m}}{\sqrt{(j-m)!}}~\mid j,m>=1 \nonumber \\
& & ~~~=<j,m\mid j,m>=<0\mid ~\frac{a_1^{j+m}}{\sqrt{(j+m)!}}~\frac{a_2^{j-m}}{\sqrt{(j-m)!}}~\mid j,m>
\end{eqnarray}

\section*{The D functions of $SU(2)$}

Since

\begin{equation}
J_i = a^\dag~\frac{\sigma_i}{2}~a
\end{equation}

\begin{equation}
J_i~\mid 0>=0,~~~~[J_i,J_j]=i~\epsilon_{ijk}~J_k
\end{equation}
and the relations

\begin{equation}
J_1+iJ_2= a_1^\dag~a_2,~~~~J_1-iJ_2= a_2^\dag~a_1,~~~~J_3= \frac{1}{2}~(a_1^\dag a_1 -a_2^\dag a_2)
\end{equation}

\begin{equation}
[J_3, J_1 \pm iJ_2] = J_1 \pm iJ_2~,~~~~~~[J_1 +iJ_2, J_1-iJ_2]=2J_3~,
\end{equation}
we have

\begin{equation}
e^{iJ_3\alpha} ~a^\dag~e^{-iJ_3 \alpha} = a^\dag~ e^{i\sigma_3\frac{\alpha}{2}}
\end{equation}
and

\begin{equation}
e^{i J_2 \beta}~ a^\dag~ e^{-i J_2 \beta} = a^\dag~ e^{i\sigma_2\frac{\beta}{2}}~.
\end{equation}

Now let

\begin{eqnarray}
U(\mbox{\boldmath$\omega$}) ~\mid j,m>&=& e^{iJ_3\alpha}~e^{iJ_2 \beta}~e^{iJ_3 \gamma}~ \mid j,m> \nonumber \\
& &= U~\frac{a_1^{\dag j+m}}{\sqrt{(j+m)!}}~\frac{a_2^{\dag j-m}}{\sqrt{(j-m)!}}~U^{-1}~U~\mid 0>
\end{eqnarray}
with

\begin{equation}
U~\mid 0>=\mid 0>~,~~~~U~a_1^\dag~U^{-1}=a_1^{'\dag}~,~~~~U~a_2^\dag~U^{-1}=a_2^{'\dag}~,
\end{equation}
where

\begin{equation}
a^\dag = \pmatrix{a_1^\dag & a_2^\dag \cr} 
\end{equation}
so that

\begin{eqnarray}
a^{'\dag}~U~a^\dag~U^{-1} &=& e^{iJ_3 \alpha} ~e^{iJ_2\beta}~ e^{iJ_3\gamma}~a^\dag~ e^{-iJ_3\gamma}~ e^{-iJ_2\beta}~e^{-iJ_3\alpha} \nonumber  \\
& & =a^\dag~e^{i\sigma_3 \frac{\alpha}{2}}~ e^{i\sigma_2 \frac{\beta}{2}}~e^{i\sigma_3 \frac{\gamma}{2}} \nonumber  \\
& & =a^\dag~\pmatrix{ \psi_1 & -\psi_2^* \cr
\psi_2 & \psi_1^*} \nonumber  \\
& & = \pmatrix{ a^\dag \psi & a^\dag \hat{\psi}\cr}=\pmatrix{ a_1^{'\dag} & a_2^{'\dag}\cr }
\end{eqnarray}
where 

\begin{equation}
\psi_1= e^{i(\alpha + \gamma)/2}~cos~(\frac{\beta}{2})~,~~~~\psi_2= - e^{i(\gamma-\alpha)/2}~sin(\frac{\beta}{2})
\end{equation}
so that $U(\mbox{\boldmath$\omega$}) =U(\psi)$, and $\psi^\dag \psi=1$.

Also 
\begin{eqnarray}
U(\mbox{\boldmath$\omega$}) \mid j,m'>&=& \frac{a_1^{'\dag j+m'}}{\sqrt{(j+m')!}}~\frac{a_2^{'\dag j-m'}}{\sqrt{(j-m')!}}~\mid 0> \nonumber \\ & & = \frac{(a^\dag \psi)^{j+m'}}{\sqrt{(j+m')!}}~\frac{(a^\dag \hat{\psi})^{j-m'}}{\sqrt{(j-m')!}}~\mid 0>
\end{eqnarray}

Now we can write

\begin{eqnarray}
& &D_{m m'}^j(\mbox{\boldmath$\omega$})= D_{mm'}^j(\psi) = <j, m\mid U(\mbox{\boldmath$\omega$}) \mid j, m'> \nonumber \\ & &
~~~=\frac{(a^\dag \psi)^{j+m'}}{\sqrt{(j+m')!}}~\frac{(a^\dag \hat{\psi})^{j-m'}}{\sqrt{(j-m')!}}~\mid 0> \nonumber  \\ & &
~~~=<0\mid  \frac{a_1^{j+m}}{\sqrt{(j+m)!}}~ \frac{a_2^{j-m}}{\sqrt{(j-m')!}}~\frac{(a^\dag \psi)^{j+m'}}{\sqrt{(j+m')!}}~\frac{(a^\dag \hat{\psi})^{j-m'}}{\sqrt{(j-m')!}}~\mid 0>
\end{eqnarray}

Now to get the expression for $D_{mm'}^j(\psi)$, consider

\begin{equation}
<\xi \mid  U(\psi)\mid j,m'> =<\xi\mid \frac{(a^\dag \psi)^{j+m'}}{\sqrt{(j+m')!}}~\frac{(a^\dag \hat{\psi})^{j-m'}}{\sqrt{(j-m')!}}~\mid 0>
\end{equation}
Using

\begin{equation}
<\xi\mid ~a^\dag=<\xi\mid ~\xi^\dag
\end{equation}
and $<\xi\mid 0>=1$ we get

\begin{equation}
<\xi \mid  U(\psi)\mid j,m'> = \frac{(\xi^\dag \psi)^{j+m'}}{\sqrt{(j+m')!}}~\frac{(\xi^\dag \hat{\psi})^{j-m'}}{\sqrt{(j-m')!}}
\end{equation}
which is homogeneous of degree $2j$ in $\xi_1^*$, $\xi_2^*$. Hence the expression

\begin{equation}
\frac{1}{\sqrt{(j+m')!}}~\frac{1}{\sqrt{(j-m')!}}~ (\frac{\partial}{\partial \xi_1^*})^{j+m}~(\frac{\partial}{\partial \xi_2^*})^{j-m}~ \frac{(\xi^\dag \psi)^{j+m'}}{\sqrt{(j+m')!}}~\frac{(\xi^\dag \hat{\psi})^{j-m'}}{\sqrt{(j-m')!}}  
\end{equation}
does not depend on $\xi$. Thus

\begin{eqnarray}
& & \frac{1}{\sqrt{(j+m')!}}~\frac{1}{\sqrt{(j-m')!}}~ (\frac{\partial}{\partial \xi_1^*})^{j+m}~(\frac{\partial}{\partial \xi_2^*})^{j-m}~ <\xi\mid U(\psi)\mid j,m'>= \nonumber  \\ & &~~~~= <\xi\mid  \frac{a_1^{j+m}}{\sqrt{(j+m)!}}~ \frac{a_2^{j-m}}{\sqrt{(j-m)!}}~U(\psi)~\mid j,m'>
\end{eqnarray}
does not depend on $\xi$. Hence we have

\begin{eqnarray}
& & <\xi\mid  ~ \frac{a_1^{j+m}}{\sqrt{(j+m)!}}~ \frac{a_2^{j-m}}{\sqrt{(j-m)!}}~U(\psi)~\mid j,m'>=  \nonumber  \\ & & ~~~~= <0\mid ~\frac{a_1^{j+m}}{\sqrt{(j+m)!}}~ \frac{a_2^{j-m}}{\sqrt{(j-m)!}}~U(\psi)~\mid j,m'>=D_{mm'}^j(\psi)
\end{eqnarray}
Thus

\begin{equation}
D_{mm'}^j(\psi)= \frac{1}{\sqrt{(j+m')!}}~\frac{1}{\sqrt{(j-m')!}}~ (\frac{\partial}{\partial \xi_1^*})^{j+m}~(\frac{\partial}{\partial \xi_2^*})^{j-m}~ <\xi\mid U(\psi)\mid j,m'>
\end{equation}
Using

\begin{equation}
x
<\xi\mid U(\psi)\mid j,m'> =  \frac{(\xi^\dag \psi)^{j+m'}}{\sqrt{(j+m')!}}~\frac{(\xi^\dag \hat{\psi})^{j-m'}}{\sqrt{(j-m')!}}  
\end{equation}
we finally get

\begin{eqnarray}
D_{mm'}^j(\psi)&=& \frac{1}{\sqrt{(j+m')!}}~\frac{1}{\sqrt{(j-m')!}}~ (\frac{\partial}{\partial \xi_1^*})^{j+m}~(\frac{\partial}{\partial \xi_2^*})^{j-m}~ \times \nonumber  \\
& &~~~~  \{\frac{(\xi^\dag \psi)^{j+m'}}{\sqrt{(j+m')!}}~\frac{(\xi^\dag \hat{\psi})^{j-m'}}{\sqrt{(j-m')!}}\}  
\end{eqnarray}

We note that $m$ numbers rows, $m'$ numbers columns. We have

\begin{equation}
D_{\frac{1}{2}~\frac{1}{2}}^{\frac{1}{2}}= \frac{\partial}{\partial \xi_1^*}~(\xi_1^* \psi_1 +\xi_2^* \psi_2)= \psi_1= e^{i(\alpha+\gamma)/2}~cos(\frac{\beta}{2})
\end{equation}

\begin{equation}
D_{\frac{1}{2}~-\frac{1}{2}}^{\frac{1}{2}}= \frac{\partial}{\partial \xi_1^*}~(-\xi_1^* \psi_2^* +\xi_2^* \psi_1^*)=-\psi_2^* ~~~~~~~~~~~~~~~~~~~~~
\end{equation}

\begin{equation}
D_{-\frac{1}{2}~\frac{1}{2}}^{\frac{1}{2}}= \frac{\partial}{\partial \xi_2^*}~(\xi_1^* \psi_1 +\xi_2^* \psi_2)= \psi_2= -e^{i(\gamma - \alpha)/2}~sin(\frac{\beta}{2})
\end{equation}

\begin{equation}
D_{-\frac{1}{2}~-\frac{1}{2}}^{\frac{1}{2}}= \frac{\partial}{\partial \xi_2^*}~(-\xi_1^* \psi_2^* +\xi_2^* \psi_1^*)= \psi_1^*~~~~~~~~~~~~~~~~~~~~~~
\end{equation}
so that

\begin{eqnarray}
D^{\frac{1}{2}}(\psi)&=& \pmatrix{D_{\frac{1}{2}~\frac{1}{2}}^{\frac{1}{2}} & D_{\frac{1}{2}~-\frac{1}{2}}^{\frac{1}{2}} \cr
D_{-\frac{1}{2}~\frac{1}{2}}^{\frac{1}{2}} & D_{-\frac{1}{2}~-\frac{1}{2}}^{\frac{1}{2}} \cr}= \pmatrix{ \psi_1 & -\psi_2^* \cr \psi_2 & \psi_1^* \cr}= \nonumber  \\ & =& e^{i\sigma_3 \frac{\alpha}{2}}~e^{i \sigma_2 \frac{\beta}{2}}~e^{i\sigma_3 \frac{\gamma}{2}}= \pmatrix{ e^{\frac{i}{2} (\alpha+\gamma)}~cos \frac{\beta}{2} & e^{-\frac{i}{2} (\gamma -\alpha)}~sin \frac{\beta}{2} \cr -e^{\frac{i}{2} (\gamma -\alpha)}~sin \frac{\beta}{2} & e^{-\frac{i}{2} (\alpha+\gamma)}~cos \frac{\beta}{2} \cr}  
\end{eqnarray}

Similarly

\begin{equation}
D^1(\psi)=\pmatrix{ \psi_1^2 & -\sqrt{2}  \psi_1 \psi_2^* & \psi_2^* \cr \sqrt{2} \psi_1 \psi_2 & \mid \psi_1\mid^2-\mid \psi_2\mid^2 & -\sqrt{2} \psi_1^* \psi_2^* \cr  \psi_2^2 & \sqrt{2} \psi_2 \psi_1^* & \psi_1^{*2} \cr}= e^{i\Sigma_3 \alpha}~e^{i\Sigma_2 \beta}~e^{i\Sigma_3 \gamma} 
\end{equation}
where $\Sigma_i$ are the $s=1$ (isotopic spin) matrices. They are

\begin{eqnarray}
\Sigma_1&=&\frac{1}{\sqrt{2}} \pmatrix{0 & 1 & 0 \cr 1 & 0 & 1 \cr 0 & 1 & 0 }~,~~~~\Sigma_2= \frac{1}{2i} \pmatrix{ 0 & \sqrt{2} & 0 \cr -\sqrt{2} & 0 & \sqrt{2} \cr 0 & -\sqrt{2} & 0 } \nonumber  \\
& & \Sigma_3= \pmatrix{ 1 & 0 & 0 \cr 0 & 0 & 0 \cr 0 & 0 & -1 \cr}
\end{eqnarray}
and we have

\begin{eqnarray}
\Sigma_+ = \Sigma_1 + i\Sigma_2 = \pmatrix{ 0 & \sqrt{2} & 0 \cr 0 & 0 & \sqrt{2} \cr 0 & 0 & 0 \cr}  \nonumber  \\
\Sigma_- = \Sigma_1 -i\Sigma_2 = \pmatrix{ 0 & 0 & 0 \cr \sqrt{2} & 0 & 0 \cr 0 & \sqrt{2} & 0 \cr}
\end{eqnarray}
These satisfy $[\Sigma_1, \Sigma_2]=i\Sigma_3$ and cyclic permutations. Now using angles $\phi=-\alpha$, $\theta=-\beta$ and $\psi=-\gamma$ we can write

\begin{eqnarray}
D^{\frac{1}{2}}(\psi)&=& \pmatrix{ \psi_1 & -\psi_2^* \cr \psi_2 & \psi_1^* \cr}=  e^{-i\sigma_3 \frac{\phi}{2}}~e^{-i \sigma_2 \frac{\theta}{2}}~e^{-i\sigma_3 \frac{\psi}{2}} \nonumber \\& & = \pmatrix{ e^{-\frac{i}{2} (\phi+\psi)}~cos \frac{\theta}{2} & -e^{-\frac{i}{2} (\phi -\psi)}~sin \frac{\beta}{2} \cr e^{\frac{i}{2} (\phi -\psi)}~sin \frac{\theta}{2} & e^{\frac{i}{2} (\phi+\psi)}~cos \frac{\theta}{2} \cr}  \label{eq:ellidort}
\end{eqnarray}
so that

\begin{equation}
\psi_1= cos \frac{\theta}{2}~e^{-i\frac{\phi+\psi}{2}} \label{eq:deellibes}
\end{equation}
and

\begin{equation}
\psi_2= sin \frac{\theta}{2}~e^{i \frac{\phi-\psi}{2}}  \label{eq:dellialti}
\end{equation}

Using these we can now write $D^1$ as

\begin{eqnarray}
D^1(\psi)= \pmatrix{ \frac{1+cos \theta}{2}~ e^{-i(\phi+\psi)} & -\frac{1}{\sqrt{2}}~ sin \theta ~e^{-i\phi} & \frac{1-cos \theta}{2}~ e^{-i(\phi-\psi)} \cr
\frac{1}{\sqrt{2}}~ sin \theta ~e^{-i\phi} & cos \theta & -\frac{1}{\sqrt{2}}~ sin \theta ~e^{i\phi} \cr
\frac{1-cos \theta}{2}~ e^{i(\phi+\psi)} & \frac{1}{\sqrt{2}}~ sin \theta ~e^{i\phi} & \frac{1+cos \theta}{2} ~e^{i(\phi+\psi)} \cr }
\end{eqnarray}

We have shown that we have formed $D$ functions with $\psi_1^{j+m} \psi_2^{j-m}$ in first columns. 

\section*{Euler Angles and $SU(2)/U(1)$ Coset Decomposition}

A group element for $SU(2)$ can be taken as

\begin{equation}
g_{\mbox{\boldmath$\omega$}} = A(\psi) = D^{\frac{1}{2}}(\psi)
\end{equation}
where
\begin{equation}
\psi = \left( \begin{array}{c}
\psi_1 \\  \psi_2  \end{array}  \right)
\end{equation}
with $\psi^\dag \psi =1$, or

\begin{equation}
\psi_1^* \psi_1 + \psi_2^* \psi_2 =1
\end{equation}

We can rewrite Eq.(\ref{eq:ellidort}) as

\begin{eqnarray}
D^{\frac{1}{2}}&=& \pmatrix{ \psi_1 & -\psi_2^* \cr \psi_2 & \psi_1^* \cr} = e^{-i \frac{\sigma_3}{2} ~\phi}~e^{-i \frac{\sigma_2}{2} ~\theta}~e^{-i \frac{\sigma_3}{2}~ \psi } \nonumber \\
& =& \pmatrix{ e^{-i \frac{\phi}{2}} & 0 \cr o & e^{i \frac{\phi}{2}} \cr} \pmatrix{ cos \frac{\theta}{2} & -sin \frac{\theta}{2} \cr sin \frac{\theta}{2} & cos \frac{\theta}{2} \cr} \pmatrix{ e^{-i \frac{\psi}{2}} & 0 \cr o & e^{i \frac{\psi}{2}} \cr} \nonumber \\
&=& \pmatrix{ cos \frac{\theta}{2}~e^{-i\frac{\phi +\psi}{2}} & -sin \frac{\theta}{2}~ e^{-i \frac{\phi-\psi}{2}} \cr
sin \frac{\theta}{2}~e^{i \frac{\phi-\psi}{2}} & cos \frac{\theta}{2}~e^{i \frac{\phi-\psi}{2}} \cr}  \label{eq:ellisekiz}
\end{eqnarray}

Let

\begin{equation}
z= \frac{\psi_2}{\psi_1} = tan \frac{\theta}{2}~e^{i\phi}
\end{equation}
and

\begin{equation}
\frac{\psi_1}{\mid \psi_1\mid } = e^{-i \frac{(\phi + \psi)}{2}} = e^{-i \frac{\eta}{2}}  \label{eq:altmisbir}
\end{equation}
where $z$ and $\eta$ are the parameters of the coset decomposition

\begin{equation}
SU(2)= (SU(2)/U(1))\times U(1)~.
\end{equation}
We can write $A=D^{\frac{1}{2}}$ as $A=BC$, where $C \in U(1)$ and $U(1)$ is the $U(1)$ subgroup of $SU(2)$ (locally isomorphic to $SO(2)$ subgroup of $SO(3)$). Now 

\begin{eqnarray}
D^{\frac{1}{2}}&=& \pmatrix{ \psi_1 & -\psi_2^* \cr \psi_2 & \psi_1^* \cr} \nonumber \\
&=& \pmatrix{ \psi_1 & -\psi_2^* \cr \psi_2 & \psi_1^* \cr} \pmatrix{ \frac{\mid \psi_1\mid }{\psi_1} &0 \cr 0 & \frac{\mid \psi_1\mid }{\psi_1^*} \cr} \pmatrix{ \frac{\mid \psi_1\mid }{\psi_1^*} &0 \cr 0 & \frac{\mid \psi_1\mid }{\psi_1} \cr} \nonumber  \\
&=& \pmatrix{ \mid \psi_1\mid  & -(\frac{\psi_2}{\psi_1})^*~\mid \psi_1\mid  \cr \frac{\psi_2}{\psi_1}~\mid \psi_1\mid  & \mid \psi_1\mid  \cr}
\pmatrix{ e^{-i \frac{\eta}{2}} & 0 \cr 0 & e^{i \frac{\eta}{2}} \cr} \nonumber \\
&=& \mid \psi_1 \mid \pmatrix{ 1 & -( \frac{\psi_2}{\psi_1})^* \cr \frac{\psi_2}{\psi_1} & 1 }
\pmatrix{ e^{-i \frac{\eta}{2}} & 0 \cr 0 & e^{i \frac{\eta}{2}} \cr} 
\end{eqnarray}

Since 
\begin{equation}
\mid \psi_1\mid^2 + \mid \psi_2\mid^2=\mid \psi_1\mid^2 (1+\mid \frac{\psi_2}{\psi_1}\mid^2)=1
\end{equation}
we have

\begin{equation}
\mid \psi_1\mid = \frac{1}{\sqrt{1+\mid z\mid^2}}
\end{equation}
Thus

\begin{equation}
D^{\frac{1}{2}} = \frac{1}{\sqrt{1+\mid z\mid^2}}~\pmatrix{ 1 & -z^* \cr z & 1 \cr} \pmatrix{ e^{-i \frac{\eta}{2}} & 0 \cr 0 & e^{i \frac{\eta}{2}} \cr}
\end{equation}

Now

\begin{equation}
B=\mid \psi_1 \mid \pmatrix{ 1 & -(\frac{\psi_2}{\psi_1})^* \cr (\frac{\psi_2}{\psi_1}) & 1 \cr} = \frac{1}{\sqrt{1+{\mid z \mid}^2}} \pmatrix{ 1 & -z^* \cr z & 1 \cr} 
\end{equation}
labels cosets $SU(2)/U(1)$. Therefore

\begin{equation}
A= \frac{1}{\sqrt{1+\mid z \mid^2}} \pmatrix{ 1 & -z^* \cr z & 1 \cr} \pmatrix{ \frac{\mid \psi_1 \mid}{\psi_1^*} &0 \cr 0 & \frac{\mid \psi_1 \mid}{\psi_1} \cr}
\end{equation}
with $\psi_1=z~\psi_2$ and $z$ is the coset label for $SU(2)/U(1)$. 

Under a rotation

\begin{equation}
\Omega= \pmatrix{ \omega_1 & \omega_2 \cr -\omega_2^* & \omega_1^* \cr}
\end{equation}
with

\begin{equation}
\mid \omega_1 \mid^2 + \mid \omega_2 \mid^2 =1
\end{equation}
we have

\begin{equation}
A' = \Omega A = \pmatrix{ \psi_1' & -\psi_2'^* \cr \psi_2' & \psi_1'^* \cr}
\end{equation}
or

\begin{equation}
\pmatrix{ \psi_1' \cr \psi_2' \cr} = \pmatrix{ \omega_1 & -\omega_2^* \cr \omega_2 & \omega_1^* \cr} \pmatrix{ \psi_1 \cr \psi_2 \cr}= \pmatrix{ \omega_1 \psi_1 -\omega_2^* \psi_2 \cr \omega_2 \psi_1 + \omega_1 \psi_2 \cr}
\end{equation}

We can write

\begin{equation}
A'= \frac{1}{\sqrt{1+\mid z' \mid^2}} \pmatrix{ 1 & -z'^* \cr z' & 1 \cr} \pmatrix{ \frac{\mid \psi_1' \mid}{\psi_1'^*} &0 \cr 0 & \frac{\mid \psi_1' \mid}{\psi_1'} \cr}
\end{equation}
with $\psi_2'=z' \psi_1'$.

Thus, for the coset label $z$ we have the transformation law

\begin{equation}
T_{\Omega} z = z' = \frac{\psi_2'}{\psi_1'} = \frac{\omega_2 \psi_1 + \omega_1^* \psi_2}{\omega_1 \psi_1 -\omega_2^* \psi_2} = \frac{\omega_2 + \omega_1^* z}{ \omega_1 - \omega_2^* z}
\end{equation}
which is a M\"obius transformation determined by $\Omega$.

We also have

\begin{equation}
\psi_1'= (\omega_1 -\omega_2^*z) \psi_1
\end{equation}

Thus far we have shown $A=A(z,\psi_1)$ and under $SU(2)$

\begin{equation}
A \rightarrow T_\Omega A= A(T_\Omega z, T_\Omega \psi_1)
\end{equation}

\begin{equation}
\Omega A= A(\frac{\omega_2 + \omega_1^* z}{\omega_1 - \omega_2^* z} , (\omega_1 -\omega_2^* z)\psi_1)
\end{equation}
where $z$ is the label for $SU(2)/U(1)$ and $\frac{\psi_1}{\mid \psi_1 \mid} = e^{-i\eta/2}$ where $\eta$ is the parameter for the $U(1)$ subgroup.

Using Eqs.(\ref{eq:ellisekiz}) and (\ref{eq:altmisbir}) we arrive at

\begin{equation}
A=BC = e^{-i \frac{\sigma_3}{2}\phi}~ e^{-i \frac{\sigma_2}{2}\theta}~ e^{-i \frac{\sigma_3}{2}\phi}~ e^{-i \frac{\sigma_3}{2}\eta}
\end{equation}
where

\begin{equation}
 B= e^{-i \frac{\sigma_3}{2}\phi}~ e^{-i \frac{\sigma_2}{2}\theta}~ e^{-i \frac{\sigma_3}{2}\phi}
\end{equation}
has the form

\begin{eqnarray}
B&=& \pmatrix{ e^{-i \frac{\phi}{2}} & 0 \cr 0 & e^{i \frac{\phi}{2}} \cr} \pmatrix{cos\frac{\theta}{2} & -sin \frac{\theta}{2} \cr sin\frac{\theta}{2} & cos\frac{\theta}{2} \cr} 
\pmatrix{ e^{i \frac{\phi}{2}} & 0 \cr 0 & e^{-i \frac{\phi}{2}} \cr} \nonumber  \\
&=& \pmatrix{cos\frac{\theta}{2} & -e^{-i\phi}sin \frac{\theta}{2} \cr e^{i\phi}sin\frac{\theta}{2} & cos\frac{\theta}{2} \cr} = cos\frac{\theta}{2} \pmatrix{ 1 & -e^{-i\phi} tan\frac{\theta}{2} \cr e^{i\phi} tan\frac{\theta}{2} & 1 \cr}
\end{eqnarray} 

If we put

\begin{equation}
z= -e^{-i\phi}~tan\frac{\theta}{2}   \label{eq:za}
\end{equation}
then

\begin{equation}
cos\frac{\theta}{2} =\frac{1}{\sqrt{1 + \mid z \mid^2}}
\end{equation}

On the other hand, if 

\begin{equation}
\frac{\psi_1}{\mid \psi_1 \mid} = e^{\frac{i}{2} (\psi+\phi)}  \label{eq:zb}
\end{equation}
we have

\begin{equation}
C= e^{-i \frac{\sigma_3}{2} (\psi+\phi)} = \pmatrix{ \frac{\mid \psi_1 \mid}{\psi_1^*} & 0 \cr 0 & \frac{\mid \psi_1 \mid }{\psi_1} \cr}
\end{equation}

Thus the formulas Eqs.(\ref{eq:za}) and (\ref{eq:zb}) give the relation between Euler angles and the coset decomposition with respect to $U(1)$. Note that $z$ involves only the two Euler angles $\phi$ and $\theta$.

For $j=1$ we find
\begin{eqnarray}
D^{(1)}(\psi)&=& \pmatrix{ \psi_1^2 & -\sqrt{2}  \psi_1 \psi_2^* & \psi_2^* \cr \sqrt{2} \psi_1 \psi_2 & \mid \psi_1\mid^2-\mid \psi_2\mid^2 & -\sqrt{2} \psi_1^* \psi_2^* \cr  \psi_2^2 & \sqrt{2} \psi_2 \psi_1^* & \psi_1^{*2} \cr} \times  \nonumber \\
& &~~\pmatrix{ \frac{\mid \psi_1\mid^2}{\psi_1^2} & 0 & 0 \cr 0 & 1 & 0 \cr 0 & 0 & \frac{\mid \psi_1\mid^2}{\psi_1^{*2}} \cr} 
\pmatrix{ \frac{\psi_1^2}{\mid \psi_1\mid^2} & 0 & 0 \cr 0 & 1 & 0 \cr 0 & 0 & \frac{\mid \psi_1\mid^{*2}}{\mid \psi_1\mid^2} \cr} \nonumber  \\
&=& \mid \psi_1\mid^2 \pmatrix{ 1 & -\sqrt{2} (\frac{\psi_2}{\psi_1})^* & (\frac{\psi_2}{\psi_1})^{*2} \cr
\sqrt{2} ~\frac{\psi_2}{\psi_1} & 1-\mid \frac{\psi_2}{\psi_1}\mid^2 & -\sqrt{2}~ \frac{\psi_2^*}{\psi_1^*} \cr
(\frac{\psi_2}{\psi_1})^2 & \sqrt{2}~ \frac{\psi_2}{\psi_1} & 1 \cr}  
\pmatrix{ e^{-i\eta} &0 &0 \cr 0 & 1 & 0 \cr 0 & 0 & e^{i\eta} \cr} 
\end{eqnarray}
or

\begin{eqnarray}
D^{(1)}= \frac{1}{1+\mid z\mid^2} \pmatrix{ 1 & -\sqrt{2}~ z^* & z^{*2} \cr \sqrt{2}~z & 1- z z^* & - \sqrt{2}~z^* \cr z^2 & \sqrt{2}~z & 1 \cr} \pmatrix{ e^{-i\eta} &0 &0 \cr 0 & 1 & 0 \cr 0 & 0 & e^{i\eta} \cr} 
\end{eqnarray}

In general the elements of 

\begin{equation}
(1+ \mid z\mid^2)^{2j}~D^{(j)}~e^{J_3 \eta} =Z
\end{equation}
are polynomials in $z$ and $z^*$. The first column is analytic and the last column is antianalytic.






