\appendix
\section{Notations}
Basically, we follow the notations of Ref.\cite{WessBagger} 
for the 4D bulk theory. 
The notations for the 3D theories are as follows. 

We take the space-time metric as 
\begin{equation}
 \eta^{mn}={\rm diag}(-1,+1,+1). 
\end{equation}

The 3D $\gamma$-matrices, $(\gamma_{(3)}^m)_\alpha^{\;\beta}$, 
can be written by the Pauli matrices as 
\begin{equation}
 \gamma_{(3)}^0=\sigma^2, \;\;\;
 \gamma_{(3)}^1=-i\sigma^3, \;\;\;
 \gamma_{(3)}^3=i\sigma^1, 
\end{equation}
and these satisfy the 3D Clifford algebra, 
\begin{equation}
 \left\{ \gamma_{(3) \right\}^m,\gamma_{(3)}^n}=-2\eta^{mn}. 
\end{equation}

The spinor indices are raised and lowered by multiplying 
$\sigma^2$ from the left. 
\begin{equation}
 \psi_\alpha=(\sigma^2)_{\alpha\beta}\psi^\beta, \;\;\;
 \psi^\alpha=(\sigma^2)^{\alpha\beta}\psi_\beta. 
\end{equation}
We take the following convention of the contraction of spinor 
indices. 
\begin{equation}
 \psi_1\psi_2\equiv \psi_1^\alpha\psi_{2\alpha}
 =(\sigma^2)_{\alpha\beta}\psi_1^\alpha\psi_2^\beta=\psi_2\psi_1. 
\end{equation}

\subsection{Covariant derivatives} \label{cov_ders}
The algebra of the 3D ${\cal N}=1$ SUSY preserved by the wall is 
\begin{equation}
 \left\{ Q_{2\alpha \right\},Q_{2\beta}}=2(\gamma_{(3)}^m\sigma^2)_{\alpha\beta}P_m. 
\end{equation}
The representation of the generators on the 3D ${\cal N}=1$ superspace 
$(x^m,\theta_2)$ is 
\begin{eqnarray}
 \hat{P}_m \!\!\!&=\!\!\!& -i\partial_m, \nonumber\\
 \hat{Q}_{2\alpha} \!\!\!&=\!\!\!& \partial_{2\alpha}+i(\gamma_{(3)}^m\theta_2)_\alpha\partial_m. 
\end{eqnarray}

The SUSY covariant derivative for $\theta_2$ is  
\begin{equation}
 D_{2\alpha}\equiv \partial_{2\alpha}-i(\gamma_{(3)}^m\theta_2)_\alpha\partial_m. 
\end{equation}

We list the covariant derivatives for superspace coordinates 
in the presence of the NG superfields as follows. 
They can be obtained by calculating 
the Cartan one-form~$\Omega^{-1}{\rm d}\Omega$ where $\Omega$ 
is defined in Eq.(\ref{Omega}). 
\begin{eqnarray}
 {\cal D}_m \rho_0 \!\!\!&=\!\!\!& (\omega^{-1})_m^{\;n}\partial_n\rho_0=\partial_m\rho_0+{\cal O}(\kappa^2), 
 \nonumber\\
 {\cal D}_{2\alpha}\rho_0 \!\!\!&=\!\!\!& D_{2\alpha}\rho_0+2\zeta_{0\alpha}
 -i\kappa^2D_{2\alpha}\zeta_0\gamma_{(3)}^m\zeta_0(\omega^{-1})_m^{\;n}\partial_n\rho_0, 
 \nonumber\\
 {\cal D}_m\zeta_{0\beta} \!\!\!&=\!\!\!& (\omega^{-1})_m^{\;n}\partial_n\zeta_{0\beta}
 =\partial_m\zeta_{0\beta}+{\cal O}(\kappa^2), \nonumber\\
 {\cal D}_{2\alpha}\zeta_{0\beta} \!\!\!&=\!\!\!& D_{2\alpha}\zeta_{0\beta}
 -i\kappa^2D_{2\alpha}\zeta_0\gamma_{(3)}^m\zeta_0(\omega^{-1})_m^{\;n}\partial_n\zeta_{0\beta}, 
 \nonumber\\
 {\cal D}_m\phi \!\!\!&=\!\!\!& (\omega^{-1})_m^{\;n}\partial_n\phi=\partial_m\phi+{\cal O}(\kappa^2), 
 \nonumber\\
 {\cal D}_{2\alpha}\phi \!\!\!&=\!\!\!& D_{2\alpha}\phi
 -i\kappa^2D_{2\alpha}\zeta_0\gamma_{(3)}^m\zeta_0(\omega^{-1})_m^{\;n}\partial_n\phi, 
 \label{cov_drvs1}
\end{eqnarray}
where $\phi$ denotes a matter field, and 
\begin{equation}
 \omega_m^{\;n}\equiv \delta_m^{\;n}+i\kappa^2\partial_m\zeta_0\gamma_{(3)}^n\zeta_0. 
\end{equation}


\subsection{Action of the generators on the fields} \label{act_on_fld}
The SUSY transformation~$\delta^Q_\xi$ of a chiral 
supermultiplet~$(A,\Psi^\alpha,F)$ is defined by 
\begin{eqnarray}
 \delta^Q_\xi A \!\!\!&=\!\!\!& \sqrt{2}\xi\Psi, \nonumber\\
 \delta^Q_\xi \Psi_\alpha \!\!\!&=\!\!\!& i\sqrt{2}(\sigma^\mu\bar{\xi})_\alpha
  \partial_\mu A+\sqrt{2}\xi_\alpha F, \nonumber\\
 \delta^Q_\xi F \!\!\!&=\!\!\!& i\sqrt{2}\bar{\xi}\bar{\sigma}^\mu\partial_{\mu}\Psi. 
\end{eqnarray}

We define an action of the generators~$P_\mu$, $Q_\alpha$ and 
$\bar{Q}^{\dot{\alpha}}$ on the fields $\phi=A,\Psi^\alpha,F$ as 
\begin{eqnarray}
 P_\mu \times \phi \!\!\!&\equiv\!\!\!& -i\partial_\mu\phi, \nonumber\\
 (\xi Q+\bar{\xi}\bar{Q})\times \phi \!\!\!&\equiv\!\!\!& \delta^Q_\xi \phi. 
\end{eqnarray}

Under the above definition, the chiral superfield~$\Phi$ can be 
written as 
\begin{equation}
 \Phi(x,\theta,\bar{\theta})=e^{ix^\mu P_\mu+\theta Q+\bar{\theta}\bar{Q}}\times A(0). 
\end{equation}

There is a useful formula that converts covariant derivatives~${\cal D}_M$ 
into the corresponding generators~$\Gamma_M$. 
\begin{equation}
 {\cal D}_M e^{iX^N \Gamma_N} = e^{iX^N \Gamma_N} \Gamma_M, 
 \label{usf_fml}
\end{equation}
where $X^M$ are coordinates for $\Gamma_M$. 

For the SUSY generator~$Q_2$, for instance, 
\begin{equation}
 {\cal D}_{2\alpha} e^{ix^m P_m+\theta_2 Q_2} = e^{ix^m P_m+\theta_2 Q_2}
 Q_{2\alpha}. 
\end{equation}

Using this formula, we can rewrite the chiral condition for $\Phi$  
as follows. 
\begin{eqnarray}
 \bar{D}_{\dot{\alpha}}\Phi \!\!\!&=\!\!\!& 
 \bar{D}_{\dot{\alpha}}e^{\theta Q+\bar{\theta}\bar{Q}}
 \times A(x) \nonumber\\
 \!\!\!&=\!\!\!& e^{\theta Q+\bar{\theta}\bar{Q}}\bar{Q}_{\dot{\alpha}}\times A(x)=0. 
\end{eqnarray}
Then, 
\begin{equation}
 \bar{Q}_{\dot{\alpha}}\times A(x)=
 -\frac{e^{i\delta/2}}{\sqrt{2}}(Q_{1\alpha}+iQ_{2\alpha})\times A(x)
 =0. 
\end{equation}
Namely, 
\begin{equation}
 Q_{1\alpha}\times A = -iQ_{2\alpha}\times A.  \label{chiral_cond}
\end{equation}

\section{Bosonic part of $S_{\rm NG}$} \label{SUSY_NG}
Here we will provide a brief derivation of the Nambu-Goto action 
from the effective action~$S_{\rm NG}$ defined in Eq.(\ref{Sng}). 
We denote the component fields of $\varphi_0$ as 
\begin{equation}
 \varphi_0=a_0+\theta_2\eta_0+\frac{1}{2}\theta_2f_0. 
 \label{comp_vph}
\end{equation}
In order to concentrate on the bosonic part of $S_{\rm NG}$, 
we will neglect the fermionic component~$\eta_0$ 
in the following. 
Then, 
\begin{equation}
 \psi_0^2|_{\rm bosonic}=\frac{1}{4}\theta_2^2(f_0^2-\partial^ma_0\partial_ma_0), 
\end{equation}
and 
\begin{equation}
 D_2^2(\psi_0^2)|_{\rm bosonic}=-f_0^2+\partial^ma_0\partial_ma_0. 
\end{equation}
Therefore, the auxiliary field~$f_0$ enters in $S_{\rm NG}$ 
only in a bilinear way. 
This means that an equation of motion for $f_0$ is $f_0$=0. 
Hence, after the elimination of $f_0$, the bosonic part of $S_{\rm NG}$ 
becomes  
\begin{eqnarray}
 S_{\mbox{\scriptsize NG bosonic}}^{\mbox{\scriptsize on-shell}} \!\!\!&=\!\!\!& 
 \int\!\!{\rm d}^3x 
 \frac{-\partial^ma_0\partial_ma_0}{1+\sqrt{1+\kappa^2\partial^ma_0\partial_ma_0}} \nonumber\\
 \!\!\!&=\!\!\!& \int\!\!{\rm d}^3x \: \kappa^{-2}\left\{ 1-\sqrt{1+\kappa^2\partial^ma_0\partial_ma_0 \right\}}. 
 \label{Nambu-Goto}
\end{eqnarray}
This is the Nambu-Goto action in the static gauge, as expected. 


\section{Transformation of $\varphi^i$ under the broken symmetries} 
\label{trf_vph}
The transformation laws of $\varphi^i$ for the broken symmetries are obtained 
from the definition of $\varphi^i$ in Eq.(\ref{def_vph}) 
and the SUSY algebra~(\ref{SUSYalg1}). 

For example, for the $Q_1$-transformation, 
\begin{eqnarray}
 \delta_{\xi_1}^{Q_1}\varphi^i \!\!\!&=\!\!\!& \xi_1Q_1\times\varphi^i
 =\xi_1Q_1e^{ix^mP_m+ix_2P_2+\theta_2Q_2}\times A^i(0) \nonumber\\
 \!\!\!&=\!\!\!& e^{ix^mP_m+ix_2P_2+\theta_2Q_2}(-2i\xi_1\theta_2P_2+\xi_1Q_1)\times A^i(0) 
 \nonumber\\
 \!\!\!&=\!\!\!& e^{ix^mP_m+ix_2P_2+\theta_2Q_2}(-2i\xi_1\theta_2P_2-i\xi_1Q_2)\times 
 A^i(0) \nonumber\\
 \!\!\!&=\!\!\!& (-2\xi_1\theta_2\partial_2-i\xi_1D_2)\varphi^i.  \label{Q1vph}
\end{eqnarray}
Here we have used the chiral condition~Eq.(\ref{chiral_cond})
%\be
% Q_1\times A^i=-iQ_2\times A^i, 
%\ee
and the formula~Eq.(\ref{usf_fml}). 

Similarly, for the $P_2$-transformation, 
\begin{equation}
 \delta_a^{P_2}\varphi^i=iaP_2\times \varphi^i=a\partial_2\varphi^i. \label{P2vph}
\end{equation}

For the broken Lorentz transformation, 
\begin{equation}
 \delta_v^K\varphi^i = iv^mK_m\times \varphi^i
 =v^m\left\{ x_2\partial_m\varphi^i-x_m\partial_2\varphi^i
 +\frac{1 \right\}{2}\theta_2\gamma_{(3)m}D_2\varphi^i}.  \label{Kvph}
\end{equation}

For the $U(1)_R$ transformation, 
\begin{equation}
 \delta_r^R\varphi^i = irR\times \varphi^i 
 =r\left\{ \theta_2^2\partial_2\varphi^i+i\theta_2D_2\varphi^i \right\}. \label{Rvph}
\end{equation}


\section{In the case of $G=G_{\rm max}$}
Here we collect the main results of the nonlinear realization 
in the case that the full symmetry group $G$ is taken to be 
$G_{\rm max}=\left\{ P_m,P_2,Q_{1\alpha \right\},Q_{2\alpha},M_{mn},K_m,R}$. 
The vacuum stability subgroup is 
$H_{\rm max}=\left\{ P_m,Q_{2\alpha \right\},M_{mn}}$ in this case. 

\subsection{Super-Poincar\'{e} algebra} \label{superPalg}
The 3D ${\cal N}=2$ super-Poincar\'{e} algebra with central extension is 
as follows. 

\begin{eqnarray}
 \{Q_{1\alpha},Q_{1\beta}\} \!\!\!&=\!\!\!& \{Q_{2\alpha},Q_{2\beta}\}
 =2(\gamma_{(3)}^m\sigma^2)_{\alpha\beta}P_m, \nonumber\\
 \{Q_{1\alpha},Q_{2\beta}\} \!\!\!&=\!\!\!& -\{Q_{2\alpha},Q_{1\beta}\}
 =2i(\sigma^2)_{\alpha\beta}P_2,  \nonumber\\
 \left[ M_{mn \right],Q_{1\alpha}} \!\!\!&=\!\!\!& -i\left( \gamma_{(3)mn \right)Q_1}_\alpha, \;\;\;
 \left[ M_{mn \right],Q_{2\alpha}}=-i\left( \gamma_{(3)mn \right)Q_2}_\alpha, \nonumber\\
 \left[ K_m,Q_{1\alpha \right]} \!\!\!&=\!\!\!& -\frac{1}{2}\left( \gamma_{(3)m \right)Q_2}_\alpha, \;\;\;
 \left[ K_m,Q_{2\alpha \right]}=\frac{1}{2}\left( \gamma_{(3)m \right)Q_1}_\alpha, \nonumber\\
 \left[ R,Q_{1\alpha \right]} \!\!\!&=\!\!\!& -iQ_{2\alpha}, \;\;\;
 \left[ R,Q_{2\alpha \right]}=iQ_{1\alpha}, \nonumber\\
 \left[ M_{mn \right],P_l} \!\!\!&=\!\!\!& i(\eta_{lm}P_n-\eta_{ln}P_m), \;\;\;
 \left[ M_{mn \right],P_2} = 0, \nonumber\\
 \left[ K_m,P_n \right] \!\!\!&=\!\!\!& i\eta_{mn}P_2, \;\;\;
 \left[ K_m,P_2 \right] = -iP_m, \nonumber\\
 \left[ M_{mn \right],M_{lp}} \!\!\!&=\!\!\!& 
 i(\eta_{ml}M_{np}-\eta_{nl}M_{mp}-\eta_{pn}M_{lm}+\eta_{pm}M_{ln}), 
 \nonumber\\
 \left[ M_{mn \right],K_l} \!\!\!&=\!\!\!& i(\eta_{ml}K_n-\eta_{nl}K_m), \nonumber\\
 \left[ K_m,K_n \right] \!\!\!&=\!\!\!& iM_{mn}.  \label{SP_alg}
\end{eqnarray}

\subsection{Standard nonlinear transformations} \label{SNT}
A coset element~$\hat{\Omega}$ can be parameterized as 
\begin{equation} 
 \hat{\Omega}=e^{ix^mP_m+\theta_2Q_2}e^{i\rho_0P_2+\zeta_0Q_1}
 e^{i\lambda_0^mK_m+i\sigma_0 R},  \label{hatOmega}
\end{equation}
where $\rho_0$, $\zeta_{0\alpha}$, $\lambda_0^m$ and $\sigma_0$ 
are the NG superfields for the corresponding generators. 

The transformation laws of each superfield are obtained 
by multiplying $\hat{\Omega}$ by corresponding group elements 
from the left. 

For $P_2$-transformation, 
\begin{eqnarray}
 \delta_a^{P_2}\rho_0 \!\!\!&=\!\!\!& \kappa^{-1}a, \nonumber\\
 \delta_a^{P_2}\zeta_{0\alpha} \!\!\!&=\!\!\!& \delta_a^{P_2}\lambda_0^m 
 =\delta_a^{P_2}\sigma_0 =\delta_a^{P_2}\phi=0. 
 \label{dltP2_st}
\end{eqnarray}

For the broken SUSY, 
\begin{eqnarray}
 \delta_{\xi_1}^{Q_1}\rho_0 \!\!\!&=\!\!\!& -2\kappa^{-1}\xi_1\theta_2
 -i\kappa\xi_1\gamma_{(3)}^m\zeta_0\partial_m\rho_0, \nonumber\\
 \delta_{\xi_1}^{Q_1}\zeta_{0\alpha} \!\!\!&=\!\!\!& \kappa^{-1}\xi_{1\alpha} 
 -i\kappa\xi_1\gamma_{(3)}^m\zeta_0\partial_m\zeta_{0\alpha}, \nonumber\\
 \delta_{\xi_1}^{Q_1}\lambda_0^m \!\!\!&=\!\!\!& 
 =-i\kappa\xi_1\gamma_{(3)}^n\zeta_0\partial_n\lambda_0^m, \nonumber\\
 \delta_{\xi_1}^{Q_1}\sigma_0 \!\!\!&=\!\!\!& 
 =-i\kappa\xi_1\gamma_{(3)}^m\zeta_0\partial_m\sigma_0, \nonumber\\
 \delta_{\xi_1}^{Q_1}\phi \!\!\!&=\!\!\!& 
 =-i\kappa\xi_1\gamma_{(3)}^m\zeta_0\partial_m\phi. 
 \label{dltQ1_st}
\end{eqnarray}

For the broken Lorentz symmetry, 
\begin{eqnarray}
 \delta_v^K\rho_0 \!\!\!&=\!\!\!& -\kappa^{-1}v^mx_m+{\cal O}(\kappa), \nonumber\\
 \delta_v^K\zeta_{0\alpha} \!\!\!&=\!\!\!& -\frac{i}{2}\kappa^{-1}v^m
 \left( \gamma_{(3)m \right)\theta_2}_\alpha+{\cal O}(\kappa), \nonumber\\
 \delta_v^K\lambda_0^m \!\!\!&=\!\!\!& \kappa^{-1}v^m+{\cal O}(\kappa), \nonumber\\
 \delta_v^K\sigma_0 \!\!\!&=\!\!\!& {\cal O}(\kappa), \nonumber\\
 \delta_v^K\phi \!\!\!&=\!\!\!& \kappa v^m\left\{ \left( -\rho_0+\frac{1 \right\ \right){2}
 \zeta_0\gamma_{(3)}^n\gamma_{(3)m}\theta_2}\partial_n\phi
 +\frac{i}{2}\zeta_0\gamma_{(3)m}D_2\phi}. 
 \label{dltK_st}
\end{eqnarray}

For the $U(1)_R$ symmetry, 
\begin{eqnarray}
 \delta_r^R\rho_0 \!\!\!&=\!\!\!& r\left\{ \kappa^{-1 \right\}\theta_2^2-\kappa\zeta_0^2
 -i\kappa\zeta_0\gamma_{(3)}^m\theta_2\partial_m\rho_0-\kappa\zeta_0D_2
 \rho_0}, \nonumber\\
 \delta_r^R\zeta_{0\alpha} \!\!\!&=\!\!\!& r\left\{ -\kappa^{-1 \right\}\theta_{2\alpha}
 -i\kappa\zeta_0\gamma_{(3)}^m\theta_2\partial_m\zeta_{0\alpha}
 -\kappa\zeta_0D_2\zeta_{0\alpha}}, \nonumber\\
 \delta_r^R\lambda_0^m \!\!\!&=\!\!\!& r\left\{ -i\kappa\zeta_0\gamma_{(3) \right\}^n\theta_2
 \partial_n\lambda_0^m-\kappa\zeta_0D_2\lambda_0^m}, \nonumber\\
 \delta_r^R\sigma_0 \!\!\!&=\!\!\!& r\left\{ \kappa^{-1 \right\}-i\kappa\zeta_0\gamma_{(3)}^m
 \theta_2\partial_m\sigma_0-\kappa\zeta_0D_2\sigma_0}, \nonumber\\
 \delta_r^R\phi \!\!\!&=\!\!\!& r\left\{ -i\kappa\zeta_0\gamma_{(3) \right\}^m\theta_2
 \partial_m\phi-\kappa\zeta_0D_2\phi}. \label{dltR_st}
\end{eqnarray}

Here, $a$, $\xi_{1\alpha}$, $v^m$ and $r$ are transformation 
parameters, and $\phi$ denotes a matter field. 

\subsection{Inverse Higgs effect}
The NG superfields introduced in Eq.(\ref{hatOmega}) are not 
independent. 
The relation between them are obtained by the following 
covariant constraints~\cite{ivsHiggs,manohar}. 
\begin{equation}
 \hat{{\cal D}}_{2\alpha}\rho_0=0, \;\;\;
 \hat{{\cal D}}_{2\alpha}\zeta_0^\beta=0, \;\;\;
 \hat{{\cal D}}_m\rho_0=0. 
\end{equation}
Here $\hat{{\cal D}}_m$ and $\hat{{\cal D}}_{2\alpha}$ are covariant 
derivatives in the presence of the NG superfields, 
which are derived from 
the Cartan one-form~$\hat{\Omega}^{-1}{\rm d}\hat{\Omega}$. 

Solving these constraints, we can express all NG superfields 
in terms of a single superfield~$\rho_0$. 
\begin{eqnarray}
 \zeta_{0\alpha} \!\!\!&=\!\!\!& -\frac{1}{2}D_{2\alpha}\rho_0+{\cal O}(\kappa^2), 
 \nonumber\\
 \lambda_0^m \!\!\!&=\!\!\!& iD_2\gamma_{(3)}^m\zeta_0+{\cal O}(\kappa^2)
 =-\partial^m\rho_0+{\cal O}(\kappa^2), \nonumber\\
 \sigma_0 \!\!\!&=\!\!\!& \frac{1}{2}D_2^\alpha\zeta_{0\alpha}+{\cal O}(\kappa^2) 
 =-\frac{1}{4}D_2^2\rho_0+{\cal O}(\kappa^2). 
\end{eqnarray}



