equation:
\begin{equation}
S_{l-s} = \int d^3 x \sqrt{-g} e^{\Phi}
   \big \{ R + (\nabla \Phi)^2 + {8 \over k} - {1 \over 12} H^2  \big \},
\label{action}
\

\begin{equation}
{\cal M}=1-M,~~T_c = (r_+^2 + r_{(-)}^2) / 2 \pi \ell^2 r_+,
~~{\cal A}_c = 2 \pi r_+,
~~\Omega_c = J / 2 r_+^2
\label{temp}
\

\begin{equation}
H_{t \phi r} = \bar H_{t \phi r} + {\cal H}_{t \phi r},~~~
\Phi = 0 + \varphi,~~~
g_{\mu\nu} = \bar g_{\mu\nu} + h_{\mu\nu}
\label{per}
\

\begin{equation}
{\tilde \varphi}''
+ {1 \over f(r)} \left ( {f(r) \over r} + f'(r) \right ) {\tilde \varphi}'
+ {1 \over f(r)} \left [
-{{J \mu \omega} \over {r^2 f(r)}}
+{\omega^2 \over f(r)}
+{(-M+{r^2 /\ell^2)} \over r^2 f(r)} \mu^2 - m_\varphi^2
\right ] \tilde \varphi
=0.
\label{eq_norm}
\

\begin{equation}
{1 \over f(r)} \left ( {f(r) \over r} + f'(r) \right ) =
-{1 \over r} +{1\over {r_+ + r}} -{1\over {r_+ - r}}
+{1 \over {r+ i r_{(-)}}} +{1 \over {r- i r_{(-)}}} ,
\label{coef1_rprm}
\

\begin{equation}
\lim_{r \rightarrow 0} {1 \over f(r)} \left ( {f(r) \over r} + f'(r) \right ) \simeq
- {1 \over r} + {8M \over J^2} r + {\cal O}(r^3).
\label{coef1_r0}
\

\begin{equation}
C_0 = {16 \over J^4} \left [ J \omega \mu + M \mu^2
  + {{ m_\varphi^2 J^2} \over 4} \right ] .
\label{def-c0}
\

\begin{equation}
\lim_{r \rightarrow i r_{(-)}} {1 \over f(r)} \left ( {f(r) \over r} + f'(r) \right ) \simeq
{1 \over {r- i r_{(-)}}}
\label{coef1_rmp}
\

\begin{equation}
\lim_{r \rightarrow -i r_{(-)}} {1 \over f(r)} \left ( {f(r) \over r} + f'(r) \right ) \simeq
{1 \over {r+ i r_{(-)}}}
\label{coef1_rmm}
