\begin{definition}
    \label{definition:starproduct}
    A star product $\star$ for $M$ is an associative
    $\mathbbm{C}[[\lambda]]$-bilinear product for
    $C^\infty(M)[[\lambda]]$,
    \begin{equation}
        \label{eq:starproduct}
        f \star g = \sum_{r=0}^\infty \lambda^r C_r(f,g),
    \end{equation}
    such that
    \begin{enumerate}
    \item $f \star g = fg + \cdots$,
    \item $f \star g - g \star f = {\mathrm i}\lambda\{f,g\} + \cdots$,
    \item $f \star 1 = f = 1 \star f$,
    \item $C_r$ is a bidifferential operator.
    \end{enumerate}
    If in addition $\overline{{f \star g}} = \overline{{g}} \star \overline{{f}}$ then the
    star product is called Hermitian.
\

\begin{definition}
    \label{definition:equivalence}
    Two star products $\star$ and $\star'$ are called equivalent if
    there is a formal series of differential operators $S = {\mathsf{id}} +
    \sum_{r=1}^\infty \lambda^r S_r$ such that (\ref{eq:newstar}) holds.
\

\begin{definition}
    \label{definition:PosPowerSer}
    A real formal power series $a = \sum_{r=r_0}^\infty \lambda^r
    a_r \in \mathbbm{R}[[\lambda]]$ is positive if $a_{r_0} > 0$.
\

\begin{definition}
    \label{definition:OrderedRing}
    An associative, commutative, unital ring ${\mathsf{{R}}}$ is called
    ordered with positive elements ${\mathsf{{P}}} \subseteq {\mathsf{{R}}}$ if 
    ${\mathsf{{P}}} \cdot {\mathsf{{P}}} \subseteq {\mathsf{{P}}}$, ${\mathsf{{P}}} + {\mathsf{{P}}}
    \subseteq {\mathsf{{P}}}$ and ${\mathsf{{R}}}$ is the disjoint union ${\mathsf{{R}}}
    = -{\mathsf{{P}}} \cup \{0\} \cup {\mathsf{{P}}}$.
\

\begin{definition}
    \label{definition:RepTheory}
    The representation theory of $\mathcal{A}$ is the category
    $\Rep(\mathcal{A})$ of all $^*$-representations of $\mathcal{A}$
    with intertwiners as morphisms.
\

\begin{definition}
    \label{definition:PosFun}
    Let $\mathcal{A}$ be a $^*$-algebra over ${\mathsf{{C}}}$. A linear
    functional $\omega: \mathcal{A} \to {\mathsf{{C}}}$ is called positive
    if
    \begin{equation}
        \label{eq:posfundef}
        \omega(A^*A) \ge 0
    \end{equation}
    for all $A \in \mathcal{A}$. It is called a state of $\mathcal{A}$
    if in addition $\omega(\mathbbm{1}) = 1$. Then the value $\omega(A)$ is
    called the expectation value of $A \in \mathcal{A}$ in the state
    $\omega$.
\

\begin{definition}
    \label{definition:posdef}
    A Hermitian deformation $\star$ of a $^*$-algebra $\mathcal{A}$
    over ${\mathsf{{C}}}$ is called a positive deformation if for any
    positive linear functional $\omega_0: \mathcal{A} \to {\mathsf{{C}}}$
    there exist `quantum corrections' $\omega_r$ such that
    \begin{equation}
        \label{eq:defposfun}
        \omega = \sum_{r=0}^\infty \lambda^r \omega_r:
        \mathcal{A}[[\lambda]] \to {\mathsf{{C}}}[[\lambda]]
    \end{equation}
    is positive with respect to $\star$.
\
