\begin{enumerate}
    \item Take $M = \mathbbm{R}^{2n}$ with the \emph{canonical Poisson
          bracket}
        \begin{equation}
            \label{eq:CanPoisson}
            \{f,g\} = \sum_{i=1}^n \left( 
                \frac{\partial f}{\partial q^i}
                \frac{\partial g}{\partial p_i}
                -
                \frac{\partial f}{\partial p_i}
                \frac{\partial g}{\partial q^i}
            \right).
        \end{equation}
    \item Let $\mathfrak{g}$ be a Lie algebra with structure constants
        $c^k_{ij}$ and let $\mathfrak{g}^*$ be its dual space with
        linear coordinates $x_1, \ldots, x_n$. Then
        \begin{equation}
            \label{eq:LiePoisson}
            \{f, g\} (x) = \sum_{i,j,k} x_k c^k_{ij} 
            \frac{\partial f}{\partial x_i}(x)
            \frac{\partial g}{\partial x_j}(x)
        \end{equation}
        defines a Poisson bracket on $\mathfrak{g}^*$. 
    \

\begin{enumerate}
    \item The $\delta$-functional on $\mathbbm{R}^{2n}$ is \emph{not}
        positive with respect to the Weyl star product $\star_{{\scriptscriptstyle\mathrm{Weyl}}}$ since
        e.g.
        \begin{equation}
            \label{eq:deltanot}
            \delta(\overline{{H}} \star_{{\scriptscriptstyle\mathrm{Weyl}}} H) = - \frac{\lambda^2}{4} < 0,
        \end{equation}
        where $H$ is the Hamiltonian of the harmonic oscillator.

        Thus points in phase space are (in general) no longer states
        in quantum mechnics, which is physically to be expected from
        the uncertainty relations.

    \item Consider $f \in C^\infty_0(\mathbbm{R}^{2n})[[\lambda]]$ then
        the Schrödinger functional
        \begin{equation}
            \label{eq:schroedinger}
            \omega(f) = \int_{\mathbbm{R}^n} f(q, p=0) \; d^nq
        \end{equation}
        turns out to be a positive functional with respect to the Weyl
        star product $\star_{{\scriptscriptstyle\mathrm{Weyl}}}$. In this case the positivity can be
        shown by successive partial integrations.
