            \{f,g\} = \sum_{i=1}^n \left( 
                \frac{\partial f}{\partial q^i}
                \frac{\partial g}{\partial p_i}
                -
                \frac{\partial f}{\partial p_i}
                \frac{\partial g}{\partial q^i}
            \right).
        \

\begin{equation}
            \label{eq:LiePoisson}
            \{f, g\} (x) = \sum_{i,j,k} x_k c^k_{ij} 
            \frac{\partial f}{\partial x_i}(x)
            \frac{\partial g}{\partial x_j}(x)
        \

\begin{equation}
    \label{eq:starsexpl}
    f \star_{{\scriptscriptstyle\mathrm{S}}} g 
    = \sum_{r=0}^\infty \frac{1}{r!}
    \left(\frac{\hbar}{{\mathrm i}}\right)^r
    \frac{\partial^r f}{\partial p^r} 
    \frac{\partial^r g}{\partial q^r}
\

\begin{equation}
    \label{eq:starwexpl}
    \begin{split}
        f \star_{{\scriptscriptstyle\mathrm{Weyl}}} g 
        &= N^{-1} (Nf \star_{{\scriptscriptstyle\mathrm{S}}} Ng) \\
        &=
        \sum_{r=0}^\infty \frac{1}{r!}
        \left(\frac{\hbar}{2{\mathrm i}}\right)^r
        \sum_{s=0}^r \binom{r}{s} (-1)^{r-s}
        \frac{\partial^r f}{\partial q^s \partial p^{r-s}}
        \frac{\partial^r g}{\partial q^{r-s} \partial p^s},
    \end{split}
\

\begin{equation}
    \label{eq:symPBW}
    \sigma: v_1 \vee \cdots \vee v_k 
    \mapsto \frac{({\mathrm i}\hbar)^k}{k!} 
    \sum_{\pi \in S_k} v_{\pi(1)} \circ \cdots \circ v_{\pi(k)}
\

\begin{equation}
    \label{eq:exeyebch}
    {\mathrm e}_x \star_{{\scriptscriptstyle\mathrm{BCH}}} {\mathrm e}_y 
    = {\mathrm e}_{\frac{1}{{\mathrm i}\hbar} \mathrm{BCH}({\mathrm i}\hbar x, {\mathrm i}\hbar y)},
\

\begin{equation}
        \label{eq:CDformula}
        a \star b 
        = \mu \circ {\mathrm e}^{\lambda \sum_i D_i \otimes E^i} (a \otimes b)
    \

\begin{equation}
    \label{eq:QPstar}
    f \star g = \mu \circ 
    {\mathrm e}^{{\mathrm i}\lambda x\frac{\partial}{\partial x}
      \otimes y\frac{\partial}{\partial y}} (f \otimes g)
\

\begin{equation}
    \label{eq:quadraticP}
    \{f, g\} 
    = xy \left(
        \frac{\partial f}{\partial x}
        \frac{\partial g}{\partial y}
        -
        \frac{\partial f}{\partial y}
        \frac{\partial g}{\partial x}
    \right).
\

\begin{equation}
    \label{eq:quantumplane}
    x \star y = q \, y \star x
    \quad
    \textrm{with}
    \quad
    q = {\mathrm e}^{{\mathrm i}\lambda}.
\

