T^r_r&=& -\frac{1}{2} \Omega ^2(\phi) \left\{ g^{tt} \sigma ^2 {P_t}^2 - g^{rr} \left[{\varphi ^\prime}^2 +{\sigma ^\prime}^2\right]+ g^{\theta \theta}
\varphi ^2Q^2\right\} \nonumber \\
&+& \frac{1}{8\pi}  g^{rr} \left\{ g^{tt}
\frac{{P_t^\prime}^2}{e^2} + g^{\theta \theta} \frac{{Q^\prime}^2}{q^2} \right\} - \Omega ^4(\phi) V(\sigma,\varphi) \nonumber \\
T^{\theta}_{\theta}&=& -\frac{1}{2} \Omega ^2(\phi) \left\{ g^{tt} \sigma ^2 {P_t}^2 + g^{rr} \left[{\varphi ^\prime}^2 +{\sigma ^\prime}^2\right]- g^{\theta \theta}
\varphi ^2Q^2 \right\} \nonumber \\
&-& \frac{1}{8\pi}  g^{rr} \left\{ g^{tt}
\frac{{P_t^\prime}^2}{e^2} - g^{\theta \theta} \frac{{Q^\prime}^2}{q^2} \right\} - \Omega ^4(\phi) V(\sigma,\varphi) \\
T^z_z&=& -\frac{1}{2} \Omega ^2(\phi) \left\{ g^{tt} \sigma ^2 {P_t}^2 + g^{rr} \left[{\varphi^\prime}^2 +{\sigma ^\prime}^2\right]+ g^{\theta \theta}
\varphi ^2Q^2 \right\} \nonumber \\
&-& \frac{1}{8\pi}  g^{rr} \left\{ g^{tt}
\frac{{P_t^\prime}^2}{e^2} + g^{\theta \theta} \frac{{Q^\prime}^2}{q^2} \right\} - \Omega ^4(\phi) V(\sigma,\varphi). \nonumber
