\section{The 8-Dimensional Bosonic Action} \label{full8daction}
%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%

Restricting to gauge groups with traceless 
structure constants, $f_{mn}{}^n = 0$, the full bosonic 8-dimensional action reads
\begin{align}
\label{eq:d8-Q-action}
S & = \frac{1}{16\pi G_{N}^{(11)}}\, C_U
{\displaystyle\int} d^{8}x \sqrt{|g_{E}|}\,
\left\{
R_{E} 
+\frac{1}{4}{\rm Tr}\left({\cal D} {\cal M}{\cal M}^{-1}\right)^{2}
+\frac{1}{4}{\rm Tr}\left(\partial {\cal W}{\cal W}^{-1}\right)^{2}
 \right. \notag \\
& 
\hspace{2cm}
-\frac{1}{4}F^{I\, m}{\cal M}_{mn}{\cal W}_{IJ} F^{J\, n}
+\frac{1}{2\cdot 3!} H_{m}{\cal M}^{mn} H_{n}
-\frac{1}{2\cdot 4!} e^{\varphi} G^{2} - {\cal V}\, \notag \\
& 
\hspace{2cm}
-\frac{1}{6^{3}\cdot 2^{4}}
{\textstyle\frac{1}{\sqrt{|g_{E}|}}}\, \epsilon
\left[GG \ell -8G H_{m} A^{2\, m} +12 G(\tilde{F}^{m}+\ell F^m)B_{m} \right. \notag \\
& 
\hspace{2cm}
\left.\left.
-8\epsilon^{mnp} H_{m}H_{n}B_{p} -8G\partial \ell C 
-16 H_{m}(\tilde{F}^{m}+\ell F^m) C \right]\right\} \, .
\end{align}
where we have made the following field strength definitions:
\begin{alignat}{2}
\label{eq:fieldstrengths2}
  G & = 4 \partial C + 6 F^{m} B_{m}\, , & \qquad
  F^{m} & = 2\partial A^{m} -\chi_{np}{}^{m}A^{n}A^{p}\, , \notag \\
  H_{m} & = 3{\cal D} B_{m} + 3 \epsilon_{mnp} F^{n} V^p\, , & 
  \tilde{F}^m & = 2{\cal D} V^m - \epsilon^{mnp} f_{np}{}^{q} B_{q}\, ,
\end{alignat}
The two vector field strengths form a doublet $F^{I\,m} = (F^m,\tilde{F}^m)$
with $I=1,2$. The $SL(2,\mathbb{R}) / SO(2)$ and $SL(3,\mathbb{R}) / SO(3)$
scalar cosets are defined in \eqref{Wscalar} and \eqref{Mscalar},
respectively. The potential ${\cal V}$ reads
\begin{equation}
 {\cal V} = {\textstyle\frac{1}{4}} e^{-\varphi}\,
             \left[ 2{\cal M}^{nq}f_{mn}{}^{p} f_{pq}{}^{m}
                  + {\cal M}^{mq}{\cal M}^{nr}{\cal M}_{ps}
                  f_{mn}{}^{p}f_{qr}{}^{s}
             \right]\, .
\label{eq:potential-1}
\end{equation}
The supersymmetric transformation rules in eight dimensions are
\begin{align}
\delta e_{\mu}{}^{a} = &
-\frac{i}{2}\overline{\epsilon} \Gamma^{a} {\psi}_{\mu} \, ,\notag \\
\delta \psi_\mu  = & 2 \partial_\mu \epsilon 
 - \frac{1}{2} {\slashed \omega}_\mu \epsilon
  +\frac{1}{2}L_{[i|}{}^{m} {\cal D}_{\mu} L_{m|j]}\Gamma^{ij} \epsilon \,
+\frac{1}{24}e^{-\varphi/2}f_{ijk}\Gamma^{ijk} \Gamma_\mu \epsilon \, \notag \\
& +\frac{1}{24} e^{\varphi/2}\Gamma^iL_{i}^{\ m}
( \Gamma_{\mu}^{\ \nu \rho}-10\delta_\mu^{\ \nu}\Gamma^\rho )
 F_{m\nu \rho}\epsilon +\frac{1}{2}e^{-\varphi}\partial_\mu \ell 
\epsilon \notag \\
& +\frac{i}{96}e^{\varphi/2}(\Gamma_{\mu}^{\ \nu \rho \delta \epsilon}
-4 \delta^{\ \nu}_{\mu} \Gamma^{\rho \delta \epsilon})
G_{\nu \rho \delta \epsilon} \epsilon 
+ \frac{i}{36}\Gamma^iL_i^{\ m}(\Gamma_{\mu}^{\ \nu \rho \delta}
-6 \delta^{\ \nu}_\mu \Gamma^{\rho \delta})H_{m \nu \rho \delta}\epsilon
\notag \\ 
& +\frac{i}{48}e^{-\varphi/2}\Gamma^i \Gamma^j L_i^{\ m} L_j^{\ n}
(\Gamma_\mu^{\ \nu \rho}-10\delta_\mu^{\ \nu}\Gamma^\rho)
(F_{mn \nu \rho} +\ell \varepsilon_{mnp}F^p_{\ \nu \rho})\epsilon \,, \notag \\
\delta \lambda_i  = &
 \frac{1}{2}L_i^{\ m} L^{jn}{\slashed {\cal D}}{\cal M}_{mn}
 \Gamma_j \epsilon -\frac{1}{3} {\slashed \partial} \varphi 
\Gamma_i \epsilon 
-\frac{1}{4}e^{-\varphi/2} (2f_{ijk}-f_{jki})\Gamma^{jk}\epsilon \, \notag \\
& + \frac{1}{4}e^{\varphi/2}L_i^{\ m}{\cal M}_{mn}{\slashed F}^n \epsilon +
\frac{i}{144}e^{\varphi/2}\Gamma_i {\slashed G}\epsilon 
+\frac{i}{36}(2\delta_i^{\ j}-\Gamma_{i}^{\ j})L_j^{\ m}{\slashed H}_m 
\epsilon \notag \\
& +\frac{i}{24}e^{-\varphi/2}\Gamma^j L_j^{\ m}L_k^{\ n}
(3\delta_i^{\ k}-\Gamma_{i}^{\ k})({\slashed F}_{mn}
+\ell \varepsilon_{mnp}{\slashed F}^p)\epsilon 
+\frac{1}{3}e^{-\varphi}\Gamma_i {\slashed \partial} \ell \epsilon \,,
\notag \displaybreak[2] \\
\delta A_{\mu}{}^{m}
= & -\frac{i}{2}e^{-\varphi/2} L_{i}^{\ m} \overline{\epsilon}  (
   \Gamma^{i} \psi_{\mu} -{\Gamma}_{\mu} 
(\eta^{ij}- \frac{1}{6}\Gamma^{i}\Gamma^{j})\lambda_j )  \,, \notag \\
\delta V_{\mu}^{\ m} = & -\frac{i}{2}e^{\varphi/2}
L_i^{\ m} \bar \epsilon ( \Gamma^i \psi_\mu
+\Gamma_\mu (\eta^{ij}-\frac{5}{6}\Gamma^i \Gamma^j) \lambda_j )-
\ell \delta A_\mu^{\ m}   \,, \notag \\
\delta B_{\mu \nu m} = & 
L_m^{\ \ i} \bar \epsilon  (\Gamma_{i[\mu} \psi_{\nu ]}
+\frac{1}{6} \Gamma_{\mu \nu}
(3\delta_i^{\ j}-\Gamma_i \Gamma^j)\lambda_j ) -2 \varepsilon_{mnp}
\delta A_{\mu}^{\ n} V_{\nu}^{\ p} \,, \notag \\
\delta C_{\mu \nu \rho} = & 
\frac{3}{2}e^{-\varphi/2} \bar \epsilon \Gamma_{[\mu \nu}( \psi_{\rho]}
-\frac{1}{6}\Gamma_{\rho]} \Gamma^i \lambda_i )
-3 \delta A_{[\mu}^{\ m} B_{ \nu \rho]m} \,, \notag \\ 
L^{\ n}_{i}\delta L_n{}_j = & \frac{i}{4}e^{\varphi/2}
  \overline\epsilon  (\Gamma_i \delta_j^{\ k} + \Gamma_j \delta^{\ k}_i-
  \frac{2}{3}\eta_{ij}\Gamma^k )\lambda_k \,,  \notag \\
\delta \varphi 
   = & -\frac{i}{2} \overline{\epsilon} \Gamma^{i} \lambda_i \,, \notag \\
\delta \ell = & -\frac{i}{2}e^{\varphi} \bar \epsilon \Gamma^i \lambda_i \,,
\end{align}

