\vskip 1.0truecm
\centerline{Dan Radu Grigore\footnote{e-mail: grigore@theor1.theory.nipne.ro}}

\centerline{Dept. Theor. Phys., Inst. Atomic Phys.,}
\centerline{Bucharest-M\u agurele, MG 6, Rom\^ania}
