\begin{enumerate}
\item
A continuum $0<\mu\leq 1$, 
\begin{eqnarray}
\psi_{\mu E k}(t,\rho,\phi)=N_{\mu E k} \; e^{-iEt}e^{-ik\phi}
\frac{\tanh^{|k|} (\rho)}{(\cosh \rho)^{1+\mu}} \nonumber \\ 
 \times {}_2 F_1 \left(
\frac{1}{2}(1+\mu+E+|k|),\frac{1}{2}(1+\mu+|k|-E) , 1+|k|, \tanh^2
\rho \right)
\end{eqnarray}

where $E,k \; \in \mathbb{Z}$.
\item
A discrete series, with $\mu \in \mathbb{N}$ and $n \in \mathbb{N}_0$,


\begin{eqnarray}
\psi_{\mu E k}(t,\rho,\phi)=N_{\mu E k} \; e^{-iEt}e^{-ik\phi}
\frac{\tanh^{|k|} (\rho)}{(\cosh \rho)^{1+\mu}} \nonumber \\
\times P^{(|k|,\mu)}_n \left( 1-2\tanh^2 \rho \right),
\end{eqnarray}

where either $E<-2n-|k|-1$ and $\mu=-2n-E-|k|-1$, or where
$E>2n+|k|+1$ and $\mu=-2n+E-|k|-1$,  and $k \in \mathbb{Z}$ in both
cases. Note that the case corresponding to both $-a$ and $-b$ $\in
\mathbb{N}_0$ is not allowed since $\mu>0$.


\

\begin{enumerate}
\item
For $0 < \mu \leq 1$ with $n,l \in \mathbb{Z}$ and $n\neq0$,
\begin{eqnarray}
\psi^{(1)}_{nl\mu}(\hat{t},\hat{r},\hat{\phi})=e^{-in\hat{t}/r_{-}}e^{-il\hat{\phi}/r_{+}}
\hat{r}^{i \frac{l}{r_{+}}} (\hat{r}^2-1)^{\frac{in}{2r_{-}}}y_1(\hat{r}^2),\\
\psi^{(2)}_{nl\mu}(\hat{t},\hat{r},\hat{\phi})=e^{-in\hat{t}/r_{-}}e^{-il\hat{\phi}/r_{+}}\hat{r}^{i\frac{l}{r_{-}}}
(\hat{r}^2-1)^{\frac{in}{2r_{-}}} y_2(\hat{r}^2)
\end{eqnarray}

where $\psi^{(1)}$ and $\psi^{(2)}$ are linearly independent; for $n=0$ a wavefunction linearly independent of
$\psi^{(1)}$ can be constructed and will be of the form $y_1(x)\log x$
plus an analytic function; however this does not belong to $L^2(BTZ)$,
and so we lose a solution in this case.

\item
For $\mu >1$  with $n,l \in \mathbb{Z}$,

\begin{eqnarray}
\psi_{nl\mu}(\hat{t},\hat{r},\hat{\phi})=  e^{-in\hat{t}/r_{-}}e^{-il\hat{\phi}/r_{+}}
\hat{r}^{i \frac{l}{r_{+}}}
(\hat{r}^2-1)^{\frac{in}{2r_{-}}} y_1(\hat{r}^2).
\end{eqnarray}
where for $n=0$ we have no solution.

