\begin{equation}
    \Phi_{ij}(x^-,\xp) =
    \int_0^\infty \frac{dp_-}{2\pi 2p_-}
    \int \frac{d^{\,d-2}p_\perp}{(2\pi)^{d-2}}  \left[
    a_{ij}(p_-,\pp) e^{ ip_- x^- + i\pp\cdot\xp} +
    a^\dagger_{ij}(p_-,\pp) e^{ -ip_- x^- - i\pp\cdot\xp}
    \right]
\

\begin{equation}
\label{eq:M particle Hamiltonian 1}
    H_{LC} = \sum_{\p} N_\p \;
             {\cal E}_{LC}(\p)
           = \sum_{\p} N_\p \;
             \frac{\pp^2 + m^2}{2 p_-} \ ,
\

\begin{equation}
\label{eq:M particle Hamiltonian 3}
    H_{LC}= \sum_{n=1}^M
    \frac{\pp^2 (n) + m^2}{2 \, p_- (n)}
    \to
    \frac 1a \int_0^l dx\;
    \frac{\pp^2 (x) + m^2}{2 p_- (x)}
\

\begin{equation}
\label{eq:M particle Hamiltonian 4}
    H_{LC}= \sum_{n=1}^M
    \frac{\pp^2 (n) + m^2}{2 \, p_- (n)}
    \to
    \int_0^l \frac{dx}{\tilde a(x)}
    \frac{\pp^2 (x) + m^2}{2 p_-(x)} =
    \int_0^l dx\; \frac{1}{\tilde a(x)^2}
    \frac{\pp^2 (x) + m^2}{2 k_-} \ .
\

\begin{equation}
\label{eq:string action}
  S=-\frac{1}{4\pi\alpha'}
       \int d\tau \int_0^l d\sigma
       \sqrt{-\gamma}\gamma^{ab} \,
       \partial_a Z^\mu \partial_b Z^\nu \, G_{\mu\nu}(Z) \ .
\

\begin{equation} \label{eq:LC Lagrangian}
    \mathcal{L}_{LC}=-\frac{h^{\tau\tau}}{4\pi\alpha'} \left(
    2 G_{+-} \dot Z^- +  G_{ij} \dot Z^i \dot Z^j -
    \frac{1}{(h^{\tau\tau})^2} G_{ij} Z^{i\,\prime} Z^{j\,\prime}
    \right)
\

\begin{equation}\label{eq:LC Hamiltonian}
    H_{LC} = \int_0^l \mathcal{H}_{LC} =
    \int_0^l d\sigma \, \frac{G_{+-}}{2 \,\pi_-}
    \left( G^{ij} \pi_i \pi_j +
    \frac{1}{(2\pi\alpha')^2} G_{ij} Z^{i\,\prime} Z^{j\,\prime}
    \right)
\

\begin{equation}
\label{eq:AdS metric}
    ds^2_{AdS}=\frac{R^2}{Y^2} \left(
    2 dZ^+ dZ^- + dZ^i dZ^j \delta_{ij}
    \right).
\

\begin{equation}\label{eq:LC AdS Hamiltonian 1}
   H_{LC}^{(AdS)}= \frac{1}{2 p_-}\int_0^l d\sigma \, \left(
                     \pi_i \pi_i +
                     \frac{1}{(2\pi\alpha')^2}
                     \frac{R^4}{Y^4} Z^{i\,\prime} Z^{i\,\prime}
                     \right)\ .
\

\begin{equation}
\label{eq:flat Hamiltonian}
    H_{LC}^{(flat)} =
    \frac{1}{2 p_-}\int_0^l d\sigma \, \left(
    \pi_i \pi_i +
    \frac{1}{(2\pi\alpha')^2}
    Z^{i\,\prime} Z^{i\,\prime}
                     \right)\ .
\

\begin{equation}
\label{eq:LC zeroAdS Hamiltonian}
    H_{LC} = \frac{1}{2 p_-}\int_0^l d\sigma \;
    \pi_i \pi_i   \equiv
    \frac{1}{2 p_-}\int_0^l d\sigma \;
    \left( \bm{\pi}_{\perp}(\sigma)^2 +
    \pi_Y(\sigma)^2\right) \ ,
\

\begin{equation}
\label{eq:py string Hamiltonian}
   H_{LC} = \int_0^l d\sigma \, \frac{
                     \sum_{i=2}^{d-1} \pi_i \pi_i + p_Y^2}
                     {2 \, \pi_-(\sigma)} \equiv
           \int_0^l d\sigma \, \frac{
                     \bm{\pi}_{\perp}(\sigma)^2 + p_Y^2}
                     {2 \, \pi_-(\sigma)}\ ,
\

\begin{equation}
\label{eq:open string H mode expansion}
     H_{LC} = \frac{1}{2p_-} \int_0^{l}\! d\sigma \, \pi_i \pi_i =
              \frac{p_-}{2l}\sum_{n=-\infty}^{\infty} p^i_n p^i_n
\

\begin{equation}
\label{eq:closed string H mode expansion}
    H_{LC} \; = \;
%            \frac{1}{2} \int_0^{2\pi P_-}\! d\sigma \, \pi_M \pi_M =
             \frac{p_-}{2l}\!\sum_{n=-\infty}^{\infty} p^i_n p^i_{-n}
             \; = \;
             \frac{p_-}{2l}\!\sum_{n=-\infty}^{\infty} p^i_n (p^i_n)^* \ .
\

\begin{equation}
    Z^m(\sigma, \tau) = \left(z^m_0 + \frac{p^m_0}{l} \tau \right)
                        \; + \;
                        i\sqrt{2} \sum_{k \neq  0} \frac{a^m_k}{k}
                        \exp\left[-\frac{i \pi k c \tau}{ l}\right]
                        \cos\left( \frac{\pi k \sigma}{l} \right) .
\

\begin{equation}
Z^m(\sigma, \tau) = (z^m_0 + \frac{p^m_0}{l} \tau) \; + \;
 \frac{i}{\sqrt{2}} \sum_{k \neq  0}\left\{
   \frac{a^m_k}{k}\exp\left[\frac{2\pi i k (\sigma - c\tau)}{l}\right]
             \; + \;
   \frac{\tilde{a}^m_k}{k}\exp\left[-\frac{2\pi i k (\sigma + c\tau)}{l}\right]
  \right\} \,,
\

