\section{The free open string theory on the plane-wave in the $SU(4)$ formalism}\label{sec2}


In the $SU(4)$ formalism the $SO(8)$ vector representation decomposes as
\begin{equation}
{\bf 8}_v={\bf 6}_0+{\bf 1}_1+{\bf 1}_{-1}\,,
\end{equation}
where the subscripts denote $U(1)$ quantum numbers. The ${\bf 8}_v$ index $I=1,\dots,8$ 
breaks into $i=1,\dots,6$ of the ${\bf 6}$, as well as $L$ and $R$ of the two singlets. 
We will take $i$ to lie along the $X^1,\dots,X^6$ directions and 
\begin{equation}
X^R=\frac{1}{\sqrt{2}}\left(X^7+iX^8\right)\,,\qquad
X^L=\frac{1}{\sqrt{2}}\left(X^7-iX^8\right)\,.
\label{LRdef}
\end{equation}
The spinor representations decompose as
\begin{equation}
{\bf 8}_s={\bf 4}_{1/2}+{\bf {\bar 4}}_{-1/2}\,,\qquad 
{\bf 8}_c={\bf 4}_{-1/2}+{\bf {\bar 4}}_{1/2}\,.
\end{equation}
The ${\bf 4}$ and ${\bf{\bar 4}}$ indices will be denoted by a superscript and subscript 
$A=1,\dots,4$ index, respectively.
An open string ending on a half-supersymmetric D-branes in the plane-wave background in
light-cone gauge is described, in the $SU(4)$ formalism by 
$x^i_u(\s_u)$, $x^L_u(\s_u)$, $x^R_u(\s_u)$,   and $\lambda^A_u(\s_u)$, $\theta_{uA}(\s_u)$,
${\tilde \lambda}^A_u(\s_u)$, ${\tilde \theta}_{uA}(\s_u)$ 
\footnote{We will often suppress these indices in what follows.} together with
a set of boundary conditions. These have been discussed at length in~\cite{st1}. For D-branes presently under consideration these are
\begin{eqnarray}
\partial_\sigma x^r_u|_{\sigma=0,\pi|\alpha_u|}&=&0\,,\qquad\qquad\qquad\qquad
x^{r^\prime,L,R}_u|_{\sigma=0,\pi|\alpha_u|}=0\\
\theta_{uA}|_{\sigma=0,\pi|\alpha_u|}
&=&\Omega_A{}^B{\tilde\theta}_{uB}|_{\sigma=0,\pi|\alpha_u|}\,,\qquad
\lambda^A_u|_{\sigma=0,\pi|\alpha_u|}=
\Omega^A{}_B{\tilde\lambda}^{B}_u|_{\sigma=0,\pi|\alpha_u|}\,.
\end{eqnarray}
Above the superscripts $r$ and $r^\prime$, which together span $i$, correspond to Neumann and Dirichlet
boundary conditions respectively; the matrix $\Omega$ is defined as~\footnote{We are using the same gamma-matrix conventions as~\cite{gssft}.}
\begin{equation}
\Omega=\rho^{RL}\prod_{r^\prime}\rho^{r^\prime}\,.
\end{equation}
Recall that for D$_-$-branes $\Omega\Pi\Omega\Pi=-1$ with $\Pi=\rho^{1234}$.
The index $u=1,2,3$ denotes the $u$th string (see Figure~\ref{fig1}). All the D-branes
under consideration here have at least two Dirichlet directions; we take $L,R$ to be
Dirichlet througout.

\noindent In a collision process $p^+_u$ will be positive for an incoming string and
negative for an outgoing one and it is convenient to define
\begin{equation}
\alpha_u=\alpha^\prime p^+_u\,.
\end{equation}
The mode expansions of the bosonic fields at $\t=0$ are
\begin{eqnarray}
x^r_u(\sigma_u)&=&x^r_{0(u)}+\sqrt{2}\sum_{n=1}^\infty x^r_{n(u)}
\cos\frac{n\sigma_u}{|\alpha_u|}\,,\\
p^r_u(\sigma_u)&=&\frac{1}{\pi|\alpha_u|}[p^r_{0(u)}+\sqrt{2}\sum_{n=1}^\infty p^r_{n(u)}
\cos\frac{n\sigma_u}{|\alpha_u|}]\,,\\
x^{r^\prime}_u(\sigma_u)&=&\sqrt{2}\sum_{n=1}^\infty x^{r^\prime}_{-n(u)}
\sin\frac{n\sigma_u}{|\alpha_u|}\,,\\
p^{r^\prime}_u(\sigma_u)&=&\frac{\sqrt{2}}{\pi|\alpha_u|}\sum_{n=1}^\infty p^{r^\prime}_{-n(u)}\sin\frac{n\sigma_u}{|\alpha_u|}\,,
\end{eqnarray}
with the $L,R$ directions having the same expansions as the $r^\prime$. The Fourier modes can be re-expressed in terms of creation and annihilation operators as
\begin{equation}\label{xp}
x_{n(u)}^I=i\sqrt{\frac{\a'}{2\o_{n(u)}}}\bigl(a_{n(u)}^I-a_{n(u)}^{I\,\dg}\bigr)\,,\qquad
p_{n(u)}^I=\sqrt{\frac{\o_{n(u)}}{2\a'}}\bigl(a_{n(u)}^I+a_{n(u)}^{I\,\dg}\bigr)\,,\qquad
\end{equation}
with $n$ non-negative for $I=r$ and negative for $I=r^\prime,\,L,\,R$.
Canonical quantization of the bosonic coordinates
\begin{equation}
[x_r^I(\s_u),p_s^J(\s_v)]=i\d^{IJ}\d_{uv}\d(\s_u-\s_v)
\end{equation}
implies the commutation relations
\begin{equation}
[a_{n(u)}^I,a_{m(v)}^{J\,\dg}]=\d^{IJ}\d_{nm}\d_{uv}\,.
\end{equation}

\noindent The following linear combinations of the fermionic fields are the fermionic normal modes
\begin{eqnarray}\label{normlambda}
\lambda^A_{\pm u}(\sigma_u)&=&
\lambda^A_u(\sigma_u)\pm\Omega^A{}_B{\tilde\lambda}^B_u(\sigma_u)\,,\\ \label{normtheta}
\theta_{\pm uA}(\sigma_u)&=&
\theta_{uA}(\sigma_u)\pm\Omega_A{}^B{\tilde\theta}_{uB}(\sigma_u)\,.
\end{eqnarray}
At $\tau=0$ the mode expansions of $\lambda^A_{\pm(u)}$ and $\theta_{\pm(u)A}$ are
\begin{eqnarray}
\lambda^A_{+u}(\sigma_u)&=&\frac{1}{\pi|\alpha_u|}
\left[\sqrt{2}\lambda_{0(u)}^A+2\sum_{n=1}^\infty
\lambda_{n(u)}^A\cos\frac{n\sigma_u}{|\alpha_u|}\right]\,,\\
\lambda^A_{-u}(\sigma_u)&=&\frac{1}{\pi|\alpha_u|}
\sum_{n=1}^\infty \lambda^A_{-n(u)}
\sin\frac{n\sigma_u}{|\alpha_u|}\,,\\
\theta_{+uA}(\sigma_u)&=&\sqrt{2}\theta_{0(u)A}+2\sum_{n=1}^\infty
\theta_{n(u)A}\cos\frac{n\sigma_u}{|\alpha_u|}\,,\\
\theta_{-uA}(\sigma_u)&=&2\sum_{n=1}^\infty
\theta_{-n(u)A}\sin\frac{n\sigma_u}{|\alpha_u|}\,.
\end{eqnarray}
The non-zero anti-commutators are
\begin{equation}
\left\{\theta_{\pm A u}(\sigma_u),\lambda_{\pm v}^B(\sigma^\prime_v)\right\}=
2\delta_{uv}\delta_A^B\delta(\sigma_u-\sigma^\prime_v)\,,
\end{equation}
or in terms of modes
\begin{equation}
\left\{\theta_{m(u)A},\lambda_{n(v)}^B\right\}=\delta_{mn}\delta_{uv}\delta_A^B\,.
\end{equation}
It is convenient to define a new basis of non-zero moded oscilators 
\begin{eqnarray}
\lambda_{n(u)}^A&=&\sqrt{\frac{|n|}{2\omega_{n(u)}}}
\left(P^{-1}_{n(u)C}{}^AR^C_{n(u)}+P_{n(u)C}{}^AR^C_{-n(u)}\right)\,,\\
\lambda_{-n(u)}^A&=&i\sqrt{\frac{|n|}{2\omega_{n(u)}}}
\left(P_{n(u)C}{}^AR^C_{n(u)}-P^{-1}_{n(u)C}{}^AR^C_{-n(u)}\right)\,,\\
\theta_{n(u)A}&=&\sqrt{\frac{|n|}{2\omega_{n(u)}}}
\left(P_{n(u)A}{}^CR_{n(u)C}+P^{-1}_{n(u)A}{}^BR_{-n(u)B}\right)\,,\\
\theta_{-n(u)A}&=&i\sqrt{\frac{|n|}{2\omega_{n(u)}}}
\left(P^{-1}_{n(u)A}{}^CR_{n(u)C}-P_{n(u)A}{}^BR_{-n(u)B}\right)\,.
\end{eqnarray}
Here
\begin{equation}
P^{\pm 1}_{n(u)A}{}^B=\frac{1}{\sqrt{1-\rho_{n(u)}^2}}(1\mp i\rho_n\Omega\Pi)_A{}^B\,,
\end{equation}
is the generalisation of the $P^{\pm 1}_{n(r)}$ in the closed string and
\begin{equation}
\r_{n(r)}=\r_{-n(r)}=\frac{\o_{n(r)}-|n|}{\m\a_r}\,,\qquad
c_{n(r)}=c_{-n(r)}=\frac{1}{\sqrt{1+\r_{n(r)}^2}}\,.
\end{equation}
These modes satisfy 
\begin{equation}
\{R_{An(u)},R^B_{m(v)}\}=\d_A^B\d_{n+m}\d_{uv}\,.
\end{equation}

\noindent The free string light-cone Hamiltonian is
\begin{eqnarray}
H_{2(u)}&=&\int_0^{\pi|\alpha_u|}d\sigma_u\left[
\frac{e(\alpha_u)}{2\pi\alpha^\prime}(\pi^2\alpha^\prime{}^2(p^I_u)^2+
(\partial_\sigma x^I_u)^2+\mu^2(x^I_u)^2)\right.
\nonumber\\
&&\left.+\frac{1}{2 i}(e(\alpha_u)(\theta_{+uA}\lambda^{\prime A}_{-u}+
\theta_{-uA}\lambda^{\prime A}_{+u})+\mu(\lambda_{+u}^A(\Omega\Pi)_A{}^B\theta_{+uB}-
\lambda_{-u}^A(\Omega\Pi)_A{}^B\theta_{-uB}))\right]\,.
\end{eqnarray}
It is convenient to define
\begin{equation}
R_{\pm 0(u)}^A=\frac{1}{2}(1\mp i e(\alpha_u)\Omega\Pi)^A{}_B\lambda_{0(u)}^B\,,\qquad
R_{\pm 0(u)A}=\frac{1}{2}(1\mp i e(\alpha_u)\Omega\Pi)_A{}^B\theta_{0(u)B}\,,
\end{equation}
which satisfy
\begin{equation}
\left\{R_{\pm 0A(u)},R_{\pm 0(v)}^B\right\}=0\,,\qquad
\left\{R_{\pm 0A(u)},R_{\mp 0(v)}^B\right\}=\delta_{uv}\frac{1}{2}(1\mp i\Omega\Pi)_A{}^B\,,
\end{equation}
The mode-expanded Hamiltonian is then
\begin{eqnarray}
H_{2(u)}&=&\frac{1}{\a_u}\sum_{n=1}^\infty
\o_{n(u)}\bigl(a_{n(u)}^{r\dg}a^r_{n(u)}+a^{r^\prime\dg}_{n(u)}a^{r^\prime}_{n(u)}
+R_{-n(u)A}R_{n(u)}^A+R^A_{-n(u)}R_{An(u)}\bigr)\nonumber \\
&&+\frac{\o_{0(u)}}{\alpha_u}(a_{0(u)}^{r\dg}a^r_{0(u)}+R_{-0(u)A}R^A_{+0(u)}
+R_{-0(u)}^AR_{+0(u)A})+\frac{1}{2}\mu e(\alpha_u)(p-5)\,,
\end{eqnarray}
where the last term is the zero-mode normal ordering constant for a D$p_-$-brane. The vacuum $|v\ra_u$ is defined as~\footnote{This vacuum is the $SU(4)$ version of the vacuum defined in~\cite{st2}.}
\begin{equation}
a^I_{n(u)}|v\ra_u=0\,,\qquad R_{m(u)}^A|v\ra_u=0\,,\qquad R_{m(u)A}|v\ra_u=0\,,\quad m > 0
\end{equation}
with $n$ negative (non-negative) for $I$ Dirichlet (Neumann) and for 
\begin{equation}
R_{+0(u)}^A|v\ra_u=0\,,\qquad R_{+0(u)A}|v\ra_u=0\,.
\end{equation}
The 16 supersymmetries are generated by the kinematical $q^+_{(u)A}$, $q^{+A}_{(u)}$ and the
dynamical $q^-_{(u)A}$, $q^{-A}_{(u)}$. The superalgebra was computed explicitly in~\cite{st2}.
In the $SU(4)$ formalism the part of the superalgebra of importance throughout this paper is
\begin{equation}\label{sualg}
\left\{q^{-A}_{(u)},q^-_{(u)B}\right\}=2\delta^A{}_B\delta_{uv}H_{2(u)} + i\mu\delta_{uv}\left((\gamma^{ij}\Pi){}^A{}_B J^{ij}_{(u)}+(\gamma^{i^\prime j^\prime 
}\Pi^\prime){}^A{}_B  J^{i^\prime j^\prime}_{(u)} -{\tilde\Pi}^A{}_BJ^{LR}_{(u)}
\right)\,,
\end{equation}
where $\Pi^\prime=\rho^{5678}$ and ${\tilde\Pi}=\Pi,\Pi^\prime$ for $R,L$ in $SO(4)$ or 
$SO(4)^\prime$, respectively.
The non-linearly realized supercharges can be expressed in terms of the normal modes of the system as
\begin{eqnarray}
q^-_{(u)A}&=&\int_0^{\pi|\alpha_u|}d\sigma_u\Bigl[
-e(\alpha_u)(\pi\alpha^\prime p^r_u\rho^r_{AB}\lambda^B_{+u}-\partial_\sigma x^r_u\rho^r_{AB}\lambda^B_{-u})
-\mu x^r_u(\rho^r\Omega\Pi)_{AB}\lambda^B_{+u}\nonumber \\ &&
-e(\alpha_u)(\pi\alpha^\prime p^{r^\prime}_u\rho^{r^\prime}_{AB}\lambda^B_{-u}
-\partial_\sigma x^{r^\prime}_u\rho^{r^\prime}_{AB}\lambda^B_{+u})
+\mu x^{r^\prime}_u(\rho^{r^\prime}\Omega\Pi)_{AB}\lambda^B_{-u}\nonumber \\ &&
+\sqrt{2}(\alpha^\prime p^R_u\theta_{-uA}-\frac{1}{\pi}\partial_\sigma x^R_u\theta_{+uA}-
e(\alpha_u)\frac{\mu}{\pi}x^R_u(\Omega\Pi)_A{}^B\theta_{-uB})\Bigr]\,,\\
q^{-A}_{(u)}&=&\int_0^{\pi|\alpha_u|}d\sigma_u\Bigl[
(\alpha^\prime p^r_u\rho^{rAB}\theta_{+uB}-\frac{1}{\pi}\partial_\sigma x^r_u\rho^{rAB}\theta_{-uB}
+e(\alpha_u)\frac{\mu}{\pi} x^r_u(\rho^r\Omega\Pi)^{AB}\theta_{+uB})\nonumber \\ &&
+(\alpha^\prime p^{r^\prime}_u\rho^{r^\prime AB}\theta_{-uB}-
\frac{1}{\pi}\partial_\sigma x^{r^\prime}_u\rho^{r^\prime AB}\theta_{+uB}
-e(\alpha_u)\frac{\mu}{\pi} x^{r^\prime}_u(\rho^{r^\prime}\Omega\Pi)^{AB}\theta_{-uB})\nonumber \\ &&
+\sqrt{2}e(\alpha_u)(\pi\alpha^\prime p^L_u\lambda^A_{-u}-\partial_\sigma x^L_u\lambda^A_{+u})-\sqrt{2}\mu x^L_u(\Omega\Pi)^A{}_B\lambda^B_{-u})\Bigr]\,.
\end{eqnarray}
Expanding in modes one finds
\begin{eqnarray}
q^-_{(u)A}&=&-\sqrt{2}\frac{\alpha^\prime}{\alpha}p_{0(u)}^r(\rho^r\lambda_{0(u)})_A-\sqrt{2}\mu x^r_{0(u)}(\rho^r\Omega\Pi\lambda_{0(u)})_A
\nonumber \\&&
-\frac{2}{\alpha_u}\sqrt{\frac{\alpha^\prime}{2}}\sum_{n=1}^\infty\sqrt{n}\Bigl[
a^r_{n(u)}(\rho^rP_{n(u)}R_{-n(u)})_A+a^{r\dagger}_{n(u)}(\rho^rP^{-1}_{n(u)}R_{n(u)})_A
-ia^{r^\prime}_{n(u)}(\rho^{r^\prime}P^{-1}_{n(u)}R_{-n(u)})_A\nonumber\\&&
+ia^{r^\prime\dagger}_{n(u)}(\rho^{r^\prime}P_{n(u)}R_{n(u)})_A
+i\sqrt{2}\alpha_u a^R_{n(u)}(P_{n(u)}R_{-n(u)})_A-i\sqrt{2}\alpha_u a^{R\dagger}_{n(u)}(P^{-1}_{n(u)}R_{n(u)})_A\Bigr]\\
q^{-A}_{(u)}&=&\sqrt{2}\alpha^\prime p_{0(u)}^r(\rho^r\theta_{0(u)})^A+\sqrt{2}\mu\alpha_u x^r_{0(u)}(\rho^r\Omega\Pi\theta_{0(u)})^A
\nonumber \\&&
+\sqrt{2\alpha^\prime}\sum_{n=1}^\infty\sqrt{n}\Bigl[
a^r_{n(u)}(\rho^rP^{-1}_{n(u)}R_{-n(u)})^A+a^{r\dagger}_{n(u)}(\rho^rP_{n(u)}R_{n(u)})^A
-ia^{r^\prime}_{n(u)}(\rho^{r^\prime}P_{n(u)}R_{-n(u)})^A\nonumber\\&&
+ia^{r^\prime\dagger}_{n(u)}(\rho^{r^\prime}P^{-1}_{n(u)}R_{n(u)})^A
-i\frac{\sqrt{2}}{\alpha_u} a^L_{n(u)}(P^{-1}_{n(u)}R_{-n(u)})^A+i\frac{\sqrt{2}}{\alpha_u} a^{L\dagger}_{n(u)}(P_{n(u)}R_{n(u)})^A\Bigr]
\end{eqnarray}
