\begin{figure}
\begin{center}
    \includegraphics[width=0.8\textwidth]{inpen.eps}
\end{center}
\caption{Slices through AdS$_3$ in global coordinates, showing the
  regions of AdS$_3$ determined by the norm of the Killing vector $\xi
  =\partial_\phi$ in the coordinates (\ref{rbtzb}). In region
  1, $\xi \cdot \xi \geq r_+^2$; in region 2, $r_+^2 \geq \xi \cdot \xi \geq
  r_-^2$; in region 3, $r_-^2 \geq \xi \cdot \xi > 0$; in region 4, $\xi
  \cdot \xi \leq 0$.} \label{fig:pen}
\

\begin{figure}
\begin{center}
    \includegraphics[width=\textwidth]{cont.eps}
\end{center}
\caption{The analytic continuation and contour deformation in passing
  from the Euclidean to the Lorentzian section. The complex $z$ plane
  is pictured; on the right, translation invariance has been employed
  to move the original contour of integration to $z \in (-Z, -Z-2\pi
  i)$. To simplify the figure, only singularities associated with an
  external point in region $1_{++}$ have been indicated.}  \label{fig:def}
\

