subequations:
\begin{subequations}
\begin{align}
A_1(\kappa)&=\frac{\kappa}{2\sinh\frac{\pi\kappa}{2}}
\\
\intertext{which agrees with \eqref{A1} and the result of \cite{okuyama1},}
A_{\frac12}(\kappa)&=\frac{1}{2\cosh\frac{\pi\kappa}{2}}
\label{A1/2}
\\
\intertext{which agrees with \cite{arevefa}, and especially}
A_0(\kappa)&=\lim_{s\to +0}\frac{A_{s+1}(\kappa)}{\kappa^2+4s^2},
\label{A_0}
\end{align}
\

\begin{subequations}
\begin{equation}
N_n^{(s)}\,\mathbf{a}^{\pm}_n=\Oc_{\mp(n+s)}.
\label{oscil}
\end{equation}
The ``rest'' consist of oscillators annihilating
both the $SL(2,\Rh)$-invariant vacuum and its
conjugate (for example for $s=1$ it contains $p/z$).
We assume the following (anti)commutation relations between the oscillators
\begin{equation}
[\mathbf{a}_n^{-},\,\mathbf{a}^{+}_m]_{\pm}=\delta_{nm}.
\end{equation}
\label{bfaosc}
\

\begin{subequations}
\begin{equation}
\psi(z)=i\left[\frac{\ap}{2}\right]^{\frac12}\,
\sum_{m\in \Zh+\frac12}\frac{\psi_m}{z^{m+\frac12}}
\end{equation}
and the two point correlation function is
\begin{equation}
\langle\psi(z)\psi(z')\rangle=-\frac{\ap}{2}\,\frac{1}{z-z'}.
\end{equation}
By comparing this correlation function with
\eqref{OOcorf} for $s=\frac12$ one concludes that
\begin{equation*}
i\left[\frac{\ap}{2}\right]^{\frac12}\,\Oc_{\frac12}(z)=\psi(z),
\end{equation*}
and therefore
\begin{equation}
\quad\mathbf{a}_n^{\pm}=\psi_{\mp (n+\frac12)},
\quad n\geqslant 0.
\end{equation}
\

\begin{subequations}
\begin{equation}
i\pd X(z)=\left[\frac{\ap}{2}\right]^{\frac12}\,
\sum_{m\in\Zh}\frac{\alpha_m}{z^{m+1}}
\end{equation}
and the two point correlation function is
\begin{equation}
\langle\pd X(z)\pd X(z')\rangle=-\frac{\ap}{2}\,\frac{1}{(z-z')^2}.
\end{equation}
By comparing this correlation function with
\eqref{OOcorf} for $s=1$ one concludes that
\begin{equation*}
\left[\frac{\ap}{2}\right]^{\frac12}\,\Oc_1(z)=i\pd X(z),
\end{equation*}
and therefore
\begin{equation}
\mathbf{a}_n^{\pm}=\frac{\alpha_{\mp (n+1)}}{\sqrt{n+1}},
\quad n\geqslant 0.
\end{equation}
\

\begin{subequations}
\begin{equation}
\Xi_{1}(\kappa)=\lim_{N\to\infty} \mu_{1,N}^{11}(\kappa)=
\lim_{N\to\infty}\left[\frac{\sinh\frac{\pi\kappa}{2}}{\sinh\frac{\pi N\kappa}{4}}\,
e^{\frac{\pi N\kappa}{4}}-e^{\frac{\pi\kappa}{2}}\right]
=-e^{-\frac{\pi|\kappa|}{2}}.
\label{xi1}
\end{equation}
Actually one can do even better. By \eqref{MII}
\begin{equation}
\Xi_s(\kappa)=\lim_{N\to\infty} \mu_{s,N}^{11}(\kappa)=
\theta(\pm\kappa)\,\frac{|\kappa|^{2s-1}}{A_s(\kappa)}
-e^{\pm\frac{\pi\kappa}{2}}.
\end{equation}
For $s=\frac12$ one obtains
\begin{equation}
\Xi_{\frac12}(\kappa)=\pm\sgn(\kappa)\,e^{-\frac{\pi|\kappa|}{2}},
\end{equation}
\

\begin{subequations}
\begin{equation}
T_{mn}^{(0)}=T_{m-1,n-1}^{(1)},\quad m,n\geqslant 1.
\end{equation}
The singular cases are
\begin{align}
T_{00}^{(0)}&=1-2s\,\log \bar{\alpha},
\\
T_{m0}^{(0)}&=\sqrt{\frac{2s}{m}}\,\left(\frac{\beta}{\bar{\alpha}}\right)^m
\\
T_{0n}^{(0)}&=\sqrt{\frac{2s}{n}}\,\left(-\frac{\bar{\beta}}{\bar{\alpha}}\right)^n
\end{align}
\label{Tmn}
\

\begin{subequations}
\begin{align}
a^{\pm}(\kappa,p)&=U^{-1}_p\,a^{\pm}(\kappa)\,U_p.
\label{523a}
\\
\intertext{Although the momentum operator does not change
under this unitary transformation}
\hat{p}&=U^{-1}_p\, \hat{p}\, U_p,
\\
\intertext{the center of mass coordinate operator changes to}
\hat{\xi}&\equiv U^{-1}_p\,\hat{x}\, U_p.
\end{align}
\

\begin{subequations}
\begin{equation}
M_{N,\,0m}^{\prime\,IJ}=-\frac{1}{\sqrt{m}}\int_{-\infty}^{\infty}d\kappa\,
\mu_{1,N}^{IJ}(\kappa)\,\mathscr{P}\,\frac{A_1(\kappa)}{\kappa}\,
V_{m-1}^{(1)}(\kappa)
+\mu_{1,N}^{IJ}(0)\,\xi_m,
\label{int1}
\end{equation}
where $\xi_m$ is by \eqref{C5}
\begin{equation}
\xi_m\equiv\int_{-\infty}^{\infty}d\kappa\,\mathscr{P}\,\frac{\sqrt{A_1(\kappa)}}{\kappa}\,
\langle\kappa,1|m-1,1\rangle
=\left\{%
\begin{array}{ll}
    -\frac{(-1)^k}{\sqrt{2k}}, & \hbox{for } m=2k;\\
    0, & \hbox{for } m=2k-1 \\
\end{array}%
\right.
\label{xim}
\end{equation}
\label{M'N}
\

