\section*{Appendix A}

Here we include some intermediate steps in the calculation  of the expansion of the  non-Abelian holonomy ${\mathbf {h}}_{\alpha_{IJ}}$ in powers of the segments as described in section 4.2. The order refers to the corresponding power.

\subsection{Quadratic order}
We have
\begin{equation}
{\mathbf{h}}_{\alpha_{IJ}}^{(2)}= U_1^{(2)} + U_2^{(2)} + U_3^{(2)}+ U_2^{(1)}U_1^{(1)}+
U_3^{(1)}U_1^{(1)}+U_3^{(1)}U_2^{(1)}.
\end{equation}
The separate contributions  are
\begin{eqnarray}
&&U_1^{(2)} + U_2^{(2)} + U_3^{(2)}=\frac {1}{2}[(-ys_{I}^{a}+xs_{J}^{a}){\mathbf A}_{a}+(2s_{J}^{a}s_{J}^{b}+2s_{I}^{a}s_{I}^{b}-s_{J}^{a}s_{I}^{b}-s_{I}^{a}s_{J}^{b})\nonumber\\
&&{\mathbf A}_{a}{\mathbf A}_{b}],\\
&&U_2^{(1)}U_1^{(1)}+
U_3^{(1)}U_1^{(1)}+U_3^{(1)}U_2^{(1)}
=(-s_{J}^{a}s_{J}^{b}-s_{I}^{a}s_{I}^{b}+s_{J}^{a}s_{I}^{b}){\mathbf A}_{a}{\mathbf A}_{b}
\end{eqnarray}

\subsection{ Third order}

Here
\begin{eqnarray}
{\mathbf{h}}_{\alpha_{IJ}}^{(3)}&=&U_1^{(3)} + U_2^{(3)} + U_3^{(3)}
+  U_3^{(2)}U_1^{(1)}+ U_3^{(2)}U_2^{(1)}\nonumber \\
&&+U_2^{(2)}U_1^{(1)}
+  U_3^{(1)}U_1^{(2)}+ U_3^{(1)}U_2^{(2)}+U_2^{(1)}U_1^{(2)}
\end{eqnarray}
The separate contributions are
\begin{eqnarray}
&&U_1^{(3)} + U_2^{(3)} + U_3^{(3)}=\frac{1}{3!}\left[x^2s_I^a-y^2s_I^a+xy(s_J^a-s_I^a)\right]{\mathbf A}_{a}+\left[\bar{x}\left(2(s_{J}^{a}-s_{I}^{a})(s_{J}^{b}-s_{I}^{b})+s_{I}^{a}s_{I}^{b}\right){}\right.
\nonumber\\&&
{}\left.+\bar{y}\left(2s_{J}^{a}s_{J}^{b}+(s_{J}^{a}-s_{I}^{a})(s_{J}^{b}-s_{I}^{b})\right)+x\left((s_{J}^{a}-s_{I}^{a})(s_{J}^{b}-s_{I}^{b})+2s_{I}^{a}s_{I}^{b}\right){}\right.
\nonumber\\&&
{}\left.+y\left(s_{J}^{a}s_{J}^{b}+2(s_{J}^{a}-s_{I}^{a})(s_{J}^{b}-s_{I}^{b})\right)\right]{\mathbf A}_{a}\bar{ \mathbf {A}}_{b}
+[-s_{J}^{a}s_{J}^{b}s_{J}^{c}+(s_{J}^a-s_{I}^{a})(s_{J}^{b}-s_{I}^{b})(s_{J}^{c}-s_{I}^c)
\nonumber\\&&
+s_{I}^{a}s_{I}^{b}s_{I}^{c}]{\mathbf A}_a {\mathbf A}_b {\mathbf A}_c
\end{eqnarray}
together with
\begin{eqnarray}
&&U_3^{(2)}U_1^{(1)}+ U_3^{(2)}U_2^{(1)}+U_2^{(2)}U_1^{(1)}
+  U_3^{(1)}U_1^{(2)}+ U_3^{(1)}U_2^{(2)}+U_2^{(1)}U_1^{(2)}
\nonumber \\
&&= \frac{1}{2}\left[\bar{x}\left(-s^{a}_Is^{b}_I-s_{J}^{a}(s_{J}^{b}-s_{I}^{b})\right)-\bar{y}s_{J}^{a}(s_{J}^{b}-s_{I}^{b}){}\right.
\nonumber\\
&&{}\left. +x(s_{J}^{a}-s_{I}^{a})s_{J}^{b}+y\left(-s^{a}_Is^{b}_I-s_{J}^{a}(s_{J}^{b}-s_{I}^{b}) \right)\right]{\mathbf A}_{a}\bar{{\mathbf A}}_{b}
\end{eqnarray}

\subsection{ Fourth order}


In the same way we have
\begin{eqnarray}
{\mathbf{h}}_{\alpha_{IJ}}^{(4)}=U_1^{(4)} + U_2^{(4)} + U_3^{(4)}
+(U_3^{(3)}+U_2^{(3)}+ U_3^{(1)}U_2^{(2)}+U_3^{(2)}U_2^{(1)})U_1^{(1)}+ (U_3^{(2)}+U_2^{(2)}
\nonumber\\
+ U_3^{(1)}U_2^{(1)})U_1^{(2)}+(U_3^{(1)}+U_2^{(1)})U_1^{(3)}+U_3^{(3)}U_2^{(1)}+U_3^{(2)}U_2^{(2)}+U_3^{(1)}U_2^{(3)}
\end{eqnarray}
The separate contributions are
\begin{eqnarray}
&&(U_3^{(3)}+U_2^{(3)}+U_3^{(1)}U_2^{(2)}+U_3^{(2)}U_2^{(1)})U_1^{(1)}=\frac{1}{3!}\Big\{ \left[(x^2+xy)(s_J^a-s_I^a)-y^2s_I^a\right]s_I^b {\mathbf A}_{a}\bar{\mathbf A}_{b}  \nonumber \\
&&+\left[(x+2y+\bar{y}+2\bar{x})(s_{J}^{a}-s_{I}^{a})(s_{J}^{b}-s_{I}^{b})+(2\bar{y}+y) s_{J}^{a}s_{J}^{b}-3(\bar{x}+\bar{y})s_{J}^{a}(s_{J}^{b}-s_{I}^{b})\right. \nonumber \\
&&\left.-3ys_{J}^{a}(s_{J}^{b}-s_{I}^{b})\right]s_I^c{\mathbf A}_a\bar{\mathbf A}_b\bar{\bar{\mathbf A}}_c+\left[(s_{J}^{a}-s_{I}^{a})(s_{J}^{b}-s_{I}^{b})(s_{J}^{c}-s_{I}^{c})-s_{J}^{a}s_{J}^bs_{J}^{c}-3s_{J}^{a}(s_{J}^{b}-s_{I}^{b}){}\right.  
\nonumber\\&&
{}\left. (s_{J}^{c}-s_{I}^{c})   +3s_{J}^{a}s_{J}^{b}(s_{J}^{c}-s_{I}^{c})\right]s_I^d{\mathbf A}_a{\mathbf A}_b{\mathbf A}_c{\mathbf A}_d\Big\}
\end{eqnarray}

\begin{eqnarray}
(U_3^{(2)}+U_2^{(2)}&+&U_3^{(1)}U_2^{(1)})U_1^{(2)}=\frac{1}{2!2!}\Big\{[x\bar{x}(s_J^a-s_I^a)-y\bar{x}s_I^a]s_I^b{\mathbf A}_a{\bar{\mathbf A}}_b+\left[x(s_{J}^{a}-s_{I}^{a})s_{I}^{b}\right. \nonumber \\
&&\left.-y s_{I}^{a}s_{I}^{b}   +\bar{\bar{x}}(s_{J}^{a}-s_{I}^{a})(s_{J}^{b}-s_{I}^{b})\right]s_I^c{\mathbf A}_a\bar{{\mathbf A}}_b\bar{{\bar{\mathbf A}}}_c\nonumber\\
&&+\left[(s_{J}^{a}-s_{I}^{a})(s_{J}^b-s_{I}^b)s_I^c\right]s_I^d{\mathbf A}_a{\mathbf A}_b{\mathbf A}_c {\mathbf A}_d\Big\}  
\end{eqnarray}

\begin{eqnarray}
\left(U_3^{(1)}+U_2^{(1)}\right)U_1^{(3)}&=&\frac{1}{3!}\left[-\bar{x}^2s_I^as_I^b{\mathbf A}_a\bar{\mathbf A}_b-(\bar{\bar{x}}+2\bar{x})s_{I}^{a}s_{I}^{b}s_{I}^{c}\right. \nonumber\\
&&\left.{\mathbf A}_a\bar{\mathbf A}_b\bar{\bar{\mathbf A_c}}-s_{I}^{a}s_{I}^{b}s_{I}^cs_{I}^d{\mathbf A}_a{\mathbf A}_b{\mathbf A}_c{\mathbf A}_d\right] 
\end{eqnarray}

\begin{eqnarray}
U_3^{(3)}U_2^{(1)}&=&\frac{1}{3!}\left[-y^2 s_J^a (s_J^{b}-s_I^{b}){\mathbf A}_a\bar{\mathbf A}_b+(2\bar{y}+y) s_J^{a} s_J^{b} (s_J^{c}-s_I^c){\mathbf A}_a\bar{\mathbf A}_b\bar{\bar{\mathbf A}_c} {}\right.
\nonumber\\&&
{}\left.-s_J^{a}s_J^{b}s_{J}^c(s_J^d-s_I^d){\mathbf A}_a{\mathbf A}_b{\mathbf A}_c{\mathbf A}_d\right]
\end{eqnarray}

\begin{eqnarray}
&&U_3^{(2)}U_2^{(2)}=\frac{1}{2!2!}\left[-y(\bar{x}+\bar{y})s_J^a (s_J^{b}-s_I^{b}){\mathbf A}_a\bar{\mathbf A}_b-y  s_J^{a} (s_J^{b}-s_I^b)(s_J^{c}-s_I^c) {\mathbf A}_a\bar{\mathbf A}_b\bar{\bar{\mathbf A}_c} \right.
\nonumber\\&&
{}\left. +(\bar{\bar{x}}+\bar{\bar{y}})  s_J^{a} s_J^{b}(s_J^{c}-s_I^c){\mathbf A}_a\bar{\mathbf A}_b\bar{\bar{\mathbf A_c}}{}+s_J^as_J^b(s_J^c-s_I^c)(s_J^d-s_I^d){\mathbf A}_a{\mathbf A}_b{\mathbf A}_c{\mathbf A}_d\right] 
\end{eqnarray}

\begin{eqnarray}
U_3^{(1)}U_2^{(3)}&=&\frac{1}{3!}\left[-(\bar{x^2}+\bar{y^2}+\bar{x}\bar{y}) s_J^a (s_J^b-s_I^b){\mathbf A}_a\bar{\mathbf A}_b -s_J^a(s_J^b-s_I^b)(s_J^c-s_I^c)(s_J^d-s_I^d){}\right.
\nonumber\\&&\left.{\mathbf A}_a{\mathbf A}_b{\mathbf A}_c{\mathbf A}_d-(\bar{x}+2\bar{y}+\bar{\bar{y}}+2\bar{\bar{y}})  s_J^{a} (s_J^{b}-s_I^b)(s_J^{c}-s_I^c){\mathbf A}_a\bar{\mathbf A}_b\bar{\bar{\mathbf A_c}}
{}\right]
\end{eqnarray}

\section*{Appendix B}

Here we include some intermediate steps in the calculation  of the expansion of  the non-Abelian holonomy ${\mathbf {h}}_{\alpha_{IJ}}$ in powers of the segments as described in section 5. The order refers to the corresponding power




\subsection{Third order }

We  consider the third order contribution to the expressions (\ref{SIGMA}),  (\ref{F2}), (\ref{F3}) and (\ref{F4}) together with the basic expressions given in Eqs. (\ref{L1}-\ref{L3}). The contributing pieces are

(i)
\begin{eqnarray}
\label{THIRDO}
\Sigma^{(3)}&=&L^{(3)}_{1}+L^{(3)}_{2}+L^{(3)}_{3}
=\frac{1}{3!}(s^c_I+ s^c_J)s^a_Is^b_
J\,\partial_c\,(\partial_a {\mathbf A}_b-\partial_b {\mathbf A}_a),
\end{eqnarray}


(ii)
\begin{eqnarray}
2!F^{(3)}_{2}&=&[L_{3},L_{1}]^{(3)}+[L_{3},L_{2}]^{(3)}+[L_{2},L_{1}]^{(3)}, 
\label{THIRDO1}
\end{eqnarray}
with
\begin{equation}
[L^{(2)}_{3},L^{(1)}_{1}]=-\frac{1}{2}s^{b}_{J}s^{a}_{J}s^{c}_{I}[\partial_{b}{\mathbf A}_{a},{\mathbf A}_{c}],
\qquad
[L^{(1)}_{3},L^{(2)}_{1}]=-\frac{1}{2}s^{b}_{I}s^{a}_{I}s^{c}_{J}[{\mathbf A}_{c},\partial_{b}{\mathbf A}_{a}],
\end{equation}
\begin{equation}
[L^{(2)}_{2},L^{(1)}_{1}]=\frac{1}{2}(s^{b}_{I}+s^{b}_{J}) (s^{a}_{J}-s^{a}_{I})s^{c}_{I}[\partial_{b}{\mathbf A}_{a},{\mathbf A}_{c}], \quad
[L^{(1)}_{2},L^{(2)}_{1}]=\frac{1}{2}(s^{c}_{J}-s^{c}_{I}) s^{b}_{I}s^{a}_{I}[{\mathbf A}_{c},\partial_{b}{\mathbf A}_{a}],
\end{equation}
\begin{equation}
[L^{(2)}_{3},L^{(1)}_{2}]=-\frac{1}{2}s^{b}_{J}s^{a}_{J}  (s^{c}_{J}-s^{c}_{I}) [\partial_{b}A_{a},A_{c}], \quad
[L^{(1)}_{3},L^{(2)}_{2}]=\frac{1}{2}(s^{b}_{I}+s^{b}_{J})(s^{a}_{J}-s^{a}_{I})s^{c}_{J}[\partial_{b}{\mathbf A}_{a},{\mathbf A}_{c}].
\end{equation}
leading to
\begin{equation}
2!F^{(3)}_{2}=\frac{1}{2}(s^{b}_{I}s^{a}_{J}s^{c}_{I}-s^{b}_{J}s^{a}_{I}s^{c}_{I}+s^{b}_{J}s^{a}_{J}s^{c}_{I}+s^{b}_{I}s^{a}_{J}s^{c}_{J}-s^{b}_{I}s^{a}_{I}s^{c}_{J}-s^{b}_{J}s^{a}_{I}s^{c}_{J})[\partial_{b}{\mathbf A}_{a},{\mathbf A}_{c}].
\end{equation}

(iii)
\begin{eqnarray}
3!F_{3}^{(3)}
&=&[L_{3}^2L_1]^{(3)}+[L_{2}^2L_1]^{(3)}+[L_{3}^{2}L_{2}]^{(3)}+2[L_3L_2L_1]^{(3)},
\end{eqnarray}
 with
\begin{eqnarray}
\left[L_{3},[L_{3},L_{1}]\right]^{(3)}&=&-s^{b}_{J}s^{a}_{I}s^{c}_{J}\left[{\mathbf A}_{c},[{\mathbf A}_{a},{\mathbf A}_{b}]\right],
\\
\left[L_{2},[L_{2},L_{1}]\right]^{(3)}&=&-(s^{b}_{J}-s^{b}_{I})s^{a}_{I}(s^{c}_{J}-s^{c}_{I})\left[{\mathbf A}_{c},[{\mathbf A}_{a},{\mathbf A}_{b}]\right],
\\
\left[L_{3},[L_{3},L_{2}]\right]^{(3)}&=&-s^{b}_{J}(s^{a}_{J}-s^{a}_{I})s^{c}_{J}\left[{\mathbf A}_{c},[{\mathbf A}_{a},{\mathbf A}_{b}]\right],
\\
2\left[L_{3},[L_{2},L_{1}]\right]^{(3)}&=&2(s^{b}_{J}-s^{b}_{I})s^{a}_{I}s^{c}_{J}\left[{\mathbf A}_{c},[{\mathbf A}_{a},{\mathbf A}_{b}]\right],
\end{eqnarray}
leading to
\begin{equation}
F^{(3)}_{3}=\frac{1}{3!}(s^{c}_{I}+s^{c}_{J})s^{a}_{I}s^{b}_{J}\partial_{c}\left[{\mathbf A}_{c},[{\mathbf A}_{a},{\mathbf A}_{b}]\right]
\end{equation}
Adding up the above results we
finally obtain Eqs. (\ref{H3FGK}) and (\ref{NCFGK}).

\subsection{Fourth order }

The contributions are

(i)
\begin{equation}
\Sigma^{(4)}=L_1^{(4)}+L_2^{(4)}+L_3^{(4)}=  \frac{1}{4!}(s_{I}^{c} s_{I}^{d}+ s_{I}^{c} s_{J}^{d}+s_{J}^{c}s_{J}^{d})s_{I}^{a}s_{J}^{b}     \partial_c\partial_d\,(\partial_a {\mathbf A}_b-\partial_b {\mathbf A}_a)
\end{equation}

(ii)
\begin{eqnarray}
2!F_{2}^{(4)}&=&[L_2,L_1]^{(4)}+[L_3,L_2]^{(4)}+[L_3,L_1]^{(4)} \nonumber \\
&=& [L_2,L_1-L_3]^{(4)}+[L_3,L_1]^{(4)}
\label{F2(4)}
\end{eqnarray}
The first term in the RHS of Eq. (\ref{F2(4)}) contributes with
\begin{equation}
[L_2^{(1)},L_1^{(3)}-L_3^{(3)}]= \frac{1}{3!}( s_{J}^{d}- s_{I}^{d})  ( s_{I}^{c}s_{I}^{b}s_{I}^{a}+s_{J}^{c} s_{J}^{b} s_{J}^{a})[ {\mathbf A}_d,\partial_b\partial_c{\mathbf A}_a]
\end{equation}
\begin{equation}
[L_2^{(2)},L_1^{(2)}-L_3^{(2)}]= \frac{1}{2!2!}( s_{J}^{c}+ s_{I}^{c}) ( s_{J}^{d}- s_{I}^{d})  ( s_{I}^{a}s_{I}^{b}+s_{J}^{a} s_{J}^{b} )[ \partial_c{\mathbf A}_d,\partial_b{\mathbf A}_a]
\end{equation}
\begin{equation}
[L_2^{(3)},L_1^{(1)}-L_3^{(1)}]= \frac{1}{3!}( s_{J}^{b}s_{J}^{c}+s_{J}^{b}s_{I}^{c}+ s_{I}^{b} s_{I}^{a})   ( s_{J}^{a}- s_{I}^{a})( s_{J}^{d}+ s_{I}^{d})  [ \partial_b\partial_c{\mathbf A}_a,{\mathbf A}_d]
\end{equation}
The second term in the RHS of Eq. (\ref{F2(4)}) contributes with
\begin{equation}
[L_3^{(1)},L_1^{(3)}]=- \frac{1}{3!}s_{J}^{d}s_{I}^{c}  s_{I}^{b}s_{I}^{a}[ {\mathbf A}_d,\partial_b\partial_c{\mathbf A}_a]
\end{equation}
\begin{equation}
[L_3^{(2)},L_1^{(2)}]=- \frac{1}{2!2!} s_{J}^{a}s_{J}^{b} s_{J}^{c}s_{J}^{d}[\partial_b{\mathbf A}_a,\partial_c{\mathbf A}_d]
\end{equation}
\begin{equation}
[L_3^{(1)},L_1^{(3)}]=- \frac{1}{3!}    s_{I}^{d}s_{J}^{c}  s_{J}^{b}s_{J}^{a}       [\partial_b\partial_c{\mathbf A}_a, {\mathbf A}_d]
\end{equation}

(iii)
\begin{equation}
3!F_{3}^{(4)}=[L_3^2L_1]^{(4)}+[L_2^2L_1]^{(4)}+[L_3^2L_2]^{(4)}
+2[L_3L_2L_1]^{(4)}-[\Sigma F_2]^{(4)}
\end{equation}
The individual contributions are
\begin{equation}
[L_3^{(1)},[L_3^{(1)},L_1^{(2)}]+[L_3^{(2)},L_1^{(1)}]]=- \frac{1}{2!}s_{J}^{d} ( s_{I}^{c}  s_{J}^{b}s_{J}^{a}- s_{J}^{c}  s_{I}^{b}s_{I}^{a})[{\mathbf A}_d,[{\mathbf A}_c,\partial_b {\mathbf A}_a]]
\end{equation}
\begin{eqnarray}
[L_3^{(2)},[L_3^{(1)},L_1^{(1)}]+2[L_2^{(1)},L_1^{(1)}]]&=&-\frac{1}{2!}(s_I^ds_{J}^{c}-2 s_{I}^{d}s_{I}^{c})s_{J}^{b}s_J^a \nonumber\\
&& \times [\partial_b {\mathbf A}_a,[{\mathbf A}_c,{\mathbf A}_d]]
\end{eqnarray}

\begin{eqnarray}
[L_2^{(1)},[L_2^{(1)},L_1^{(2)}]+[L_2^{(2)},L_1^{(1)}]]&=&\frac{1}{2!}(s_{J}^{d}-s_I^d)\left((s_{J}^{c}-s_I^c)s_{I}^{b}s_{I}^{a}-s_{I}^{c}( s_{I}^{b}+s_{J}^{b})(s_{J}^{a}-s_I^a)\right)     \nonumber \\
&&  \times [{\mathbf A}_d,[{\mathbf A}_c,\partial_b {\mathbf A}_a]]
\end{eqnarray}
\begin{eqnarray}
[L_2^{(2)},[L_2^{(1)},L_1^{(1)}]]=\frac{1}{2!}s_I^d (s_{J}^{c}-s_{I}^{c})( s_{I}^{b}+s_{J}^{b}) ( s_J^a-s_I^a) [\partial_b {\mathbf A}_a,[{\mathbf A}_c,{\mathbf A}_d]]
\end{eqnarray}

\begin{eqnarray}
[L_3^{(1)},[L_3^{(1)},L_2^{(2)}]+[L_3^{(2)},L_2^{(1)}]]&=&-s_J^d\frac{1}{2!} \left( (s_{J}^{c}( s_{I}^{b}+s_{J}^{b})(s_{J}^{a}-s_I^a)+ (s_{J}^{c}-s_I^c)s_J^bs_J^a   \right)     \nonumber \\
&&  \times [{\mathbf A}_d,[{\mathbf A}_c,\partial_b {\mathbf A}_a]]
\end{eqnarray}
\begin{eqnarray}
[L_3^{(2)},[L_3^{(1)},L_2^{(1)}]]=\frac{1}{2!}(s_J^d-s_I^d) s_{J}^{c} s_{J}^{b}s_J^a [\partial_b {\mathbf A}_a,[{\mathbf A}_c,{\mathbf A}_d]]
\end{eqnarray}





\begin{eqnarray}
2[L_3^{(1)},[L_2^{(1)},L_1^{(2)}]+[L_2^{(3)},L_1^{(1)}]]&=&-s_J^d \left((s_{I}^{c}( s_{I}^{b}+s_{J}^{b})(s_{J}^{a}-s_I^a)+ (s_{J}^{c}-s_I^c)s_I^bs_I^a   \right)     \nonumber \\
&& \times  [{\mathbf A}_d,[{\mathbf A}_c,\partial_b {\mathbf A}_a]]
\end{eqnarray}


\begin{eqnarray}
-[\Sigma^{(2)},F_2^{(2)}]=\frac{1}{2!2!}s_J^ds_{I}^{c}( s_{I}^{b}s_J^a-s_J^bs_I^a) [\partial_b {\mathbf A}_a,[{\mathbf A}_c,{\mathbf A}_d]]
\end{eqnarray}


(iv)
\begin{eqnarray}
4!F_{4}^{(4)}&=&[L_3^2L_2]^{(4)} +[L_3^3L_1]^{(4)}+[L_2^3L_1]^{(4)}+3[L_3^2L_2L_1]^{(4)}+3[L_3L_2^2L_1]^{(4)}\nonumber \\
&&-3![\Sigma,F_3]^{(4)}-[\Sigma^2,F_2]^{(4)}  
\end{eqnarray}
The contributions are

\begin{equation}
[L_3^3L_2]^{(4)}=-s_{J}^{c} s_{J}^{d}  s_{J}^{a} (s_{J}^{b}-s_I^b) [{\mathbf A}_c,[{\mathbf A}_d,[{\mathbf A}_a,{\mathbf A}_b]]]
\end{equation}


\begin{equation}
[L_3^3L_1]^{(4)}=-s_{J}^{c} s_{J}^{d}  s_{J}^{a} s_I^b [{\mathbf A}_c,[{\mathbf A}_d,[{\mathbf A}_a,{\mathbf A}_b]]]
\end{equation}


\begin{equation}
[L_2^3L_1]^{(4)}=(s_{J}^{c}-s_{I}^{c})(s_{J}^{d}-s_{I}^{d}) (s_J^a-s_I^a)s_I^b [{\mathbf A}_c,[{\mathbf A}_d,[{\mathbf A}_a,{\mathbf A}_b]]]
\end{equation}


\begin{equation}
3[L_3^2L_2L_1]^{(4)}=3s_{J}^{c} s_{J}^{d} ( s_{J}^{a}-s_I^a) s_I^b [{\mathbf A}_c,[{\mathbf A}_d,[{\mathbf A}_a,{\mathbf A}_b]]]
\end{equation}


\begin{equation}
3[L_3L_2^2L_1]^{(4)}=-3s_{J}^{c} (s_{J}^{d}-s_I^d) ( s_{J}^{a}-s_I^a) s_I^b [{\mathbf A}_c,[{\mathbf A}_d,[{\mathbf A}_a,{\mathbf A}_b]]]
\end{equation}

The terms $[\Sigma,F_3]^{(4)}$ and $[\Sigma^2,F_2]^{(4)}$ do not contribute to fourth order
because they start at the fifth and sixth power in the segments, respectively.




