with
\begin{equation}
[L^{(2)}_{3},L^{(1)}_{1}]=-\frac{1}{2}s^{b}_{J}s^{a}_{J}s^{c}_{I}[\partial_{b}{\mathbf A}_{a},{\mathbf A}_{c}],
\qquad
[L^{(1)}_{3},L^{(2)}_{1}]=-\frac{1}{2}s^{b}_{I}s^{a}_{I}s^{c}_{J}[{\mathbf A}_{c},\partial_{b}{\mathbf A}_{a}],
\end{equation}
\begin{equation}
[L^{(2)}_{2},L^{(1)}_{1}]=\frac{1}{2}(s^{b}_{I}+s^{b}_{J}) (s^{a}_{J}-s^{a}_{I})s^{c}_{I}[\partial_{b}{\mathbf A}_{a},{\mathbf A}_{c}], \quad
[L^{(1)}_{2},L^{(2)}_{1}]=\frac{1}{2}(s^{c}_{J}-s^{c}_{I}) s^{b}_{I}s^{a}_{I}[{\mathbf A}_{c},\partial_{b}{\mathbf A}_{a}],
\end{equation}
\begin{equation}
[L^{(2)}_{3},L^{(1)}_{2}]=-\frac{1}{2}s^{b}_{J}s^{a}_{J}  (s^{c}_{J}-s^{c}_{I}) [\partial_{b}A_{a},A_{c}], \quad
[L^{(1)}_{3},L^{(2)}_{2}]=\frac{1}{2}(s^{b}_{I}+s^{b}_{J})(s^{a}_{J}-s^{a}_{I})s^{c}_{J}[\partial_{b}{\mathbf A}_{a},{\mathbf A}_{c}].
\end{equation}
leading to
\begin{equation}
2!F^{(3)}_{2}=\frac{1}{2}(s^{b}_{I}s^{a}_{J}s^{c}_{I}-s^{b}_{J}s^{a}_{I}s^{c}_{I}+s^{b}_{J}s^{a}_{J}s^{c}_{I}+s^{b}_{I}s^{a}_{J}s^{c}_{J}-s^{b}_{I}s^{a}_{I}s^{c}_{J}-s^{b}_{J}s^{a}_{I}s^{c}_{J})[\partial_{b}{\mathbf A}_{a},{\mathbf A}_{c}].
\end{equation}

(iii)
\begin{eqnarray}
3!F_{3}^{(3)}
&=&[L_{3}^2L_1]^{(3)}+[L_{2}^2L_1]^{(3)}+[L_{3}^{2}L_{2}]^{(3)}+2[L_3L_2L_1]^{(3)},
\end{eqnarray}
 with
\begin{eqnarray}
\left[L_{3},[L_{3},L_{1}]\right]^{(3)}&=&-s^{b}_{J}s^{a}_{I}s^{c}_{J}\left[{\mathbf A}_{c},[{\mathbf A}_{a},{\mathbf A}_{b}]\right],
\\
\left[L_{2},[L_{2},L_{1}]\right]^{(3)}&=&-(s^{b}_{J}-s^{b}_{I})s^{a}_{I}(s^{c}_{J}-s^{c}_{I})\left[{\mathbf A}_{c},[{\mathbf A}_{a},{\mathbf A}_{b}]\right],
\\
\left[L_{3},[L_{3},L_{2}]\right]^{(3)}&=&-s^{b}_{J}(s^{a}_{J}-s^{a}_{I})s^{c}_{J}\left[{\mathbf A}_{c},[{\mathbf A}_{a},{\mathbf A}_{b}]\right],
\\
2\left[L_{3},[L_{2},L_{1}]\right]^{(3)}&=&2(s^{b}_{J}-s^{b}_{I})s^{a}_{I}s^{c}_{J}\left[{\mathbf A}_{c},[{\mathbf A}_{a},{\mathbf A}_{b}]\right],
\end{eqnarray}
leading to
\begin{equation}
F^{(3)}_{3}=\frac{1}{3!}(s^{c}_{I}+s^{c}_{J})s^{a}_{I}s^{b}_{J}\partial_{c}\left[{\mathbf A}_{c},[{\mathbf A}_{a},{\mathbf A}_{b}]\right]
\end{equation}
Adding up the above results we
finally obtain Eqs. (\ref{H3FGK}) and (\ref{NCFGK}).

\subsection{Fourth order }

The contributions are

(i)
\begin{equation}
\Sigma^{(4)}=L_1^{(4)}+L_2^{(4)}+L_3^{(4)}=  \frac{1}{4!}(s_{I}^{c} s_{I}^{d}+ s_{I}^{c} s_{J}^{d}+s_{J}^{c}s_{J}^{d})s_{I}^{a}s_{J}^{b}     \partial_c\partial_d\,(\partial_a {\mathbf A}_b-\partial_b {\mathbf A}_a)
\end{equation}

(ii)
\begin{eqnarray}
2!F_{2}^{(4)}&=&[L_2,L_1]^{(4)}+[L_3,L_2]^{(4)}+[L_3,L_1]^{(4)} \nonumber \\
&=& [L_2,L_1-L_3]^{(4)}+[L_3,L_1]^{(4)}
\label{F2(4)}
\end{eqnarray}
The first term in the RHS of Eq. (\ref{F2(4)}) contributes with
\begin{equation}
[L_2^{(1)},L_1^{(3)}-L_3^{(3)}]= \frac{1}{3!}( s_{J}^{d}- s_{I}^{d})  ( s_{I}^{c}s_{I}^{b}s_{I}^{a}+s_{J}^{c} s_{J}^{b} s_{J}^{a})[ {\mathbf A}_d,\partial_b\partial_c{\mathbf A}_a]
\end{equation}
\begin{equation}
[L_2^{(2)},L_1^{(2)}-L_3^{(2)}]= \frac{1}{2!2!}( s_{J}^{c}+ s_{I}^{c}) ( s_{J}^{d}- s_{I}^{d})  ( s_{I}^{a}s_{I}^{b}+s_{J}^{a} s_{J}^{b} )[ \partial_c{\mathbf A}_d,\partial_b{\mathbf A}_a]
\end{equation}
\begin{equation}
[L_2^{(3)},L_1^{(1)}-L_3^{(1)}]= \frac{1}{3!}( s_{J}^{b}s_{J}^{c}+s_{J}^{b}s_{I}^{c}+ s_{I}^{b} s_{I}^{a})   ( s_{J}^{a}- s_{I}^{a})( s_{J}^{d}+ s_{I}^{d})  [ \partial_b\partial_c{\mathbf A}_a,{\mathbf A}_d]
\end{equation}
The second term in the RHS of Eq. (\ref{F2(4)}) contributes with
\begin{equation}
[L_3^{(1)},L_1^{(3)}]=- \frac{1}{3!}s_{J}^{d}s_{I}^{c}  s_{I}^{b}s_{I}^{a}[ {\mathbf A}_d,\partial_b\partial_c{\mathbf A}_a]
\end{equation}
\begin{equation}
[L_3^{(2)},L_1^{(2)}]=- \frac{1}{2!2!} s_{J}^{a}s_{J}^{b} s_{J}^{c}s_{J}^{d}[\partial_b{\mathbf A}_a,\partial_c{\mathbf A}_d]
\end{equation}
\begin{equation}
[L_3^{(1)},L_1^{(3)}]=- \frac{1}{3!}    s_{I}^{d}s_{J}^{c}  s_{J}^{b}s_{J}^{a}       [\partial_b\partial_c{\mathbf A}_a, {\mathbf A}_d]
\end{equation}

(iii)
\begin{equation}
3!F_{3}^{(4)}=[L_3^2L_1]^{(4)}+[L_2^2L_1]^{(4)}+[L_3^2L_2]^{(4)}
+2[L_3L_2L_1]^{(4)}-[\Sigma F_2]^{(4)}
\end{equation}
The individual contributions are
\begin{equation}
[L_3^{(1)},[L_3^{(1)},L_1^{(2)}]+[L_3^{(2)},L_1^{(1)}]]=- \frac{1}{2!}s_{J}^{d} ( s_{I}^{c}  s_{J}^{b}s_{J}^{a}- s_{J}^{c}  s_{I}^{b}s_{I}^{a})[{\mathbf A}_d,[{\mathbf A}_c,\partial_b {\mathbf A}_a]]
\end{equation}
\begin{eqnarray}
[L_3^{(2)},[L_3^{(1)},L_1^{(1)}]+2[L_2^{(1)},L_1^{(1)}]]&=&-\frac{1}{2!}(s_I^ds_{J}^{c}-2 s_{I}^{d}s_{I}^{c})s_{J}^{b}s_J^a \nonumber\\
&& \times [\partial_b {\mathbf A}_a,[{\mathbf A}_c,{\mathbf A}_d]]
\end{eqnarray}

\begin{eqnarray}
[L_2^{(1)},[L_2^{(1)},L_1^{(2)}]+[L_2^{(2)},L_1^{(1)}]]&=&\frac{1}{2!}(s_{J}^{d}-s_I^d)\left((s_{J}^{c}-s_I^c)s_{I}^{b}s_{I}^{a}-s_{I}^{c}( s_{I}^{b}+s_{J}^{b})(s_{J}^{a}-s_I^a)\right)     \nonumber \\
&&  \times [{\mathbf A}_d,[{\mathbf A}_c,\partial_b {\mathbf A}_a]]
\end{eqnarray}
\begin{eqnarray}
[L_2^{(2)},[L_2^{(1)},L_1^{(1)}]]=\frac{1}{2!}s_I^d (s_{J}^{c}-s_{I}^{c})( s_{I}^{b}+s_{J}^{b}) ( s_J^a-s_I^a) [\partial_b {\mathbf A}_a,[{\mathbf A}_c,{\mathbf A}_d]]
\end{eqnarray}

\begin{eqnarray}
[L_3^{(1)},[L_3^{(1)},L_2^{(2)}]+[L_3^{(2)},L_2^{(1)}]]&=&-s_J^d\frac{1}{2!} \left( (s_{J}^{c}( s_{I}^{b}+s_{J}^{b})(s_{J}^{a}-s_I^a)+ (s_{J}^{c}-s_I^c)s_J^bs_J^a   \right)     \nonumber \\
&&  \times [{\mathbf A}_d,[{\mathbf A}_c,\partial_b {\mathbf A}_a]]
\end{eqnarray}
\begin{eqnarray}
[L_3^{(2)},[L_3^{(1)},L_2^{(1)}]]=\frac{1}{2!}(s_J^d-s_I^d) s_{J}^{c} s_{J}^{b}s_J^a [\partial_b {\mathbf A}_a,[{\mathbf A}_c,{\mathbf A}_d]]
\end{eqnarray}





\begin{eqnarray}
2[L_3^{(1)},[L_2^{(1)},L_1^{(2)}]+[L_2^{(3)},L_1^{(1)}]]&=&-s_J^d \left((s_{I}^{c}( s_{I}^{b}+s_{J}^{b})(s_{J}^{a}-s_I^a)+ (s_{J}^{c}-s_I^c)s_I^bs_I^a   \right)     \nonumber \\
&& \times  [{\mathbf A}_d,[{\mathbf A}_c,\partial_b {\mathbf A}_a]]
\end{eqnarray}


\begin{eqnarray}
-[\Sigma^{(2)},F_2^{(2)}]=\frac{1}{2!2!}s_J^ds_{I}^{c}( s_{I}^{b}s_J^a-s_J^bs_I^a) [\partial_b {\mathbf A}_a,[{\mathbf A}_c,{\mathbf A}_d]]
\end{eqnarray}


(iv)
\begin{eqnarray}
4!F_{4}^{(4)}&=&[L_3^2L_2]^{(4)} +[L_3^3L_1]^{(4)}+[L_2^3L_1]^{(4)}+3[L_3^2L_2L_1]^{(4)}+3[L_3L_2^2L_1]^{(4)}\nonumber \\
&&-3![\Sigma,F_3]^{(4)}-[\Sigma^2,F_2]^{(4)}  
\end{eqnarray}
The contributions are

\begin{equation}
[L_3^3L_2]^{(4)}=-s_{J}^{c} s_{J}^{d}  s_{J}^{a} (s_{J}^{b}-s_I^b) [{\mathbf A}_c,[{\mathbf A}_d,[{\mathbf A}_a,{\mathbf A}_b]]]
\end{equation}


\begin{equation}
[L_3^3L_1]^{(4)}=-s_{J}^{c} s_{J}^{d}  s_{J}^{a} s_I^b [{\mathbf A}_c,[{\mathbf A}_d,[{\mathbf A}_a,{\mathbf A}_b]]]
\end{equation}


\begin{equation}
[L_2^3L_1]^{(4)}=(s_{J}^{c}-s_{I}^{c})(s_{J}^{d}-s_{I}^{d}) (s_J^a-s_I^a)s_I^b [{\mathbf A}_c,[{\mathbf A}_d,[{\mathbf A}_a,{\mathbf A}_b]]]
\end{equation}


\begin{equation}
3[L_3^2L_2L_1]^{(4)}=3s_{J}^{c} s_{J}^{d} ( s_{J}^{a}-s_I^a) s_I^b [{\mathbf A}_c,[{\mathbf A}_d,[{\mathbf A}_a,{\mathbf A}_b]]]
\end{equation}


\begin{equation}
3[L_3L_2^2L_1]^{(4)}=-3s_{J}^{c} (s_{J}^{d}-s_I^d) ( s_{J}^{a}-s_I^a) s_I^b [{\mathbf A}_c,[{\mathbf A}_d,[{\mathbf A}_a,{\mathbf A}_b]]]
\end{equation}

The terms $[\Sigma,F_3]^{(4)}$ and $[\Sigma^2,F_2]^{(4)}$ do not contribute to fourth order
because they start at the fifth and sixth power in the segments, respectively.




