\begin{subequations}\label{eq:e-k-chi}
\begin{equation}\label{eq:echi}
\begin{split}
 E_\chi(A_+,A_-,\Phi;\theta)=& |\text{Pf}(2\pi\theta)|\operatorname{Tr }
 \left\{F_{+\mu\nu}F_{+\mu\nu}^\dagger
 +F_{-\mu\nu}F_{-\mu\nu}^\dagger+{\textstyle\frac{1}{2}}D_\mu\phi
 (D_\mu\phi)^\dagger+{\textstyle\frac{1}{2}}(D_\mu\phi)^\dagger 
 D_\mu\phi\right.\\
 &\left.\hspace*{1in}+\,\,{\textstyle\frac{1}{8}}(v_+-\phi\phi^\dagger)^2
 +{\textstyle\frac{1}{8}}(v_--\phi^\dagger\phi)^2\right\}
\end{split}
\end{equation} 
and $K_\chi$ is given by
\begin{equation}\label{eq:kchi-1}
 K_\chi=-\frac{i}{32\pi^2}|\text{Pf}(2\pi\theta)|
 \operatorname{Tr }\left\{\{F_{+12}+F_{+34},v_+\}-\{F_{-12}+F_{-34},v_-\}\right\}.
\end{equation}
