\left(
\begin{array}{cc}
g \cos f(\mu) 
& \vecs \cdot {\bf p} + ig {\bf e_\mu} \cdot \vectau \sin f(\mu) \\ 
  \vecs \cdot {\bf p} - ig {\bf e_\mu} \cdot \vectau \sin f(\mu) 
& - g \cos f(\mu)
\end{array}
\right) \psi,
\

\begin{eqnarray} 
\begin{array}{ccc}
{\hat P}~ \gamma_0~ {\hat P}^{-1} = \gamma_0 & {\rm and} & 
{\hat P}~ \gamma_i~ {\hat P}^{-1} = - \gamma_i, \\
{\hat P}~ \partial_0~ {\hat P}^{-1} = \partial_0 & {\rm and} &
{\hat P}~ \partial_i~ {\hat P}^{-1} = - \partial_i. 
\end{array}
\

\begin{eqnarray}
\label{general1}
\frac{{\rm d}}{{\rm {d}} \mu}
\left(
\begin{array}{c}
F \\
G
\end{array}
\right) = \left(
\begin{array}{cc}
\frac{1 - 3 \cos \mu}{2\sin \mu} - g \sin f & E - g \cos f \\
- \left(E + g \cos f \right) & 
        g \sin f
        -\frac{3 \left(1 + \cos \mu \right)}{2\sin \mu} 
\end{array}
\right) \left(
\begin{array}{c}
F \\
G
\end{array}
\right),
\

\begin{eqnarray}
\label{general2}
\frac{{\rm d}}{{\rm {d}} \mu}
\left(
\begin{array}{c}
{\tilde F} \\
{\tilde G}
\end{array}
\right) = \left(
\begin{array}{cc}
- \frac{1 + 3 \cos \mu}{2 \sin \mu} + g \sin f & E + g \cos f \\
- \left(E - g \cos f \right) & 
      \frac{3 \left(1 - \cos \mu \right)}{2 \sin \mu} - g \sin f
\end{array}
\right) \left(
\begin{array}{c}
{\tilde F} \\
{\tilde G}
\end{array}
\right).
\

\begin{eqnarray}
\nonumber
z(u) &=& A + B_1 \int\limits_{}^{u} 
\frac{1}{\left( y_1(v)\right)^2} 
\exp
\left( -\int\limits_{}^{v} \frac{p(w)}{w-u_0} {\rm d} w
\right) {\rm d}v, \\
\nonumber
&=& A + B_2 \int\limits_{}^u \frac{v-u_0}{\left( y_1(v)\right)^2}
\exp \left(
- p_1 (v-u_0) - \tfrac{1}{2} p_2 (v-u_0)^2 -\dots
\right) {\rm d}v, \\
\label{ggss}
&=& A + B_3 \int\limits_{}^{u} (v - u_0)^{-p_0 -2\rho_1} g(v) {\rm d}v, 
\

\begin{eqnarray}
\nonumber
G(u) &=& c_1 {\cal F} \left(\tfrac{3}{2}+ e,\tfrac{3}{2}-e;\tfrac{3}{2};
                 \tfrac{1}{2} \left(1+u \right) \right),
\\ 
\label{hypid}
     &=& c_1 \left( 
    \frac{\Gamma{(\tfrac{3}{2})} \Gamma{\left(-\tfrac{3}{2}\right)}}
    {\Gamma{(e)}\Gamma{(-e)}}
    {\cal F} \left(\tfrac{3}{2}+e,\tfrac{3}{2}-e;\tfrac{5}{2};
           \tfrac{1}{2}\left(1-u \right) \right) 
\right.   \\
\nonumber
&+&
\left. 
   \left(\frac{1-u}{2}\right)^{-\frac{3}{2}}
     \frac{\Gamma{\left(\tfrac{3}{2}\right)}\Gamma{\left(\tfrac{3}{2}\right)}}
     {\Gamma{\left(\tfrac{3}{2}-e\right)}\Gamma{\left(\tfrac{3}{2}+e\right)}}
     {\cal F} \left(-e,e;-\tfrac{1}{2};\tfrac{1}{2}\left(1-u \right) \right)
\right).
\

\begin{eqnarray}
\label{recurrence}
\nonumber
a_{n+1} = \frac{ 
\left( \left(E-3g \right) n^2 + \left(3E -2g \right) n -\left( E-g
\right) \left(E^2 - \left(g-\tfrac{3}{2}\right)^2 \right) 
\right)}{\left(E-g\right) \left(2n+3 \right) \left(n+1 \right)} a_n \\
+ \frac{g \left( n^2 - \left(E^2 - E -g^2 +2g +\tfrac{1}{4} \right) \right)} 
{\left(E-g \right) \left(2n+3 \right) \left(n+1 \right)} a_{n-1}.
